---
import Layout from './Layout.astro';
import { Icon } from 'astro-icon/components';

interface Props {
  title?: string;
  description?: string;
}

const {
  title = "Documentation",
  description = "Learn how to use Socialaize to manage your social media"
} = Astro.props;
---

<Layout title={title} navVariant="default">
  <!-- Documentation Header -->
  <header class="sticky top-0 z-40 bg-white/80 dark:bg-gray-900/80 backdrop-blur-lg border-b border-gray-200 dark:border-gray-800">
    <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <div class="flex items-center justify-between h-16">
        <!-- Logo and Title -->
        <div class="flex items-center gap-4">
          <a href="/" class="flex items-center gap-2">
            <img src="/logo.svg" alt="Socialaize" class="h-8 w-8" />
            <span class="text-xl font-bold">Socialaize</span>
          </a>
          <div class="h-6 w-px bg-gray-300 dark:bg-gray-700"></div>
          <span class="text-gray-600 dark:text-gray-400">Documentation</span>
        </div>

        <!-- Actions -->
        <div class="flex items-center gap-3">
          <a
            href="/dashboard"
            class="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white transition-colors"
          >
            Dashboard
          </a>
          <a
            href="/login"
            class="px-4 py-2 text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 rounded-lg transition-colors"
          >
            Get Started
          </a>
        </div>
      </div>
    </div>
  </header>

  <!-- Main Content Area -->
  <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
    <div class="flex gap-8">
      <!-- Sidebar Navigation -->
      <aside class="hidden lg:block w-64 flex-shrink-0">
        <nav class="sticky top-24 space-y-6">
          <!-- Quick Links -->
          <div>
            <h3 class="text-xs font-semibold text-gray-500 dark:text-gray-400 uppercase tracking-wider mb-3">
              Quick Links
            </h3>
            <ul class="space-y-2" id="category-nav">
              <li>
                <a href="#" data-category="getting-started" class="category-link flex items-center gap-2 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                  <Icon name="mdi:rocket-launch" class="w-4 h-4" />
                  Getting Started
                </a>
              </li>
              <li>
                <a href="#" data-category="features" class="category-link flex items-center gap-2 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                  <Icon name="mdi:star" class="w-4 h-4" />
                  Features
                </a>
              </li>
              <li>
                <a href="#" data-category="workflows" class="category-link flex items-center gap-2 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                  <Icon name="mdi:workflow" class="w-4 h-4" />
                  Workflows
                </a>
              </li>
              <li>
                <a href="#" data-category="integrations" class="category-link flex items-center gap-2 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                  <Icon name="mdi:puzzle" class="w-4 h-4" />
                  Integrations
                </a>
              </li>
              <li>
                <a href="#" data-category="analytics" class="category-link flex items-center gap-2 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                  <Icon name="mdi:chart-line" class="w-4 h-4" />
                  Analytics
                </a>
              </li>
              <li>
                <a href="#" data-category="advanced" class="category-link flex items-center gap-2 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
                  <Icon name="mdi:code-braces" class="w-4 h-4" />
                  Advanced
                </a>
              </li>
            </ul>
          </div>

          <!-- Popular Topics -->
          <div>
            <h3 class="text-xs font-semibold text-gray-500 dark:text-gray-400 uppercase tracking-wider mb-3">
              Popular Topics
            </h3>
            <ul class="space-y-2">
              <li>
                <a href="#getting-started/connecting-accounts" class="block px-3 py-1.5 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  Connecting Accounts
                </a>
              </li>
              <li>
                <a href="#features/creating-posts" class="block px-3 py-1.5 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  Creating Posts
                </a>
              </li>
              <li>
                <a href="#workflows/workflow-basics" class="block px-3 py-1.5 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  AI Workflows
                </a>
              </li>
              <li>
                <a href="#analytics/analytics-dashboard" class="block px-3 py-1.5 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  Analytics Dashboard
                </a>
              </li>
            </ul>
          </div>

          <!-- Help & Support -->
          <div class="pt-6 border-t border-gray-200 dark:border-gray-800">
            <h3 class="text-xs font-semibold text-gray-500 dark:text-gray-400 uppercase tracking-wider mb-3">
              Need Help?
            </h3>
            <ul class="space-y-2">
              <li>
                <a href="/support" class="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  <Icon name="mdi:lifebuoy" class="w-4 h-4" />
                  Support Center
                </a>
              </li>
              <li>
                <a href="/community" class="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  <Icon name="mdi:forum" class="w-4 h-4" />
                  Community
                </a>
              </li>
              <li>
                <a href="/contact" class="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">
                  <Icon name="mdi:email" class="w-4 h-4" />
                  Contact Us
                </a>
              </li>
            </ul>
          </div>
        </nav>
      </aside>

      <!-- Main Documentation Content -->
      <main class="flex-1 min-w-0">
        <slot />
      </main>

      <!-- Table of Contents (Right Sidebar) -->
      <aside class="hidden xl:block w-56 flex-shrink-0">
        <div class="sticky top-24">
          <h3 class="text-xs font-semibold text-gray-500 dark:text-gray-400 uppercase tracking-wider mb-3">
            On This Page
          </h3>
          <nav id="table-of-contents" class="space-y-2">
            <!-- Dynamically populated by JS -->
          </nav>
        </div>
      </aside>
    </div>
  </div>

  <script>
    // Auto-generate table of contents from h2 and h3 headings
    document.addEventListener('DOMContentLoaded', () => {
      const toc = document.getElementById('table-of-contents');
      const headings = document.querySelectorAll('main h2, main h3');

      headings.forEach(heading => {
        const link = document.createElement('a');
        link.href = `#${heading.id}`;
        link.textContent = heading.textContent;
        link.className = `block py-1.5 text-sm transition-colors ${
          heading.tagName === 'H2'
            ? 'text-gray-700 dark:text-gray-300 hover:text-gray-900 dark:hover:text-gray-100'
            : 'pl-4 text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200'
        }`;

        toc?.appendChild(link);
      });

      // Highlight active section in TOC
      const observerOptions = {
        rootMargin: '-20% 0px -35% 0px'
      };

      const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const id = entry.target.getAttribute('id');
            const links = toc?.querySelectorAll('a');
            links?.forEach(link => {
              if (link.getAttribute('href') === `#${id}`) {
                link.classList.add('text-primary-600', 'dark:text-primary-400', 'font-medium');
              } else {
                link.classList.remove('text-primary-600', 'dark:text-primary-400', 'font-medium');
              }
            });
          }
        });
      }, observerOptions);

      headings.forEach(heading => observer.observe(heading));

      // Handle category navigation clicks
      const categoryLinks = document.querySelectorAll('.category-link');
      categoryLinks.forEach(link => {
        link.addEventListener('click', (e) => {
          e.preventDefault();
          const category = link.getAttribute('data-category');

          // Remove active state from all links
          categoryLinks.forEach(l => {
            l.classList.remove('bg-primary-50', 'dark:bg-primary-900/20', 'text-primary-700', 'dark:text-primary-300', 'font-semibold');
          });

          // Add active state to clicked link
          link.classList.add('bg-primary-50', 'dark:bg-primary-900/20', 'text-primary-700', 'dark:text-primary-300', 'font-semibold');

          // Dispatch custom event for Vue component to handle
          window.dispatchEvent(new CustomEvent('category-change', { detail: { category } }));
        });
      });

      // Set initial active category
      const firstCategoryLink = document.querySelector('.category-link');
      if (firstCategoryLink) {
        firstCategoryLink.classList.add('bg-primary-50', 'dark:bg-primary-900/20', 'text-primary-700', 'dark:text-primary-300', 'font-semibold');
      }
    });
  </script>
</Layout>

