    76→
    77→<script setup lang="ts">
    78→import { onMounted, onUnmounted, nextTick } from "vue";
    79→import Typed from "typed.js";
    80→import { Icon } from "@iconify/vue";
    81→import AiConductorGraphic from "../../ui/AiConductorGraphic.vue";
    82→import { usePerformance } from "@/components/composables/usePerformance";
    83→
    84→
    85→
    86→
    87→// Performance monitoring with all VueUse composables included
    88→const {
    89→  measureOperation,
    90→  optimizations,
    91→  batteryInfo,
    92→  networkInfo,
    93→  windowRef
    94→} = usePerformance();
    95→
    96→const typewriterStrings: string[] = ['Post once —', 'Schedule smarter —', 'Grow faster —'];
    97→
    98→let typedInstance: any = null;
    99→let cleanupRefs: {
   100→  timeouts: Set<ReturnType<typeof setTimeout>>;
   101→  callbacks: Set<number>;
   102→} = {
   103→  timeouts: new Set(),
   104→  callbacks: new Set()
   105→};
   106→
   107→onMounted(() => {
   108→  const initOperation = measureOperation('chatInitTime')
   109→
   110→  try {
   111→    // Check performance conditions for adaptive animations
   112→    const prefersReducedMotion = optimizations.reducedMotion
   113→
   114→    const lowBattery = optimizations.batteryOptimization
   115→
   116→    const slowNetwork = networkInfo.effectiveType &&
   117→      (networkInfo.effectiveType === 'slow-2g' ||
   118→       networkInfo.effectiveType === '2g')
   119→
   120→    const shouldOptimizeAnimations = prefersReducedMotion ||
   121→      (optimizations.batteryOptimization && lowBattery) ||
   122→      slowNetwork
   123→
   124→    // Adaptive typewriter configuration
   125→    const typewriterConfig = {
   126→      strings: typewriterStrings,
   127→      smartBackspace: false,
   128→      shuffle: !shouldOptimizeAnimations, // Disable shuffle for better performance
   129→      typeSpeed: shouldOptimizeAnimations ? 100 : 50, // Faster typing when optimizing
   130→      backSpeed: shouldOptimizeAnimations ? 60 : 30,
   131→      backDelay: shouldOptimizeAnimations ? 1000 : 2000, // Shorter delays
   132→      loop: !shouldOptimizeAnimations, // Disable loop for reduced motion
   133→      showCursor: true,
   134→      cursorChar: '|',
   135→    };
   136→
   137→    // Add progressive enhancement classes
   138→    if (windowRef.value) {
   139→      windowRef.value.document.documentElement.classList.add('js-enabled')
   140→      if (shouldOptimizeAnimations) {
   141→        windowRef.value.document.documentElement.classList.add('reduced-motion')
   142→      }
   143→    }
   144→
   145→    // Initialize typewriter with performance considerations
   146→    if (!shouldOptimizeAnimations) {
   147→      typedInstance = new Typed(".typewriter", typewriterConfig);
   148→    } else {
   149→      // For reduced performance scenarios, show static text
   150→      if (windowRef.value) {
   151→        const typewriterElement = windowRef.value.document.querySelector('.typewriter');
   152→        if (typewriterElement) {
   153→          typewriterElement.textContent = typewriterStrings[0].replace(' —', '');
   154→        }
   155→      }
   156→    }
   157→
   158→    // Use requestIdleCallback for non-critical operations
   159→    nextTick(() => {
   160→      if (windowRef.value && 'requestIdleCallback' in windowRef.value && !shouldOptimizeAnimations) {
   161→        const callbackId = (windowRef.value as any).requestIdleCallback(() => {
   162→          cleanupRefs.callbacks.delete(callbackId)
   163→          // Non-critical initialization code
   164→        });
   165→        cleanupRefs.callbacks.add(callbackId)
   166→      } else {
   167→        // Fallback for browsers without requestIdleCallback or when optimizing
   168→        const timeoutId = setTimeout(() => {
   169→          cleanupRefs.timeouts.delete(timeoutId)
   170→          // Simplified initialization
   171→        }, shouldOptimizeAnimations ? 0 : 100);
   172→        cleanupRefs.timeouts.add(timeoutId)
   173→      }
   174→    });
   175→
   176→    initOperation.end()
   177→  } catch (error) {
   178→    console.error('HeroSection: Error during typewriter initialization:', error)
   179→    initOperation.end()
   180→  }
   181→});
   182→
   183→onUnmounted(() => {
   184→  // Clean up typewriter instance
   185→  if (typedInstance) {
   186→    typedInstance.destroy();
   187→    typedInstance = null;
   188→  }
   189→
   190→  // Clear all timeouts
   191→  cleanupRefs.timeouts.forEach(timeout => {
   192→    clearTimeout(timeout);
   193→  });
   194→  cleanupRefs.timeouts.clear();
   195→
   196→  // Cancel idle callbacks
   197→  cleanupRefs.callbacks.forEach(callbackId => {
   198→    if (windowRef.value && 'cancelIdleCallback' in windowRef.value) {
   199→      (windowRef.value as any).cancelIdleCallback(callbackId);
   200→    }
   201→  });
   202→  cleanupRefs.callbacks.clear();
   203→});
   204→</script>
   205→
