    74→import { ref, computed, watch, onMounted, onUnmounted } from 'vue'
    75→import { Icon } from '@iconify/vue'
    76→import { useRafFn } from '@vueuse/core'
    77→import { useAnimationEnvironment } from '@/components/composables/useAnimationEnvironment'
    78→
    79→interface ScheduledPost {
    80→  icon: string
    81→  color: string
    82→  time: number
    83→}
    84→
    85→interface Props {
    86→  active: boolean
    87→  timeInterval?: number // milliseconds between time changes
    88→}
    89→
    90→const props = withDefaults(defineProps<Props>(), {
    91→  timeInterval: 2500
    92→})
    93→
    94→const { optimizations } = useAnimationEnvironment()
    95→
    96→const currentHour = ref(9)
    97→const showPeakIndicator = ref(false)
    98→const hasCompletedCycle = ref(false)
    99→const stageIndex = ref(0)
   100→const startTime = ref<number | null>(null)
   101→const isRunning = ref(false)
   102→
   103→const scheduledPosts: ScheduledPost[] = [
   104→  { icon: 'mdi:instagram', color: 'text-pink-600 dark:text-pink-400', time: 9 },
   105→  { icon: 'mdi:linkedin', color: 'text-blue-700 dark:text-blue-500', time: 12 },
   106→  { icon: 'mdi:twitter', color: 'text-blue-500 dark:text-blue-400', time: 15 },
   107→  { icon: 'mdi:facebook', color: 'text-blue-600 dark:text-blue-400', time: 18 }
   108→]
   109→
   110→const cycleDuration = computed(() => Math.max(props.timeInterval, 1200))
   111→const totalDuration = computed(() => cycleDuration.value * (scheduledPosts.length - 1))
   112→
   113→const displayHour = computed(() => {
   114→  const hour = currentHour.value % 12 || 12
   115→  return `${hour}:00`
   116→})
   117→
   118→const timeLabel = computed(() => (currentHour.value < 12 ? 'AM' : 'PM'))
   119→
   120→const hourRotation = computed(() => (currentHour.value % 12) * 30)
   121→
   122→const isPeakTime = (postTime: number) => currentHour.value === postTime
   123→
   124→const stopCycle = () => {
   125→  pauseLoop()
   126→  startTime.value = null
   127→  isRunning.value = false
   128→  showPeakIndicator.value = false
   129→}
   130→
   131→const startCycle = () => {
   132→  if (optimizations.reducedMotion || hasCompletedCycle.value) {
   133→    currentHour.value = scheduledPosts[0].time
   134→    showPeakIndicator.value = false
   135→    return
   136→  }
   137→
   138→  stageIndex.value = 0
   139→  currentHour.value = scheduledPosts[0].time
   140→  showPeakIndicator.value = true
   141→  startTime.value = performance.now()
   142→  hasCompletedCycle.value = false
   143→  isRunning.value = true
   144→  resumeLoop()
   145→}
   146→
   147→const { pause: pauseLoop, resume: resumeLoop } = useRafFn(() => {
   148→  if (!isRunning.value || startTime.value === null) return
   149→
   150→  const now = performance.now()
   151→  const elapsed = now - startTime.value
   152→
   153→  if (elapsed >= totalDuration.value + cycleDuration.value) {
   154→    hasCompletedCycle.value = true
   155→    stopCycle()
   156→    return
   157→  }
   158→
   159→  const currentStage = Math.min(
   160→    Math.floor(elapsed / cycleDuration.value),
   161→    scheduledPosts.length - 1
   162→  )
   163→
   164→  if (currentStage !== stageIndex.value) {
   165→    stageIndex.value = currentStage
   166→    currentHour.value = scheduledPosts[currentStage].time
   167→  }
   168→
   169→  const stageElapsed = elapsed - currentStage * cycleDuration.value
   170→  showPeakIndicator.value = stageElapsed < Math.min(1200, cycleDuration.value / 2)
   171→}, { immediate: false })
   172→
   173→// Hour marker position helpers
   174→const getHourMarkerStyle = (hour: number) => {
   175→  const angle = (hour / 12) * 360 - 90
   176→  const radius = 112
   177→  const x = Math.round(Math.cos(angle * Math.PI / 180) * radius * 100) / 100
   178→  const y = Math.round(Math.sin(angle * Math.PI / 180) * radius * 100) / 100
   179→
   180→  return {
   181→    left: `calc(50% + ${x}px)`,
   182→    top: `calc(50% + ${y}px)`,
   183→    transform: 'translate(-50%, -50%)'
   184→  }
   185→}
   186→
   187→const getPostStyle = (index: number) => {
   188→  const angle = (index / scheduledPosts.length) * 360 - 90
   189→  const radius = 200
   190→  const x = Math.round(Math.cos(angle * Math.PI / 180) * radius * 100) / 100
   191→  const y = Math.round(Math.sin(angle * Math.PI / 180) * radius * 100) / 100
   192→
   193→  return {
   194→    left: `calc(50% + ${x}px)`,
   195→    top: `calc(50% + ${y}px)`,
   196→    transform: 'translate(-50%, -50%)',
   197→    animationDelay: `${index * 0.3}s`
   198→  }
   199→}
   200→
   201→watch(() => props.active, (isActive) => {
   202→  stopCycle()
   203→
   204→  if (isActive) {
   205→    startCycle()
   206→  }
   207→})
   208→
   209→watch(() => optimizations.reducedMotion, (value) => {
   210→  if (value) {
   211→    stopCycle()
   212→    currentHour.value = scheduledPosts[0].time
   213→    showPeakIndicator.value = false
   214→  } else if (props.active && !isRunning.value) {
   215→    startCycle()
   216→  }
   217→}, { immediate: true })
   218→
   219→onMounted(() => {
   220→  if (props.active) {
   221→    startCycle()
   222→  }
   223→})
   224→
   225→onUnmounted(() => {
   226→  stopCycle()
   227→})
