const documentVisibility = useDocumentVisibility()
const isElementVisible = useElementVisibility(theaterRef, { threshold: 0.25 })

const shouldAnimate = computed(() => {
  if (optimizations.reducedMotion) return false
  if (!props.active) return false
  if (documentVisibility.value !== 'visible') return false
  return isElementVisible.value
})

// Watch combined animation state for robust handling of visibility/screen reader settings.
watch(shouldAnimate, async (value) => {
