   107→  --color-text-placeholder: var(--color-gray-400);
   108→
   109→  /* ===== DARK MODE COLORS ===== */
   110→  --color-dark-surface-primary: var(--color-gray-800);
   111→  --color-dark-surface-secondary: var(--color-gray-900);
   112→  --color-dark-surface-tertiary: var(--color-gray-950);
   113→  --color-dark-surface-inverse: #ffffff;
   114→
   115→  --color-dark-border-primary: var(--color-gray-700);
   116→  --color-dark-border-secondary: var(--color-gray-600);
