   296→/* ===== KEYFRAME ANIMATIONS ===== */
   297→@keyframes fadeIn {
   298→  from {
   299→    opacity: 0;
   300→  }
   301→  to {
   302→    opacity: 1;
   303→  }
   304→}
   305→
   306→@keyframes fadeOut {
   307→  from {
