   309→  }
   310→  to {
   311→    opacity: 0;
   312→  }
   313→}
   314→
   315→@keyframes slideInUp {
   316→  from {
   317→    opacity: 0;
   318→    transform: translateY(1rem);
   319→  }
   320→  to {
   321→    opacity: 1;
   322→    transform: translateY(0);
   323→  }
   324→}
   325→
   326→@keyframes slideInDown {
