   916→}
   917→
   918→.touch-optimized button,
   919→.touch-optimized [role="button"],
   920→.touch-optimized a,
   921→.touch-optimized input,
   922→.touch-optimized select,
   923→.touch-optimized textarea {
   924→  -webkit-user-select: text;
   925→  user-select: text;
   926→}
   927→
   928→/* Large touch targets for mobile */
   929→.large-touch-targets button,
   930→.large-touch-targets [role="button"],
   931→.large-touch-targets a,
   932→.large-touch-targets .btn-base {
   933→  min-height: 44px;
   934→  min-width: 44px;
   935→  padding: var(--spacing-3) var(--spacing-4);
   936→}
   937→
   938→.large-touch-targets .btn-sm {
   939→  min-height: 40px;
   940→  min-width: 40px;
   941→}
   942→
   943→.large-touch-targets .btn-lg {
   944→  min-height: 48px;
   945→  min-width: 48px;
   946→}
   947→
   948→/* Performance mode optimizations */
   949→.performance-mode * {
   950→  will-change: auto !important;
   951→  animation-duration: 0.1s !important;
   952→  transition-duration: 0.1s !important;
   953→}
   954→
   955→.performance-mode .glass-medium,
   956→.performance-mode .glass-strong {
   957→  backdrop-filter: none !important;
   958→  -webkit-backdrop-filter: none !important;
   959→  background: var(--color-surface-primary) !important;
   960→}
   961→
   962→/* Reduced animations for accessibility and battery */
   963→.reduce-animations * {
   964→  animation-duration: 0.01ms !important;
   965→  animation-iteration-count: 1 !important;
   966→  transition-duration: 0.01ms !important;
   967→}
   968→
   969→.reduce-animations .animate-pulse,
   970→.reduce-animations .animate-bounce,
   971→.reduce-animations .animate-spin {
   972→  animation: none !important;
   973→}
   974→
   975→/* Mobile-specific breakpoints */
   976→@media (max-width: 480px) {
   977→  /* Extra small mobile phones */
   978→  .responsive-mobile-xs {
   979→    font-size: 14px;
   980→    padding: var(--spacing-2);
   981→  }
   982→  
   983→  .btn-base {
   984→    font-size: var(--font-size-sm);
   985→    padding: var(--spacing-2) var(--spacing-3);
   986→  }
   987→  
   988→  .modal-backdrop {
   989→    backdrop-filter: none;
   990→    -webkit-backdrop-filter: none;
   991→  }
   992→}
   993→
   994→@media (max-width: 640px) {
   995→  /* Small mobile devices */
   996→  .responsive-mobile-sm {
   997→    padding: var(--spacing-3);
   998→  }
   999→  
  1000→  .card-base {
  1001→    border-radius: var(--radius-lg);
  1002→    margin: var(--spacing-2);
  1003→  }
  1004→  
  1005→  .glass-medium,
  1006→  .glass-strong {
  1007→    backdrop-filter: blur(8px) !important;
  1008→    -webkit-backdrop-filter: blur(8px) !important;
  1009→  }
  1010→}
  1011→
  1012→/* Landscape mobile orientation */
  1013→@media (max-height: 500px) and (orientation: landscape) {
  1014→  .landscape-optimize {
  1015→    height: 100vh;
  1016→    overflow-y: auto;
  1017→  }
  1018→  
  1019→  .notifications-container {
  1020→    top: var(--spacing-2);
  1021→    right: var(--spacing-2);
  1022→  }
  1023→  
  1024→  .chat-widget-container {
  1025→    bottom: var(--spacing-2);
  1026→    right: var(--spacing-2);
  1027→  }
  1028→}
  1029→
  1030→/* High DPI displays (retina) */
  1031→@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1032→  .retina-optimize {
  1033→    image-rendering: -webkit-optimize-contrast;
  1034→    image-rendering: crisp-edges;
  1035→  }
  1036→  
  1037→  .retina-optimize img {
  1038→    image-rendering: -webkit-optimize-contrast;
  1039→    image-rendering: crisp-edges;
  1040→  }
  1041→}
  1042→
  1043→/* Hover detection for touch devices */
  1044→@media (hover: none) {
  1045→  .hover-only {
  1046→    display: none !important;
  1047→  }
  1048→  
  1049→  .btn-base:hover {
  1050→    transform: none !important;
  1051→  }
  1052→  
  1053→  .card-base:hover {
  1054→    transform: none !important;
  1055→    box-shadow: var(--shadow-sm) !important;
  1056→  }
  1057→}
  1058→
  1059→/* Pointer device optimization */
  1060→@media (pointer: coarse) {
  1061→  /* Touch devices */
  1062→  .form-control-base {
  1063→    font-size: 16px; /* Prevents zoom on iOS */
  1064→    padding: var(--spacing-3);
  1065→  }
  1066→  
  1067→  .btn-base {
  1068→    padding: var(--spacing-3) var(--spacing-4);
  1069→  }
  1070→  
  1071→  select,
  1072→  input,
  1073→  textarea {
  1074→    font-size: 16px; /* Prevents zoom on iOS */
  1075→  }
  1076→}
  1077→
  1078→/* Safe area insets for devices with notches */
  1079→@supports (padding: max(0px)) {
  1080→  .safe-area-inset-top {
  1081→    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1082→  }
  1083→  
  1084→  .safe-area-inset-bottom {
  1085→    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1086→  }
  1087→  
  1088→  .safe-area-inset-left {
  1089→    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1090→  }
  1091→  
  1092→  .safe-area-inset-right {
  1093→    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1094→  }
  1095→}
  1096→
  1097→/* PWA display modes */
  1098→@media (display-mode: standalone) {
  1099→  .pwa-standalone {
  1100→    padding-top: var(--spacing-4);
  1101→  }
  1102→  
  1103→  .pwa-standalone .navbar {
  1104→    padding-top: env(safe-area-inset-top);
  1105→  }
  1106→}
  1107→
  1108→/* Connection-aware styles */
  1109→.slow-connection .glass-medium,
  1110→.slow-connection .glass-strong {
  1111→  backdrop-filter: none !important;
  1112→  -webkit-backdrop-filter: none !important;
  1113→  background: var(--color-surface-primary) !important;
  1114→}
  1115→
  1116→.slow-connection img {
  1117→  filter: blur(2px);
  1118→  transition: filter var(--transition-base);
  1119→}
  1120→
  1121→.slow-connection img.loaded {
  1122→  filter: none;
  1123→}
  1124→
  1125→/* Gesture feedback */
  1126→.gesture-feedback {
  1127→  position: relative;
  1128→  overflow: hidden;
  1129→}
  1130→
  1131→.gesture-feedback::after {
  1132→  content: '';
  1133→  position: absolute;
  1134→  top: 50%;
  1135→  left: 50%;
  1136→  width: 0;
  1137→  height: 0;
  1138→  border-radius: 50%;
  1139→  background: rgba(0, 0, 0, 0.1);
  1140→  transform: translate(-50%, -50%);
  1141→  transition: width 0.3s ease, height 0.3s ease;
  1142→  pointer-events: none;
  1143→}
  1144→
  1145→.gesture-feedback.active::after {
  1146→  width: 100px;
  1147→  height: 100px;
  1148→}
  1149→
  1150→/* Dark mode gesture feedback */
  1151→[data-theme="dark"] .gesture-feedback::after {
  1152→  background: rgba(255, 255, 255, 0.1);
  1153→}
  1154→
  1155→/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1156→
  1157→/* Swipe gesture indicators */
  1158→.swipe-indicator {
  1159→  position: relative;
  1160→}
  1161→
  1162→.swipe-indicator::before {
  1163→  content: '';
  1164→  position: absolute;
  1165→  top: 50%;
  1166→  right: var(--spacing-4);
  1167→  width: 20px;
  1168→  height: 2px;
  1169→  background: var(--color-gray-400);
  1170→  transform: translateY(-50%);
  1171→  opacity: 0.5;
  1172→}
  1173→
  1174→.swipe-indicator::after {
  1175→  content: '';
  1176→  position: absolute;
  1177→  top: 50%;
  1178→  right: var(--spacing-3);
  1179→  width: 0;
  1180→  height: 0;
  1181→  border-left: 6px solid var(--color-gray-400);
  1182→  border-top: 4px solid transparent;
  1183→  border-bottom: 4px solid transparent;
  1184→  transform: translateY(-50%);
  1185→  opacity: 0.5;
  1186→}
  1187→
  1188→/* Loading states optimized for mobile */
  1189→.mobile-loading {
  1190→  display: flex;
  1191→  flex-direction: column;
  1192→  align-items: center;
  1193→  justify-content: center;
  1194→  min-height: 200px;
  1195→  padding: var(--spacing-8);
  1196→}
  1197→
  1198→.mobile-loading .loading-spinner {
  1199→  width: 40px;
  1200→  height: 40px;
  1201→  border: 3px solid var(--color-gray-200);
  1202→  border-top: 3px solid var(--color-primary-500);
  1203→  border-radius: 50%;
  1204→  animation: spin 1s linear infinite;
  1205→}
  1206→
  1207→.mobile-loading .loading-text {
  1208→  margin-top: var(--spacing-4);
  1209→  font-size: var(--font-size-sm);
  1210→  color: var(--color-text-secondary);
  1211→  text-align: center;
  1212→}
  1213→
  1214→/* Battery optimization styles */
  1215→.battery-optimize {
  1216→  animation-play-state: paused !important;
  1217→}
  1218→
  1219→.battery-optimize * {
  1220→  animation-play-state: paused !important;
  1221→  transition-duration: 0.1s !important;
  1222→}
  1223→
  1224→.battery-optimize .glass-medium,
  1225→.battery-optimize .glass-strong {
  1226→  backdrop-filter: none !important;
  1227→  -webkit-backdrop-filter: none !important;
  1228→  background: var(--color-surface-primary) !important;
  1229→}
  1230→
  1231→.battery-optimize .animate-pulse,
  1232→.battery-optimize .animate-bounce,
  1233→.battery-optimize .animate-spin {
  1234→  animation: none !important;
  1235→}
  1236→
  1237→/* Accessibility enhancements for mobile */
  1238→@media (max-width: 768px) {
  1239→  .mobile-a11y button,
  1240→  .mobile-a11y [role="button"],
  1241→  .mobile-a11y input,
  1242→  .mobile-a11y select {
  1243→    min-height: 44px;
