diff --git a/src/components/vue/display/workflows/configs/AIGenCaptionConfig.vue b/src/components/vue/display/workflows/configs/AIGenCaptionConfig.vue
new file mode 100644
index 00000000..91cb1416
--- /dev/null
+++ b/src/components/vue/display/workflows/configs/AIGenCaptionConfig.vue
@@ -0,0 +1,114 @@
+<template>
+  <div class="flex flex-col" :class="displayMode ? 'gap-2 mt-2' : 'gap-4'">
+    <!-- Display Mode -->
+    <template v-if="displayMode">
+      <!-- Instructions Preview -->
+      <div v-if="config.user_instructions" class="flex items-start gap-2 text-sm text-gray-600 dark:text-gray-300">
+        <Icon icon="mdi:robot-excited" class="w-4 h-4 mt-0.5" />
+        <span class="line-clamp-2">{{ config.user_instructions }}</span>
+      </div>
+
+      <!-- Settings Summary -->
+      <div class="flex items-center gap-3 text-xs text-gray-500 dark:text-gray-400 ml-6">
+        <div class="flex items-center gap-1">
+          <Icon icon="mdi:palette" class="w-4 h-4" />
+          <span>{{ formatStyle(config.style) }}</span>
+        </div>
+        <div class="flex items-center gap-1">
+          <Icon icon="mdi:text-box-outline" class="w-4 h-4" />
+          <span>{{ formatLength(config.length) }}</span>
+        </div>
+      </div>
+    </template>
+
+    <!-- Edit Mode -->
+    <template v-else>
+      <TextAreaInput label="Instructions" v-model="config.user_instructions"
+        placeholder="Enter your caption generation instructions..."
+        icon="mdi:robot-excited"
+        description="Generates ONLY new caption content (title will not be changed)"
+        :rows="3" />
+
+      <SearchableDropdown label="Style" v-model="config.style" :options="[
+        { value: 'professional', label: 'Professional' },
+        { value: 'casual', label: 'Casual' },
+        { value: 'creative', label: 'Creative' },
+        { value: 'formal', label: 'Formal' },
+        { value: 'friendly', label: 'Friendly' },
+        { value: 'match', label: 'Match Original' },
+      ]" icon="mdi:palette" placeholder="Select writing style" />
+
+      <SearchableDropdown label="Length" v-model="config.length" :options="[
+        { value: 'shorter', label: 'Shorter' },
+        { value: 'same', label: 'Same Length' },
+        { value: 'longer', label: 'Longer' },
+      ]" icon="mdi:text-box-outline" placeholder="Select desired length" />
+    </template>
+  </div>
+</template>
+
+<script setup lang="ts">
+import { ref, watch } from "vue";
+import { Icon } from '@iconify/vue';
+import TextAreaInput from "@/components/vue/inputs/TextAreaInput.vue";
+import SearchableDropdown from "@/components/vue/inputs/SearchableDropdown.vue";
+import type { AIGenerateCaptionActionConfig } from "@/schemas/workflows/actionSchemas";
+import { useNotification } from "@/utils/useNotification";
+
+const props = defineProps<{
+  displayMode?: boolean;
+}>();
+
+const modelValue = defineModel<AIGenerateCaptionActionConfig>({
+  required: true,
+  default: () => ({
+    user_instructions: "",
+    style: "match",
+    tone: "match",
+    length: "same",
+    content: "",
+  }),
+});
+
+const notification = useNotification();
+
+const config = ref<AIGenerateCaptionActionConfig>({
+  user_instructions: "",
+  tone: "match",
+  style: "match",
+  length: "same",
+  content: "",
+});
+
+const formatStyle = (style: string) => {
+  if (style === 'match') return 'Match Original Style';
+  return style.charAt(0).toUpperCase() + style.slice(1);
+};
+
+const formatLength = (length: string) => {
+  switch (length) {
+    case 'shorter': return 'Shorter Length';
+    case 'same': return 'Same Length';
+    case 'longer': return 'Longer Length';
+    default: return length;
+  }
+};
+
+watch(() => modelValue.value, (newValue) => {
+  try {
+    if (newValue) {
+      config.value = newValue;
+    }
+  } catch (e) {
+    notification.error("Failed to parse AI caption generation configuration");
+  }
+}, { immediate: true });
+
+watch(config, (newConfig) => {
+  try {
+    modelValue.value = newConfig;
+  } catch (e) {
+    notification.error("Failed to save AI caption generation configuration");
+  }
+}, { deep: true });
+</script>
