     1‚Üí<template>
     2‚Üí  <div class="relative mt-2 mb-4" ref="referenceEl">
     3‚Üí    <label v-if="label"
     4‚Üí      class="absolute text-sm font-medium text-black dark:text-gray-300 left-2 label-position px-1 z-10 bg-white dark:bg-gray-800">
     5‚Üí      {{ label }}
     6‚Üí      <span v-if="required" class="text-red-500 font-medium">*</span>
     7‚Üí    </label>
     8‚Üí    <div class="relative">
     9‚Üí      <!-- Selected options tags for multiple mode -->
    10‚Üí      <div v-if="multiple && selectedOptions.length > 0" class="flex flex-wrap gap-1 mb-2">
    11‚Üí        <div v-for="option in selectedOptions" :key="option.value"
    12‚Üí          class="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full flex items-center text-sm">
    13‚Üí          <span>{{ option.label }}</span>
    14‚Üí          <button @click.stop="removeOption(option.value)" class="ml-1 text-blue-500 hover:text-blue-700">
    15‚Üí            <Icon icon="mdi:close-circle" class="w-4 h-4" />
    16‚Üí          </button>
    17‚Üí        </div>
    18‚Üí      </div>
    19‚Üí
    20‚Üí      <input ref="inputEl" :value="displayValue" @input="handleInput" @focus="openDropdown" @blur="handleBlur"
    21‚Üí        @touchstart="handleTouchStart" @keydown.esc="isOpen = false" @keydown.down.prevent="handleArrowDown"
    22‚Üí        @keydown.up.prevent="handleArrowUp" @keydown.enter.prevent="handleEnter" :placeholder="placeholder"
    23‚Üí        :disabled="disabled" :class="[
    24‚Üí          'border rounded-full w-full py-2 px-4 h-12 text-gray-700 dark:text-gray-200 bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-primary-500 dark:focus:ring-primary-400',
    25‚Üí          inputClass,
    26‚Üí          icon ? 'pl-10' : '',
    27‚Üí          disabled ? 'opacity-50 cursor-not-allowed' : '',
    28‚Üí        ]" type="text" />
    29‚Üí      <Icon v-if="icon && mounted" :icon="icon"
    30‚Üí        class="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 dark:text-gray-500" />
    31‚Üí      <Icon icon="mdi:chevron-down"
    32‚Üí        class="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 dark:text-gray-500" />
    33‚Üí    </div>
    34‚Üí    <p v-if="errorMessage" class="mt-1 text-sm text-red-500">
    35‚Üí      {{ errorMessage }}
    36‚Üí    </p>
    37‚Üí    <p v-else-if="description" class="mt-1 text-sm text-gray-500 dark:text-gray-400">
    38‚Üí      {{ description }}
    39‚Üí    </p>
    40‚Üí
    41‚Üí    <!-- Dropdown -->
    42‚Üí    <div v-show="isOpen" ref="floatingEl" :style="[
    43‚Üí      floatingStyles,
    44‚Üí      {
    45‚Üí        maxHeight: '240px',
    46‚Üí        width: '100%',
    47‚Üí      },
    48‚Üí    ]"
    49‚Üí      class="absolute z-[1050] bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 rounded-lg shadow-lg overflow-y-auto">
    50‚Üí      <div v-if="loading" class="p-2 text-gray-700 dark:text-gray-200">
    51‚Üí        <slot name="loading">
    52‚Üí          <div class="animate-pulse flex space-x-4">
    53‚Üí            <div class="flex-1 space-y-4 py-1">
    54‚Üí              <div class="h-4 bg-gray-300 rounded w-3/4"></div>
    55‚Üí            </div>
    56‚Üí          </div>
    57‚Üí        </slot>
    58‚Üí      </div>
    59‚Üí      <template v-else>
    60‚Üí        <div v-for="(option, index) in selectableOptions" :key="option.value" @mousedown="selectOption(option)"
    61‚Üí          @touchstart.prevent="selectOption(option)" :class="[
    62‚Üí            'p-2 cursor-pointer text-gray-700 dark:text-gray-200 flex items-center',
    63‚Üí            {
    64‚Üí              'bg-gray-100 dark:bg-gray-700': highlightedIndex === index,
    65‚Üí              'hover:bg-gray-50 dark:hover:bg-gray-700':
    66‚Üí                highlightedIndex !== index,
    67‚Üí            },
    68‚Üí          ]">
    69‚Üí          <Icon v-if="isSelected(option.value)" icon="mdi:check" class="mr-2 text-green-500" />
    70‚Üí          <span :class="{ 'ml-6': !isSelected(option.value) }">{{
    71‚Üí            option.label
    72‚Üí          }}</span>
    73‚Üí        </div>
    74‚Üí      </template>
    75‚Üí    </div>
    76‚Üí  </div>
    77‚Üí</template>
    78‚Üí
    79‚Üí<script setup lang="ts">
    80‚Üíimport { ref, computed, watch, nextTick } from "vue";
    81‚Üíimport { useMounted } from "@vueuse/core";
    82‚Üíimport { useFloating, offset, flip, shift } from "@floating-ui/vue";
    83‚Üíimport { Icon } from "@iconify/vue";
    84‚Üí
    85‚Üíinterface Option {
    86‚Üí  value: string;
    87‚Üí  label: string;
    88‚Üí  isDefault?: boolean;
    89‚Üí}
    90‚Üí
    91‚Üíconst props = defineProps<{
    92‚Üí  options: Option[];
    93‚Üí  isValidTest?: (value: string | string[]) => boolean;
    94‚Üí  isValid?: boolean;
    95‚Üí  placeholder?: string;
    96‚Üí  icon?: string;
    97‚Üí  label?: string;
    98‚Üí  loading?: boolean;
    99‚Üí  description?: string;
   100‚Üí  required?: boolean;
   101‚Üí  disabled?: boolean;
   102‚Üí  invalidMessage?: string;
   103‚Üí  customClass?: string;
   104‚Üí  multiple?: boolean;
   105‚Üí}>();
   106‚Üí
   107‚Üí// Define the model without referencing props in the default function
   108‚Üíconst modelValue = defineModel<string | string[]>("modelValue", { default: "" });
   109‚Üí
   110‚Üí// Initialize model value correctly based on multiple prop
   111‚Üíwatch(() => props.multiple, (isMultiple) => {
   112‚Üí  if (isMultiple && typeof modelValue.value === 'string') {
   113‚Üí    // Convert to array for multiple mode if it's a string
   114‚Üí    modelValue.value = modelValue.value ? [modelValue.value] : [];
   115‚Üí  } else if (!isMultiple && Array.isArray(modelValue.value)) {
   116‚Üí    // Convert to string for single mode if it's an array
   117‚Üí    modelValue.value = modelValue.value.length > 0 ? modelValue.value[0] : '';
   118‚Üí  }
   119‚Üí}, { immediate: true });
   120‚Üí
   121‚Üíconst searchQuery = ref("");
   122‚Üíconst mounted = useMounted();
   123‚Üíconst isOpen = ref(false);
   124‚Üíconst hasBlurredInput = ref(false);
   125‚Üíconst errorMessage = ref("");
   126‚Üíconst referenceEl = ref<HTMLElement>();
   127‚Üíconst floatingEl = ref<HTMLElement>();
   128‚Üíconst inputEl = ref<HTMLElement>();
   129‚Üíconst highlightedIndex = ref(-1);
   130‚Üí
   131‚Üíconst { x, y, strategy, update } = useFloating(referenceEl, floatingEl, {
   132‚Üí  placement: "bottom-start",
   133‚Üí  middleware: [
   134‚Üí    offset(4),
   135‚Üí    flip({
   136‚Üí      padding: 8,
   137‚Üí      fallbackPlacements: ["top-start"],
   138‚Üí    }),
   139‚Üí    shift({ padding: 8 }),
   140‚Üí  ],
   141‚Üí});
   142‚Üí
   143‚Üíconst floatingStyles = computed(() => ({
   144‚Üí  position: strategy.value,
   145‚Üí  top: `${y.value}px`,
   146‚Üí  left: `${x.value}px`,
   147‚Üí}));
   148‚Üí
   149‚Üíconst defaultOption = computed(() =>
   150‚Üí  props.options.find((option) => option.isDefault)
   151‚Üí);
   152‚Üí
   153‚Üí// Get the selected options for multiple mode
   154‚Üíconst selectedOptions = computed(() => {
   155‚Üí  if (!props.multiple) return [];
   156‚Üí  const valueArray = Array.isArray(modelValue.value) ? modelValue.value : [modelValue.value].filter(Boolean);
   157‚Üí  return props.options.filter(option => valueArray.includes(option.value));
   158‚Üí});
   159‚Üí
   160‚Üíconst displayValue = computed(() => {
   161‚Üí  if (isOpen.value) {
   162‚Üí    return searchQuery.value;
   163‚Üí  }
   164‚Üí
   165‚Üí  if (props.multiple) {
   166‚Üí    // For multiple selection, clear the input value since we show tags
   167‚Üí    return "";
   168‚Üí  }
   169‚Üí
   170‚Üí  if (!modelValue.value && defaultOption.value) {
   171‚Üí    return defaultOption.value.label;
   172‚Üí  }
   173‚Üí
   174‚Üí  const selectedOption = props.options.find(
   175‚Üí    (option) => option.value === modelValue.value
   176‚Üí  );
   177‚Üí  return selectedOption ? selectedOption.label : searchQuery.value;
   178‚Üí});
   179‚Üí
   180‚Üíconst inputClass = computed(() => {
   181‚Üí  if (!mounted.value) return "";
   182‚Üí  if (!hasBlurredInput.value) return "";
   183‚Üí
   184‚Üí  if (props.isValidTest) {
   185‚Üí    return props.isValidTest(modelValue.value) || props.isValid
   186‚Üí      ? "border-green-500"
   187‚Üí      : "border-red-500";
   188‚Üí  }
   189‚Üí
   190‚Üí  if (props.required && (!modelValue.value || (Array.isArray(modelValue.value) && modelValue.value.length === 0))) {
   191‚Üí    return "border-red-500";
   192‚Üí  }
   193‚Üí
   194‚Üí  if (props.isValid === undefined || !props.isValidTest) return "";
   195‚Üí  return props.isValid ? "border-green-500" : "border-red-500";
   196‚Üí});
   197‚Üí
   198‚Üíconst selectableOptions = computed(() => {
   199‚Üí  const query = searchQuery.value.toLowerCase();
   200‚Üí  return props.options
   201‚Üí    .filter((option) => !option.isDefault)
   202‚Üí    .filter(
   203‚Üí      (option) =>
   204‚Üí        !searchQuery.value || option.label.toLowerCase().includes(query)
   205‚Üí    );
   206‚Üí});
   207‚Üí
   208‚Üíconst handleInput = (event: Event) => {
   209‚Üí  const target = event.target as HTMLInputElement;
   210‚Üí  searchQuery.value = target.value;
   211‚Üí  isOpen.value = true;
   212‚Üí  highlightedIndex.value = -1;
   213‚Üí
   214‚Üí  if (hasBlurredInput.value && props.isValidTest) {
   215‚Üí    validateInput();
   216‚Üí  }
   217‚Üí
   218‚Üí  nextTick(() => {
   219‚Üí    if (isOpen.value && mounted.value) {
   220‚Üí      update();
   221‚Üí    }
   222‚Üí  });
   223‚Üí};
   224‚Üí
   225‚Üíconst handleTouchStart = (event: TouchEvent) => {
   226‚Üí  event.preventDefault();
   227‚Üí  if (!props.disabled) {
   228‚Üí    openDropdown();
   229‚Üí  }
   230‚Üí};
   231‚Üí
   232‚Üíconst openDropdown = () => {
   233‚Üí  if (!props.disabled) {
   234‚Üí    isOpen.value = true;
   235‚Üí    searchQuery.value = ""; // Clear search query when opening dropdown
   236‚Üí    highlightedIndex.value = -1;
   237‚Üí    nextTick(() => {
   238‚Üí      if (mounted.value) {
   239‚Üí        update();
   240‚Üí      }
   241‚Üí    });
   242‚Üí  }
   243‚Üí};
   244‚Üí
   245‚Üíconst handleBlur = () => {
   246‚Üí  hasBlurredInput.value = true;
   247‚Üí  // Delay closing to allow for option selection
   248‚Üí  setTimeout(() => {
   249‚Üí    isOpen.value = false;
   250‚Üí    if (props.isValidTest) {
   251‚Üí      validateInput();
   252‚Üí    }
   253‚Üí    // Reset search query and show selected value or default
   254‚Üí    searchQuery.value = "";
   255‚Üí    highlightedIndex.value = -1;
   256‚Üí  }, 200);
   257‚Üí};
   258‚Üí
   259‚Üíconst selectOption = (option: Option) => {
   260‚Üí  if (props.multiple) {
   261‚Üí    // For multiple selection, toggle the selection
   262‚Üí    const valueArray = Array.isArray(modelValue.value) ? [...modelValue.value] : [];
   263‚Üí    const index = valueArray.indexOf(option.value);
   264‚Üí
   265‚Üí    if (index >= 0) {
   266‚Üí      valueArray.splice(index, 1); // Remove if already selected
   267‚Üí    } else {
   268‚Üí      valueArray.push(option.value); // Add if not selected
   269‚Üí    }
   270‚Üí    modelValue.value = valueArray;
   271‚Üí  } else {
   272‚Üí    // For single selection
   273‚Üí    modelValue.value = option.value;
   274‚Üí  }
   275‚Üí
   276‚Üí  searchQuery.value = "";
   277‚Üí  if (!props.multiple) {
   278‚Üí    isOpen.value = false;
   279‚Üí  }
   280‚Üí
   281‚Üí  if (props.isValidTest) {
   282‚Üí    validateInput();
   283‚Üí  }
   284‚Üí  highlightedIndex.value = -1;
   285‚Üí};
   286‚Üí
   287‚Üíconst removeOption = (value: string) => {
   288‚Üí  if (!props.multiple || !Array.isArray(modelValue.value)) return;
   289‚Üí
   290‚Üí  const index = modelValue.value.indexOf(value);
   291‚Üí  if (index >= 0) {
   292‚Üí    const newValue = [...modelValue.value];
   293‚Üí    newValue.splice(index, 1);
   294‚Üí    modelValue.value = newValue;
   295‚Üí  }
   296‚Üí
   297‚Üí  if (props.isValidTest) {
   298‚Üí    validateInput();
   299‚Üí  }
   300‚Üí};
   301‚Üí
   302‚Üíconst handleArrowDown = () => {
   303‚Üí  if (!isOpen.value) {
   304‚Üí    openDropdown();
   305‚Üí    return;
   306‚Üí  }
   307‚Üí  highlightedIndex.value = Math.min(
   308‚Üí    highlightedIndex.value + 1,
   309‚Üí    selectableOptions.value.length - 1
   310‚Üí  );
   311‚Üí  scrollHighlightedOptionIntoView();
   312‚Üí};
   313‚Üí
   314‚Üíconst handleArrowUp = () => {
   315‚Üí  if (!isOpen.value) {
   316‚Üí    openDropdown();
   317‚Üí    return;
   318‚Üí  }
   319‚Üí  highlightedIndex.value = Math.max(highlightedIndex.value - 1, 0);
   320‚Üí  scrollHighlightedOptionIntoView();
   321‚Üí};
   322‚Üí
   323‚Üíconst handleEnter = () => {
   324‚Üí  if (isOpen.value && highlightedIndex.value >= 0) {
   325‚Üí    selectOption(selectableOptions.value[highlightedIndex.value]);
   326‚Üí  }
   327‚Üí};
   328‚Üí
   329‚Üíconst scrollHighlightedOptionIntoView = () => {
   330‚Üí  if (!mounted.value) return;
   331‚Üí
   332‚Üí  nextTick(() => {
   333‚Üí    const highlightedEl = floatingEl.value?.children[
   334‚Üí      highlightedIndex.value + 1
   335‚Üí    ] as HTMLElement;
   336‚Üí    if (highlightedEl) {
   337‚Üí      highlightedEl.scrollIntoView({
   338‚Üí        block: "nearest",
   339‚Üí        behavior: "smooth",
   340‚Üí      });
   341‚Üí    }
   342‚Üí  });
   343‚Üí};
   344‚Üí
   345‚Üíconst isSelected = (value: string) => {
   346‚Üí  if (props.multiple) {
   347‚Üí    const valueArray = Array.isArray(modelValue.value) ? modelValue.value : [];
   348‚Üí    return valueArray.includes(value);
   349‚Üí  }
   350‚Üí  return modelValue.value === value;
   351‚Üí};
   352‚Üí
   353‚Üíconst validateInput = () => {
   354‚Üí  errorMessage.value = "";
   355‚Üí
   356‚Üí  if (props.required) {
   357‚Üí    if (
   358‚Üí      !modelValue.value ||
   359‚Üí      (Array.isArray(modelValue.value) && modelValue.value.length === 0)
   360‚Üí    ) {
   361‚Üí      errorMessage.value = "This field is required";
   362‚Üí      return;
   363‚Üí    }
   364‚Üí  }
   365‚Üí
   366‚Üí  if (!modelValue.value || (Array.isArray(modelValue.value) && modelValue.value.length === 0)) {
   367‚Üí    return; // Skip validation if empty and not required
   368‚Üí  }
   369‚Üí
   370‚Üí  if (props.isValidTest && !props.isValidTest(modelValue.value)) {
   371‚Üí    errorMessage.value = props.invalidMessage || "Invalid selection";
   372‚Üí  }
   373‚Üí};
   374‚Üí
   375‚Üí// For single selection, update the display value when modelValue changes
   376‚Üíwatch(modelValue, (newValue) => {
   377‚Üí  if (!props.multiple && !Array.isArray(newValue)) {
   378‚Üí    const selectedOption = props.options.find(
   379‚Üí      (option) => option.value === newValue
   380‚Üí    );
   381‚Üí    if (selectedOption) {
   382‚Üí      searchQuery.value = selectedOption.label;
   383‚Üí    }
   384‚Üí  }
   385‚Üí});
   386‚Üí
   387‚Üí// Update position when dropdown is open
   388‚Üíwatch(isOpen, (newValue) => {
   389‚Üí  if (newValue && mounted.value) {
   390‚Üí    nextTick(update);
   391‚Üí  }
   392‚Üí});
   393‚Üí</script>

