     1‚Üí<template>
     2‚Üí    <BaseModal v-model:open="isOpen" :closeOnClickOutside="true" :closeOnEscape="true"
     3‚Üí        contentClass="max-w-3xl w-full pt-4">
     4‚Üí        <template #title>
     5‚Üí            <h2 class="text-xl font-semibold text-gray-900 dark:text-white px-4 mb-4">
     6‚Üí                Create New Team
     7‚Üí            </h2>
     8‚Üí        </template>
     9‚Üí        <template #content>
    10‚Üí            <div class="flex flex-col gap-3 px-4">
    11‚Üí                <TextInput v-model="teamName" label="Team Name" placeholder="Enter team name" :required="true"
    12‚Üí                    :disabled="isLoading" @keyup.enter="handleCreate" />
    13‚Üí                <p class="text-sm text-gray-500 dark:text-gray-400">
    14‚Üí                    Create a new team to collaborate with others
    15‚Üí                </p>
    16‚Üí            </div>
    17‚Üí        </template>
    18‚Üí
    19‚Üí        <template #footer>
    20‚Üí            <div class="flex justify-end gap-2">
    21‚Üí                <OutlineButton @click="handleClose" text="Cancel" :disabled="isLoading" />
    22‚Üí                <PrimaryButton @click="handleCreate" text="Create Team" :loading="isLoading"
    23‚Üí                    :disabled="!teamName || isLoading" />
    24‚Üí            </div>
    25‚Üí        </template>
    26‚Üí    </BaseModal>
    27‚Üí</template>
    28‚Üí<script setup lang="ts">
    29‚Üíimport { ref } from 'vue';
    30‚Üíimport BaseModal from '@/components/vue/modals/BaseModal.vue';
    31‚Üíimport TextInput from '@/components/vue/inputs/TextInput.vue';
    32‚Üíimport PrimaryButton from '@/components/vue/buttons/PrimaryButton.vue';
    33‚Üíimport OutlineButton from '@/components/vue/buttons/OutlineButton.vue';
    34‚Üí
    35‚ÜídefineProps<{
    36‚Üí    isLoading: boolean;
    37‚Üí}>();
    38‚Üí
    39‚Üíconst isOpen = defineModel('isOpen', {
    40‚Üí    default: false,
    41‚Üí    required: true,
    42‚Üí});
    43‚Üí
    44‚Üíconst emit = defineEmits<{
    45‚Üí    'submit': [name: string];
    46‚Üí}>();
    47‚Üí
    48‚Üíconst teamName = ref('');
    49‚Üí
    50‚Üíconst handleClose = () => {
    51‚Üí    isOpen.value = false;
    52‚Üí    teamName.value = '';
    53‚Üí};
    54‚Üí
    55‚Üíconst handleCreate = () => {
    56‚Üí    if (!teamName.value) return;
    57‚Üí    emit('submit', teamName.value);
    58‚Üí};
    59‚Üí</script>

