    30‚Üí  --color-success-500: #10b981;
    31‚Üí  --color-success-600: #059669;
    32‚Üí  --color-success-700: #047857;
    33‚Üí  --color-success-800: #065f46;
    34‚Üí  --color-success-900: #064e3b;
    35‚Üí  --color-success-950: #022c22;
    36‚Üí
    37‚Üí  /* Semantic Colors - Warning */
    38‚Üí  --color-warning-50: #fffbeb;
    39‚Üí  --color-warning-100: #fef3c7;
    40‚Üí  --color-warning-200: #fde68a;
    41‚Üí  --color-warning-300: #fcd34d;
    42‚Üí  --color-warning-400: #fbbf24;
    43‚Üí  --color-warning-500: #f59e0b;
    44‚Üí  --color-warning-600: #d97706;
    45‚Üí  --color-warning-700: #b45309;
    46‚Üí  --color-warning-800: #92400e;
    47‚Üí  --color-warning-900: #78350f;
    48‚Üí  --color-warning-950: #451a03;
    49‚Üí
    50‚Üí  /* Semantic Colors - Error/Danger */
    51‚Üí  --color-error-50: #fef2f2;
    52‚Üí  --color-error-100: #fee2e2;
    53‚Üí  --color-error-200: #fecaca;
    54‚Üí  --color-error-300: #fca5a5;
    55‚Üí  --color-error-400: #f87171;
    56‚Üí  --color-error-500: #ef4444;
    57‚Üí  --color-error-600: #dc2626;
    58‚Üí  --color-error-700: #b91c1c;
    59‚Üí  --color-error-800: #991b1b;
    60‚Üí  --color-error-900: #7f1d1d;
    61‚Üí  --color-error-950: #450a0a;
    62‚Üí
    63‚Üí  /* Semantic Colors - Info */
    64‚Üí  --color-info-50: #eff6ff;
    65‚Üí  --color-info-100: #dbeafe;
    66‚Üí  --color-info-200: #bfdbfe;
    67‚Üí  --color-info-300: #93c5fd;
    68‚Üí  --color-info-400: #60a5fa;
    69‚Üí  --color-info-500: #3b82f6;
    70‚Üí  --color-info-600: #2563eb;
    71‚Üí  --color-info-700: #1d4ed8;
    72‚Üí  --color-info-800: #1e40af;
    73‚Üí  --color-info-900: #1e3a8a;
    74‚Üí  --color-info-950: #172554;
    75‚Üí
    76‚Üí  /* Neutral Grays */
    77‚Üí  --color-gray-50: #f9fafb;
    78‚Üí  --color-gray-100: #f3f4f6;
    79‚Üí  --color-gray-200: #e5e7eb;
    80‚Üí  --color-gray-300: #d1d5db;
    81‚Üí  --color-gray-400: #9ca3af;
    82‚Üí  --color-gray-500: #6b7280;
    83‚Üí  --color-gray-600: #4b5563;
    84‚Üí  --color-gray-700: #374151;
    85‚Üí  --color-gray-800: #1f2937;
    86‚Üí  --color-gray-900: #111827;
    87‚Üí  --color-gray-950: #030712;
    88‚Üí
    89‚Üí  /* Surface Colors */
    90‚Üí  --color-surface-primary: #ffffff;
    91‚Üí  --color-surface-secondary: var(--color-gray-50);
    92‚Üí  --color-surface-tertiary: var(--color-gray-100);
    93‚Üí  --color-surface-inverse: var(--color-gray-900);
    94‚Üí
    95‚Üí  /* Border Colors */
    96‚Üí  --color-border-primary: var(--color-gray-200);
    97‚Üí  --color-border-secondary: var(--color-gray-300);
    98‚Üí  --color-border-focus: var(--color-primary-500);
    99‚Üí  --color-border-error: var(--color-error-500);
   100‚Üí  --color-border-success: var(--color-success-500);
   101‚Üí
   102‚Üí  /* Text Colors */
   103‚Üí  --color-text-primary: var(--color-gray-900);
   104‚Üí  --color-text-secondary: var(--color-gray-600);
   105‚Üí  --color-text-tertiary: var(--color-gray-500);
   106‚Üí  --color-text-inverse: #ffffff;
   107‚Üí  --color-text-placeholder: var(--color-gray-400);
   108‚Üí
   109‚Üí  /* ===== DARK MODE COLORS ===== */
   110‚Üí  --color-dark-surface-primary: var(--color-gray-800);
   111‚Üí  --color-dark-surface-secondary: var(--color-gray-900);
   112‚Üí  --color-dark-surface-tertiary: var(--color-gray-950);
   113‚Üí  --color-dark-surface-inverse: #ffffff;
   114‚Üí
   115‚Üí  --color-dark-border-primary: var(--color-gray-700);
   116‚Üí  --color-dark-border-secondary: var(--color-gray-600);
   117‚Üí  --color-dark-border-focus: var(--color-primary-400);
   118‚Üí
   119‚Üí  --color-dark-text-primary: var(--color-gray-100);
   120‚Üí  --color-dark-text-secondary: var(--color-gray-300);
   121‚Üí  --color-dark-text-tertiary: var(--color-gray-400);
   122‚Üí  --color-dark-text-placeholder: var(--color-gray-500);
   123‚Üí
   124‚Üí  /* ===== SPACING SYSTEM ===== */
   125‚Üí  --spacing-0: 0;
   126‚Üí  --spacing-px: 1px;
   127‚Üí  --spacing-0_5: 0.125rem;  /* 2px */
   128‚Üí  --spacing-1: 0.25rem;     /* 4px */
