   163‚Üí  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
   164‚Üí  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
   165‚Üí  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
   166‚Üí  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
   167‚Üí  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
   168‚Üí  --shadow-inner: inset 0 2px 4px 0 rgb(0 0 0 / 0.05);
