   174‚Üí
   175‚Üí  /* ===== TRANSITIONS ===== */
   176‚Üí  --transition-fast: 150ms ease;
   177‚Üí  --transition-base: 200ms ease;
   178‚Üí  --transition-medium: 300ms ease;
   179‚Üí  --transition-slow: 500ms ease;
   180‚Üí
   181‚Üí  /* Easing Functions */
   182‚Üí  --ease-linear: linear;
   183‚Üí  --ease-in: cubic-bezier(0.4, 0, 1, 1);
   184‚Üí  --ease-out: cubic-bezier(0, 0, 0.2, 1);
   185‚Üí  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
   186‚Üí  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   187‚Üí
   188‚Üí  /* ===== TYPOGRAPHY ===== */
   189‚Üí  --font-family-sans: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
   190‚Üí  --font-family-mono: 'JetBrains Mono', 'SF Mono', Monaco, Inconsolata, 'Roboto Mono', 'Source Code Pro', monospace;
   191‚Üí  --font-family-emoji: 'Noto Color Emoji', 'Apple Color Emoji', 'Segoe UI Emoji', sans-serif;
   192‚Üí
   193‚Üí  /* Font Weights */
   194‚Üí  --font-weight-thin: 100;
