   203‚Üí
   204‚Üí  /* Font Sizes */
   205‚Üí  --font-size-xs: 0.75rem;      /* 12px */
   206‚Üí  --font-size-sm: 0.875rem;     /* 14px */
   207‚Üí  --font-size-base: 1rem;       /* 16px */
   208‚Üí  --font-size-lg: 1.125rem;     /* 18px */
   209‚Üí  --font-size-xl: 1.25rem;      /* 20px */
   210‚Üí  --font-size-2xl: 1.5rem;      /* 24px */
   211‚Üí  --font-size-3xl: 1.875rem;    /* 30px */
   212‚Üí  --font-size-4xl: 2.25rem;     /* 36px */
   213‚Üí  --font-size-5xl: 3rem;        /* 48px */
   214‚Üí  --font-size-6xl: 3.75rem;     /* 60px */
   215‚Üí  --font-size-7xl: 4.5rem;      /* 72px */
   216‚Üí  --font-size-8xl: 6rem;        /* 96px */
   217‚Üí  --font-size-9xl: 8rem;        /* 128px */
   218‚Üí
   219‚Üí  /* Line Heights */
   220‚Üí  --line-height-none: 1;
   221‚Üí  --line-height-tight: 1.25;
   222‚Üí  --line-height-snug: 1.375;
   223‚Üí  --line-height-normal: 1.5;
