   232‚Üí  --size-xl: 3.5rem;    /* 56px */
   233‚Üí  --size-2xl: 4rem;     /* 64px */
   234‚Üí
   235‚Üí  /* Input Heights */
   236‚Üí  --input-height-sm: var(--size-sm);
   237‚Üí  --input-height-md: var(--size-md);
   238‚Üí  --input-height-lg: var(--size-lg);
   239‚Üí
   240‚Üí  /* ===== Z-INDEX SCALE ===== */
   241‚Üí  --z-index-behind: -1;
   242‚Üí  --z-index-base: 0;
   243‚Üí  --z-index-dropdown: 1000;
   244‚Üí  --z-index-sticky: 1020;
   245‚Üí  --z-index-fixed: 1030;
   246‚Üí  --z-index-modal-backdrop: 1040;
   247‚Üí  --z-index-modal: 1050;
   248‚Üí  --z-index-popover: 1060;
   249‚Üí  --z-index-tooltip: 1070;
   250‚Üí  --z-index-toast: 1080;
   251‚Üí  --z-index-max: 2147483647;
   252‚Üí
   253‚Üí  /* ===== ANIMATION UTILITIES ===== */
   254‚Üí  --animation-pulse: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
   255‚Üí  --animation-bounce: bounce 1s infinite;
   256‚Üí  --animation-spin: spin 1s linear infinite;
   257‚Üí  --animation-ping: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
   258‚Üí  --animation-fade-in: fadeIn var(--transition-base);
   259‚Üí  --animation-fade-out: fadeOut var(--transition-base);
   260‚Üí  --animation-slide-in-up: slideInUp var(--transition-medium) var(--ease-out);
   261‚Üí  --animation-slide-in-down: slideInDown var(--transition-medium) var(--ease-out);
   262‚Üí  --animation-scale-in: scaleIn var(--transition-base) var(--ease-out);
   263‚Üí  --animation-scale-out: scaleOut var(--transition-base) var(--ease-in);
   264‚Üí}
   265‚Üí
   266‚Üí/* ===== DARK MODE OVERRIDES ===== */
   267‚Üí[data-theme="dark"] {
