  1122‚Üí  filter: none;
  1123‚Üí}
  1124‚Üí
  1125‚Üí/* Gesture feedback */
  1126‚Üí.gesture-feedback {
  1127‚Üí  position: relative;
  1128‚Üí  overflow: hidden;
  1129‚Üí}
  1130‚Üí
  1131‚Üí.gesture-feedback::after {
  1132‚Üí  content: '';
  1133‚Üí  position: absolute;
  1134‚Üí  top: 50%;
  1135‚Üí  left: 50%;
  1136‚Üí  width: 0;
  1137‚Üí  height: 0;
  1138‚Üí  border-radius: 50%;
  1139‚Üí  background: rgba(0, 0, 0, 0.1);
  1140‚Üí  transform: translate(-50%, -50%);
  1141‚Üí  transition: width 0.3s ease, height 0.3s ease;
  1142‚Üí  pointer-events: none;
  1143‚Üí}
  1144‚Üí
  1145‚Üí.gesture-feedback.active::after {
  1146‚Üí  width: 100px;
  1147‚Üí  height: 100px;
  1148‚Üí}
  1149‚Üí
  1150‚Üí/* Dark mode gesture feedback */
  1151‚Üí[data-theme="dark"] .gesture-feedback::after {
  1152‚Üí  background: rgba(255, 255, 255, 0.1);
  1153‚Üí}
  1154‚Üí
  1155‚Üí/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1156‚Üí
  1157‚Üí/* Swipe gesture indicators */
  1158‚Üí.swipe-indicator {
  1159‚Üí  position: relative;
  1160‚Üí}
  1161‚Üí
  1162‚Üí.swipe-indicator::before {
  1163‚Üí  content: '';
  1164‚Üí  position: absolute;
  1165‚Üí  top: 50%;
  1166‚Üí  right: var(--spacing-4);
  1167‚Üí  width: 20px;
  1168‚Üí  height: 2px;
  1169‚Üí  background: var(--color-gray-400);
  1170‚Üí  transform: translateY(-50%);
  1171‚Üí  opacity: 0.5;
  1172‚Üí}
  1173‚Üí
  1174‚Üí.swipe-indicator::after {
  1175‚Üí  content: '';
  1176‚Üí  position: absolute;
  1177‚Üí  top: 50%;
  1178‚Üí  right: var(--spacing-3);
  1179‚Üí  width: 0;
  1180‚Üí  height: 0;
  1181‚Üí  border-left: 6px solid var(--color-gray-400);
  1182‚Üí  border-top: 4px solid transparent;
  1183‚Üí  border-bottom: 4px solid transparent;
  1184‚Üí  transform: translateY(-50%);
  1185‚Üí  opacity: 0.5;
  1186‚Üí}
  1187‚Üí
  1188‚Üí/* Loading states optimized for mobile */
  1189‚Üí.mobile-loading {
  1190‚Üí  display: flex;
  1191‚Üí  flex-direction: column;
  1192‚Üí  align-items: center;
  1193‚Üí  justify-content: center;
  1194‚Üí  min-height: 200px;
  1195‚Üí  padding: var(--spacing-8);
  1196‚Üí}
  1197‚Üí
  1198‚Üí.mobile-loading .loading-spinner {
  1199‚Üí  width: 40px;
  1200‚Üí  height: 40px;
  1201‚Üí  border: 3px solid var(--color-gray-200);
  1202‚Üí  border-top: 3px solid var(--color-primary-500);
  1203‚Üí  border-radius: 50%;
  1204‚Üí  animation: spin 1s linear infinite;
  1205‚Üí}
  1206‚Üí
  1207‚Üí.mobile-loading .loading-text {
  1208‚Üí  margin-top: var(--spacing-4);
  1209‚Üí  font-size: var(--font-size-sm);
  1210‚Üí  color: var(--color-text-secondary);
  1211‚Üí  text-align: center;
  1212‚Üí}
  1213‚Üí
  1214‚Üí/* Battery optimization styles */
  1215‚Üí.battery-optimize {
  1216‚Üí  animation-play-state: paused !important;
  1217‚Üí}
  1218‚Üí
  1219‚Üí.battery-optimize * {
  1220‚Üí  animation-play-state: paused !important;
  1221‚Üí  transition-duration: 0.1s !important;
  1222‚Üí}
  1223‚Üí
  1224‚Üí.battery-optimize .glass-medium,
  1225‚Üí.battery-optimize .glass-strong {
  1226‚Üí  backdrop-filter: none !important;
  1227‚Üí  -webkit-backdrop-filter: none !important;
  1228‚Üí  background: var(--color-surface-primary) !important;
  1229‚Üí}
  1230‚Üí
  1231‚Üí.battery-optimize .animate-pulse,
  1232‚Üí.battery-optimize .animate-bounce,
  1233‚Üí.battery-optimize .animate-spin {
  1234‚Üí  animation: none !important;
  1235‚Üí}
  1236‚Üí
  1237‚Üí/* Accessibility enhancements for mobile */
  1238‚Üí@media (max-width: 768px) {
  1239‚Üí  .mobile-a11y button,
  1240‚Üí  .mobile-a11y [role="button"],
  1241‚Üí  .mobile-a11y input,
  1242‚Üí  .mobile-a11y select {
  1243‚Üí    min-height: 44px;
  1244‚Üí    font-size: 16px;
  1245‚Üí  }
  1246‚Üí  
  1247‚Üí  .mobile-a11y .focus-ring:focus-visible {
  1248‚Üí    outline: 3px solid var(--color-primary-500);
  1249‚Üí    outline-offset: 2px;
  1250‚Üí  }
  1251‚Üí}

