     1‚Üíimport { persistentAtom } from "@nanostores/persistent";
     2‚Üíimport {
     3‚Üí  Query,
     4‚Üí  type Models,
     5‚Üí  ExecutionMethod,
     6‚Üí} from "appwrite";
     7‚Üíimport {
     8‚Üí  databaseId,
     9‚Üí} from "@/constants/appwrite";
    10‚Üíimport { z } from "zod";
    11‚Üíimport type { ULIDFactory } from "ulid-workers";
    12‚Üíimport { useAppwrite } from "@composables/useAppwriteClient";
    13‚Üí
    14‚Üí
    15‚Üíexport class BaseStore<T extends z.ZodType> {
    16‚Üí  protected collectionId: string;
    17‚Üí  protected schema: T;
    18‚Üí  protected databaseId: string | undefined;
    19‚Üí  atom: ReturnType<typeof persistentAtom<z.infer<T> | undefined>>;
    20‚Üí  private ulidInstance: ULIDFactory | null = null;
    21‚Üí
    22‚Üí  constructor(
    23‚Üí    collectionId: string,
    24‚Üí    schema: T,
    25‚Üí    atomKey: string,
    26‚Üí    databaseId?: string
    27‚Üí  ) {
    28‚Üí    this.collectionId = collectionId;
    29‚Üí    this.schema = schema;
    30‚Üí    this.atom = persistentAtom<z.infer<T> | undefined>(atomKey, undefined, {
    31‚Üí      encode: JSON.stringify,
    32‚Üí      decode: (val) => {
    33‚Üí        try {
    34‚Üí          const parsed = JSON.parse(val);
    35‚Üí          return parsed ? this.schema.parse(parsed) : undefined;
    36‚Üí        } catch (e) {
    37‚Üí          return undefined;
    38‚Üí        }
    39‚Üí      },
    40‚Üí    });
    41‚Üí    this.databaseId = databaseId;
    42‚Üí  }
    43‚Üí
    44‚Üí  get client() {
    45‚Üí    const { client } = useAppwrite();
    46‚Üí    return client;
    47‚Üí  }
    48‚Üí
    49‚Üí  get db() {
    50‚Üí    const { databases } = useAppwrite();
    51‚Üí    return databases;
    52‚Üí  }
    53‚Üí
    54‚Üí  get teams() {
    55‚Üí    const { teams } = useAppwrite();
    56‚Üí    return teams;
    57‚Üí  }
    58‚Üí
    59‚Üí  get storage() {
    60‚Üí    const { storage } = useAppwrite();
    61‚Üí    return storage;
    62‚Üí  }
    63‚Üí
    64‚Üí  get functions() {
    65‚Üí    const { functions } = useAppwrite();
    66‚Üí    return functions;
    67‚Üí  }
    68‚Üí
    69‚Üí  getUlidFactory = async (): Promise<ULIDFactory> => {
    70‚Üí    if (!this.ulidInstance) {
