import { BaseStore } from "./baseStore";
import {
  PostContainerSchema,
  type PostContainer,
} from "@appwrite/schemas/postContainer";
import { APPWRITE_COLL_POST_CONTAINER } from "astro:env/client";

class PostContainerStore extends BaseStore<typeof PostContainerSchema> {
  constructor() {
    super(
      APPWRITE_COLL_POST_CONTAINER,
      PostContainerSchema,
      "currentPostContainer",
      "socialaize_data"
    );
  }

  async listContainers(queries: string[] = []) {
    return this.list(queries);
  }

  async listWithCount(queries: string[] = []) {
    return this.listWithTotal(queries);
  }

  async createContainer(data: PostContainer) {
    return this.create(data);
  }

  async updateContainer(id: string, data: Partial<PostContainer>) {
    return this.update(id, data);
  }

  async deleteContainer(id: string) {
    return this.delete(id);
  }
}

const createPostContainerStore = () => {
  const store = new PostContainerStore();
  return {
    store,
    currentPostContainer: store.atom,
    listContainers: store.listContainers.bind(store),
    listWithCount: store.listWithCount.bind(store),
    createContainer: store.createContainer.bind(store),
    updateContainer: store.updateContainer.bind(store),
    deleteContainer: store.deleteContainer.bind(store),
  };
};

export const {
  store: postContainerStore,
  currentPostContainer,
  listContainers: listPostContainers,
  listWithCount: listPostContainersWithCount,
  createContainer: createPostContainer,
  updateContainer: updatePostContainer,
  deleteContainer: deletePostContainer,
} = createPostContainerStore();

