   249‚Üí) => {
   250‚Üí  const account = getAccountSingleton();
   251‚Üí  const databases = getDatabasesSingleton();
   252‚Üí  const ulid = ulidFactory();
   253‚Üí  const id = ulid();
   254‚Üí  try {
   255‚Üí    // Create the user account
   256‚Üí    const user = await account.create(id, email, password, name);
   257‚Üí    
   258‚Üí    // Create member document
   259‚Üí    await databases.createDocument(
   260‚Üí      "socialaize_data",
   261‚Üí      APPWRITE_COLL_MEMBERS,
   262‚Üí      user.$id,
