   266‚Üí    );
   267‚Üí    
   268‚Üí    // Initialize user resources
   269‚Üí    await checkUserInitialization(user.$id);
   270‚Üí    
   271‚Üí    // Automatically log in the user after successful registration
   272‚Üí    try {
   273‚Üí      // Use the proper login flow that sets up server-side sessions
   274‚Üí      const loginResult = await loginUser(email, password);
   275‚Üí      
   276‚Üí      if (loginResult) {
   277‚Üí        return { user, session: loginResult, autoLoggedIn: true };
   278‚Üí      } else {
   279‚Üí        console.warn("Failed to auto-login after registration: No login result");
   280‚Üí        return user;
   281‚Üí      }
   282‚Üí    } catch (loginError) {
   283‚Üí      console.warn("Failed to auto-login after registration:", loginError);
   284‚Üí      // Still return the user even if auto-login failed
   285‚Üí      return user;
   286‚Üí    }
   287‚Üí  } catch (error) {
   288‚Üí    if (error instanceof AppwriteException) {
   289‚Üí      if (
   290‚Üí        error.message.includes(
   291‚Üí          "A user with the same id, email, or phone already exists in this project"
   292‚Üí        )
   293‚Üí      ) {
   294‚Üí        return "A user with this email already exists, please login instead.";
   295‚Üí      }
   296‚Üí    }
   297‚Üí    throw error;
   298‚Üí  }
   299‚Üí};
   300‚Üí
   301‚Üíexport const loginUser = async (
   302‚Üí  email: string,
   303‚Üí  password: string
   304‚Üí): Promise<Models.Session | null> => {
   305‚Üí  const response = await fetch(
   306‚Üí    `${import.meta.env.SITE}/api/auth/connect.json`,
   307‚Üí    {
   308‚Üí      method: "POST",
   309‚Üí      credentials: "include",
   310‚Üí      headers: {
   311‚Üí        "Content-Type": "application/json",
   312‚Üí      },
   313‚Üí      body: JSON.stringify({ email, password }),
   314‚Üí    }
   315‚Üí  );
   316‚Üí  if (!response.ok) {
   317‚Üí    const error = await response.json();
   318‚Üí    console.error("loginUser", error);
   319‚Üí    return null;
   320‚Üí  }
   321‚Üí  const data = await response.json();
   322‚Üí  const sessionData = data?.fallback_cookies;
   323‚Üí
   324‚Üí  // Set the user state immediately if we have it
   325‚Üí  if (data?.user) {
   326‚Üí    curUser.set(data.user);
   327‚Üí  }
   328‚Üí  // CRITICAL FIX: Initialize session from response data
   329‚Üí  if (data?.sessionToken && data?.user) {
   330‚Üí    await initializeSessionAfterLogin(data.user, data.sessionToken);
   331‚Üí  } else if (sessionData && sessionData?.[SESSION_COOKIE_NAME]) {
   332‚Üí    // Fallback to session data if direct token not available
   333‚Üí    const sessionToken = sessionData[SESSION_COOKIE_NAME];
   334‚Üí    await initializeSessionAfterLogin(data.user, sessionToken);
   335‚Üí  }
   336‚Üí  
   337‚Üí  // Single small delay to allow session propagation
   338‚Üí  await new Promise((resolve) => setTimeout(resolve, 200));
   339‚Üí  
   340‚Üí  return data;
   341‚Üí};
   342‚Üí
   343‚Üíexport const checkUserInitialization = async (userId: string) => {
   344‚Üí  const response = await fetch(
   345‚Üí    `${import.meta.env.SITE}/api/auth/initUser.json`,
   346‚Üí    {
   347‚Üí      method: "POST",
   348‚Üí      credentials: "include",
   349‚Üí      headers: {
