   358‚Üíexport const currentUser = async (): Promise<Models.User<Models.Preferences> | null> => {
   359‚Üí  try {
   360‚Üí    // Only block if actively logging out
   361‚Üí    if (isLoggingOut.get()) {
   362‚Üí      return null;
   363‚Üí    }
   364‚Üí
   365‚Üí    // First check if we already have the user in store
   366‚Üí    const existingUser = curUser.get();
   367‚Üí    if (existingUser) {
