import {
  AppwriteException,
  AuthenticationFactor,
  Query,
  type Models,
} from "appwrite";
import { persistentAtom } from "@nanostores/persistent";
import { ulidFactory } from "ulid-workers";
import { DateTime } from "luxon";
import { APPWRITE_COLL_MEMBERS, SESSION_COOKIE_NAME } from "astro:env/client";
import {
  clearCurrentUserMemberships,
  clearSelectedUserTeam,
  ensureTeamStorage,
} from "@/stores/teamStore";
import { clearAllTiktokCreatorInfo } from "@/stores/providers/tiktokStore";
import { 
  useAppwriteClient,
  getAccountSingleton,
  getDatabasesSingleton,
  setFallbackCookies,
  clearFallbackCookies,
  setSession,
  clearSession,
} from "@composables/useAppwriteClient";
import { resetSessionForNewAccount, initializeSessionAfterLogin } from "@/utils/sessionReset";

// This should handle our client side state, just in case we need to use it
export const curUserSession = persistentAtom<string | null>(
  "curUserSession",
  null,
  {
    encode: JSON.stringify,
    decode: (value) => {
      try {
        const parsed = JSON.parse(value);
        return parsed;
      } catch (error) {
        return null;
      }
    },
  }
);

// Track logout state to prevent auto-login during logout process
export const isLoggingOut = persistentAtom<boolean>(
  "isLoggingOut",
  false,
  {
    encode: JSON.stringify,
    decode: (value) => {
      try {
        return JSON.parse(value);
      } catch (error) {
        return false;
      }
    },
  }
);

// Track recent logout to prevent immediate re-login
export const recentLogout = persistentAtom<number>(
  "recentLogout",
  0,
  {
    encode: JSON.stringify,
    decode: (value) => {
      try {
        return JSON.parse(value);
      } catch (error) {
        return 0;
      }
    },
  }
);

export const curUser = persistentAtom<Models.User<Models.Preferences> | null>(
  "curUser",
  null,
  {
    encode: JSON.stringify,
    decode: (value) => {
      try {
        const parsed = JSON.parse(value);
        return parsed;
      } catch (error) {
        return null;
      }
    },
  }
);
interface JwtStore {
  jwt: string;
  expiresAt: number;
}

interface JwtCache {
  isRequesting: boolean;
  lastRequestTime: number;
  CACHE_DURATION: number;
}

export const jwtCache = persistentAtom<JwtCache>(
  "jwtCache",
  {
    isRequesting: false,
    lastRequestTime: 0,
    CACHE_DURATION: 15000, // 15 seconds in milliseconds
  },
  {
    encode: JSON.stringify,
    decode: (value) => {
      try {
        const parsed = JSON.parse(value);
        return {
          ...parsed,
          isRequesting: false, // Always reset isRequesting on decode
        };
      } catch (error) {
        return {
          isRequesting: false,
          lastRequestTime: 0,
          CACHE_DURATION: 15000,
        };
      }
    },
  }
);

const curUserJwt = persistentAtom<JwtStore | null>("curUserJwt", null, {
  encode: JSON.stringify,
  decode: (value: string) => {
    try {
      const parsed = JSON.parse(value);
      return parsed;
    } catch (error) {
      return null;
    }
  },
});
export const setCurUserSession = (session: string) => {
  curUserSession.set(session);
};

export const clearCurUser = () => {
  curUser.set(null);
  curUserSession.set(null);
  curUserJwt.set(null);
  isLoggingOut.set(false);

  // Also clear any cached session state
  clearFallbackCookies();
};

// Check if user recently logged out (within last 5 seconds)
export const wasRecentlyLoggedOut = (): boolean => {
  const lastLogout = recentLogout.get();
  const now = Date.now();
  const fiveSecondsAgo = now - 5000;
  return lastLogout > fiveSecondsAgo;
};

export const getCurUserSession = () => {
  return curUserSession.get();
};

export const setCurrentUser = (user: Models.User<Models.Preferences>) => {
  curUser.set(user);
};

export const initOrGetCurUserSession = (sessionCookie: string) => {
  if (!curUserSession.get()) {
    setCurUserSession(sessionCookie);
  }
  return curUserSession.get();
};

export const resetPassword = async (email: string) => {
  const account = getAccountSingleton();
  try {
    await account.createRecovery(
      email,
      `${import.meta.env.SITE}/reset-password`
    );
  } catch (error) {
    console.error("Error resetting password: ", error);
    return false;
  }
  return true;
};

export const completePasswordReset = async (
  userId: string,
  secret: string,
  newPassword: string
) => {
  const account = getAccountSingleton();
  try {
    await account.updateRecovery(userId, secret, newPassword);
    return true;
  } catch (error) {
    console.error("Error completing password reset: ", error);
    return false;
  }
};

export const authenticateUser = async (
  userId: string,
  tokenSecret: string,
  isLoggedIn: boolean = false
) => {
  setSession(tokenSecret);
  const account = getAccountSingleton();
  if (isLoggedIn) {
    await account.updateVerification(userId, tokenSecret);
  } else {
    await account.createSession(userId, tokenSecret);
    await account.updateVerification(userId, tokenSecret);
  }
  return await account.get();
};

export const getOrCreateUserMember = async (userId: string) => {
  const databases = getDatabasesSingleton();
  try {
    const member = await databases.getDocument(
      "socialaize_data",
      APPWRITE_COLL_MEMBERS,
      userId
    );
    return member;
  } catch (error) {
    const member = await databases.createDocument(
      "socialaize_data",
      APPWRITE_COLL_MEMBERS,
      userId,
      {
        oauthAccounts: [],
      }
    );
    return member;
  }
};

export const createUser = async (
  name: string,
  email: string,
  password: string
) => {
  const account = getAccountSingleton();
  const databases = getDatabasesSingleton();
  const ulid = ulidFactory();
  const id = ulid();
  try {
    // Create the user account
    const user = await account.create(id, email, password, name);

    // Create member document
    await databases.createDocument(
      "socialaize_data",
      APPWRITE_COLL_MEMBERS,
      user.$id,
      {
        oauthAccounts: [],
      }
    );

    // Initialize user resources
    await checkUserInitialization(user.$id);

    // Automatically log in the user after successful registration
    try {
      // Use the proper login flow that sets up server-side sessions
      const loginResult = await loginUser(email, password);

      if (loginResult) {
        return { user, session: loginResult, autoLoggedIn: true };
      } else {
        console.warn("Failed to auto-login after registration: No login result");
        return user;
      }
    } catch (loginError) {
      console.warn("Failed to auto-login after registration:", loginError);
      // Still return the user even if auto-login failed
      return user;
    }
  } catch (error) {
    if (error instanceof AppwriteException) {
      if (
        error.message.includes(
          "A user with the same id, email, or phone already exists in this project"
        )
      ) {
        return "A user with this email already exists, please login instead.";
      }
    }
    throw error;
  }
};

export const loginUser = async (
  email: string,
  password: string
): Promise<Models.Session | null> => {
  const response = await fetch(
    `${import.meta.env.SITE}/api/auth/connect.json`,
    {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, password }),
    }
  );
  if (!response.ok) {
    const error = await response.json();
    console.error("loginUser", error);
    return null;
  }
  const data = await response.json();
  const sessionData = data?.fallback_cookies;

  // Set the user state immediately if we have it
  if (data?.user) {
    curUser.set(data.user);
  }
  // CRITICAL FIX: Initialize session from response data
  if (data?.sessionToken && data?.user) {
    await initializeSessionAfterLogin(data.user, data.sessionToken);
  } else if (sessionData && sessionData?.[SESSION_COOKIE_NAME]) {
    // Fallback to session data if direct token not available
    const sessionToken = sessionData[SESSION_COOKIE_NAME];
    await initializeSessionAfterLogin(data.user, sessionToken);
  }

  // Single small delay to allow session propagation
  await new Promise((resolve) => setTimeout(resolve, 200));

  return data;
};

export const checkUserInitialization = async (userId: string) => {
  const response = await fetch(
    `${import.meta.env.SITE}/api/auth/initUser.json`,
    {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ userId }),
    }
  );
  return response.json();
};

export const currentUser = async (): Promise<Models.User<Models.Preferences> | null> => {
  try {
    // Only block if actively logging out
    if (isLoggingOut.get()) {
      return null;
    }

    // First check if we already have the user in store
    const existingUser = curUser.get();
    if (existingUser) {
      return existingUser;
    }

    // Try the SSR endpoint first
    const userResponse = await fetch(
      `${import.meta.env.SITE}/api/auth/verify.json`,
      {
        method: "GET",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    if (userResponse.ok && userResponse.status === 200) {
      const data = await userResponse.json();

      // CRITICAL FIX: Initialize client-side session if we have a session token
      if (data.sessionToken) {
        setSession(data.sessionToken);
        setFallbackCookies({ [SESSION_COOKIE_NAME]: data.sessionToken });
        setCurUserSession(data.sessionToken);
      }

      // Update user state
      if (data.user) {
        curUser.set(data.user);
      }

      return data.user;
    } else {
      try {
        const account = getAccountSingleton();
        const user = await account.get();

        // If we got user from client but no session token, something is wrong
        // This shouldn't happen but provides a safeguard
        if (user) {
          curUser.set(user);
        }

        return user;
      } catch {
        return null;
      }
    }

    return null;
  } catch (error) {
    return null;
  }
};

export const getUserJwt = async () => {
  const curUserJwtData = curUserJwt.get();
  if (curUserJwtData) {
    if (
      DateTime.fromSeconds(curUserJwtData.expiresAt).toSeconds() >
      DateTime.now().toSeconds()
    ) {
      return {
        jwt: curUserJwtData.jwt,
      } as Models.Jwt;
    }
  }
  const account = getAccountSingleton();
  const jwt = await account.createJWT();
  curUserJwt.set({
    jwt: jwt.jwt,
    expiresAt: DateTime.now().plus({ minutes: 15 }).toSeconds(),
  });
  return jwt;
};

/**
 * Checks if the user has a verification email sent within the last 24 hours, and sends a new one if they do not.
 * @returns {Promise<boolean>} - Returns true if the email was sent, false otherwise.
 */
export const checkAndSendVerificationEmail = async (): Promise<boolean> => {
  const account = getAccountSingleton();
  try {
    const currentAccount = await currentUser();
    if (JSON.stringify(currentAccount) !== JSON.stringify(curUser.get())) {
      curUser.set(currentAccount);
    }
    // First check - if no user or already verified, exit
    if (!currentAccount || currentAccount.emailVerification === true) {
      return false;
    }

    // Get current preferences
    const prefs = await account.getPrefs();
    const lastSent = prefs.verificationSent;
    const now = DateTime.now().toSeconds();

    // Check if email was sent in last 8 hours
    if (lastSent) {
      const nextAllowedSend = DateTime.fromSeconds(lastSent)
        .plus({ hours: 8 })
        .toSeconds();

      if (now < nextAllowedSend) {
        return false;
      }
    }

    // Create verification with absolute URL
    const verificationUrl = new URL(
      "/dashboard/verification/email",
      import.meta.env.SITE
    ).toString();

    await account.createVerification(verificationUrl);

    // Update preferences with atomic operation
    await account.updatePrefs({
      verificationSent: now,
    });

    return true;
  } catch (error) {
    console.error("Error sending verification email: ", error);
    return false;
  }
};

export const verifyUserEmail = async (
  userId: string,
  secret: string
): Promise<boolean> => {
  const account = getAccountSingleton();
  try {
    await account.updateVerification(userId, secret);
    curUser.set({
      ...(curUser.get() as Models.User<Models.Preferences>),
      emailVerification: true,
    });

    return true;
  } catch (error) {
    console.error("Error verifying user email: ", error);
    return false;
  }
};

export const logoutUser = async () => {
  try {
    // Set logout flag and timestamp to prevent auto-login during and after logout process
    isLoggingOut.set(true);
    recentLogout.set(Date.now());

    // Get user ID before clearing state
    const currentUserId = curUser.get()?.$id;

    // STEP 1: Clear ALL client state IMMEDIATELY
    curUser.set(null);
    curUserSession.set(null);
    curUserJwt.set(null);
    clearSession();
    clearSelectedUserTeam();
    clearCurrentUserMemberships();
    clearAllTiktokCreatorInfo();

    // Delete Appwrite sessions
    try {
      const account = getAccountSingleton();
      await account.deleteSessions(); // Delete ALL sessions, not just current
    } catch (appwriteError) {
      console.warn("Failed to delete Appwrite sessions:", appwriteError);
    }

    // Clear server-side session and cookies
    const response = await fetch(
      `${import.meta.env.SITE}/api/auth/connect.json`,
      {
        method: "DELETE",
        credentials: "include",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      }
    );
    if (!response.ok) {
      const error = await response.json();
      console.error("Error with server logout: ", error);
    }

    // Clear initUser cache
    if (currentUserId) {
      try {
        await fetch(`${import.meta.env.SITE}/api/auth/clear-cache.json`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ userId: currentUserId })
        });
      } catch (cacheError) {
        console.warn('Failed to clear initUser cache:', cacheError);
      }
    }

    // Clear logout flag after a short delay
    setTimeout(() => {
      isLoggingOut.set(false);
    }, 2000); // 2 second delay

    // Keep recent logout timestamp briefly to prevent race conditions
    setTimeout(() => {
      recentLogout.set(0);
    }, 5000); // 5 second delay

  } catch (error) {
    console.error("Critical logout error:", error);
    // Emergency cleanup
    curUser.set(null);
    curUserSession.set(null);
    curUserJwt.set(null);
    clearSession();
    setTimeout(() => isLoggingOut.set(false), 3000);
  }
};

