    98‚Üí          defaultPosition: parsed.settings?.defaultPosition || DEFAULT_POSITION,
    99‚Üí          defaultSize: parsed.settings?.defaultSize || DEFAULT_SIZE,
   100‚Üí        }
   101‚Üí      };
   102‚Üí    } catch {
   103‚Üí      return {
   104‚Üí        panelOrder: [],
   105‚Üí        globalZIndex: 1000,
   106‚Üí        settings: {
   107‚Üí          enableAnimations: true,
