   218‚Üí  const newRuntimeState = { ...runtimeState };
   219‚Üí  delete newRuntimeState[id];
   220‚Üí  runtimePanelState.set(newRuntimeState);
   221‚Üí
   222‚Üí  // Update manager state
   223‚Üí  panelManagerState.set({
