   388‚Üí
   389‚Üíexport const centerPanel = (id: string): void => {
   390‚Üí  // Use defaults on SSR or if window not available
   391‚Üí  const viewport = {
   392‚Üí    width: !import.meta.env.SSR && typeof window !== 'undefined' ? window.innerWidth : 1200,
   393‚Üí    height: !import.meta.env.SSR && typeof window !== 'undefined' ? window.innerHeight : 800
   394‚Üí  };
   395‚Üí
   396‚Üí  const configs = panelConfigs.get();
