   471‚Üí        position: stored.position,
   472‚Üí        size: stored.size,
   473‚Üí        isVisible: stored.isVisible ?? false,
   474‚Üí        isMinimized: stored.isMinimized ?? false,
   475‚Üí        zIndex: stored.zIndex ?? getNextZIndex(),
   476‚Üí        snapEdge: stored.snapEdge ?? null,
   477‚Üí        lastUpdated: stored.lastUpdated ?? Date.now()
   478‚Üí      };
   479‚Üí
   480‚Üí      panelConfigs.setKey(id, recovered);
   481‚Üí      return true;
