import { map, computed } from 'nanostores';
import { persistentAtom, persistentMap } from '@nanostores/persistent';
import type { FloatingPanelPosition, FloatingPanelSize } from '@composables/floatingPanelTypes';
import {
  usePersistenceManager,
  persistenceManager,
  currentSession,
  storageStatus,
  recoveryState,
  type PersistentPanelState,
} from '@/stores/persistenceManager';

// Individual panel configuration
export interface FloatingPanelConfig {
  id: string;
  position: FloatingPanelPosition;
  size: FloatingPanelSize;
  isVisible: boolean;
  isMinimized: boolean;
  zIndex: number;
  snapEdge?: 'left' | 'right' | 'top' | 'bottom' | null;
  lastUpdated: number;
  type?: string;
  metadata?: Record<string, any>;
}

// Global panel manager state
export interface PanelManagerState {
  activePanel?: string;
  panelOrder: string[];
  globalZIndex: number;
  settings: {
    enableAnimations: boolean;
    enableSnapping: boolean;
    snapThreshold: number;
    enablePersistence: boolean;
    defaultPosition: FloatingPanelPosition;
    defaultSize: FloatingPanelSize;
  };
}

// Default values
const DEFAULT_POSITION: FloatingPanelPosition = { x: 100, y: 100 };
const DEFAULT_SIZE: FloatingPanelSize = { width: 320, height: 400 };

// Client-side persistence manager instance (initialized on mount)
let persistenceManagerInstance: ReturnType<typeof usePersistenceManager> | null = null;

/**
 * Initialize the persistence manager (client-side only)
 * Call this in a component's onMounted hook
 */
export async function initializePersistence() {
  if (import.meta.env.SSR) return;
  if (!persistenceManagerInstance) {
    persistenceManagerInstance = usePersistenceManager();
  }
  await persistenceManager.init();
  return persistenceManagerInstance;
}

// Create persistent stores
export const panelConfigs = persistentMap<Record<string, FloatingPanelConfig>>('floating-panels:', {}, {
  encode: JSON.stringify,
  decode: (value) => {
    try {
      return JSON.parse(value);
    } catch {
      return {};
    }
  }
});

export const panelManagerState = persistentAtom<PanelManagerState>('panel-manager-state', {
  panelOrder: [],
  globalZIndex: 1000,
  settings: {
    enableAnimations: true,
    enableSnapping: true,
    snapThreshold: 50,
    enablePersistence: true,
    defaultPosition: DEFAULT_POSITION,
    defaultSize: DEFAULT_SIZE,
  }
}, {
  encode: JSON.stringify,
  decode: (value) => {
    try {
      const parsed = JSON.parse(value);
      return {
        panelOrder: parsed.panelOrder || [],
        globalZIndex: parsed.globalZIndex || 1000,
        settings: {
          enableAnimations: parsed.settings?.enableAnimations ?? true,
          enableSnapping: parsed.settings?.enableSnapping ?? true,
          snapThreshold: parsed.settings?.snapThreshold ?? 50,
          enablePersistence: parsed.settings?.enablePersistence ?? true,
          defaultPosition: parsed.settings?.defaultPosition || DEFAULT_POSITION,
          defaultSize: parsed.settings?.defaultSize || DEFAULT_SIZE,
        }
      };
    } catch {
      return {
        panelOrder: [],
        globalZIndex: 1000,
        settings: {
          enableAnimations: true,
          enableSnapping: true,
          snapThreshold: 50,
          enablePersistence: true,
          defaultPosition: DEFAULT_POSITION,
          defaultSize: DEFAULT_SIZE,
        }
      };
    }
  }
});

// Non-persistent reactive state for runtime data
export const runtimePanelState = map<Record<string, {
  isDragging: boolean;
  isResizing: boolean;
  element?: HTMLElement;
}>>({});

// Computed stores
export const visiblePanels = computed(panelConfigs, (configs) =>
  Object.values(configs).filter(panel => panel.isVisible)
);

export const visiblePanelIds = computed(visiblePanels, (panels) =>
  panels.map(panel => panel.id)
);

export const topPanel = computed([panelConfigs, panelManagerState], (configs) => {
  const visible = Object.values(configs).filter(panel => panel.isVisible);
  if (visible.length === 0) return null;

  return visible.reduce((highest, panel) =>
    panel.zIndex > highest.zIndex ? panel : highest
  );
});

// Panel management functions
export const createPanel = (
  id: string,
  options: Partial<Omit<FloatingPanelConfig, 'id' | 'lastUpdated'>> = {}
): FloatingPanelConfig => {
  const state = panelManagerState.get();
  const configs = panelConfigs.get();

  // Check if panel already exists
  if (configs[id]) {
    return configs[id];
  }

  const newPanel: FloatingPanelConfig = {
    id,
    position: options.position || state.settings.defaultPosition,
    size: options.size || state.settings.defaultSize,
    isVisible: options.isVisible ?? false,
    isMinimized: options.isMinimized ?? false,
    zIndex: options.zIndex ?? state.globalZIndex + 1,
    snapEdge: options.snapEdge || null,
    type: options.type,
    metadata: options.metadata,
    lastUpdated: Date.now()
  };

  // Update configs
  panelConfigs.setKey(id, newPanel);

  // Update manager state
  panelManagerState.set({
    ...state,
    panelOrder: [...state.panelOrder, id],
    globalZIndex: newPanel.zIndex
  });

  return newPanel;
};

export const updatePanel = (id: string, updates: Partial<FloatingPanelConfig>): void => {
  const configs = panelConfigs.get();
  const existing = configs[id];

  if (!existing) {
    console.warn(`Panel ${id} does not exist`);
    return;
  }

  const updated: FloatingPanelConfig = {
    ...existing,
    ...updates,
    id, // Ensure id cannot be changed
    lastUpdated: Date.now()
  };

  panelConfigs.setKey(id, updated);
};

export const deletePanel = (id: string): void => {
  const configs = panelConfigs.get();
  const state = panelManagerState.get();

  if (!configs[id]) {
    console.warn(`Panel ${id} does not exist`);
    return;
  }

  // Remove from configs
  const newConfigs = { ...configs };
  delete newConfigs[id];
  panelConfigs.set(newConfigs);

  // Remove from runtime state
  const runtimeState = runtimePanelState.get();
  const newRuntimeState = { ...runtimeState };
  delete newRuntimeState[id];
  runtimePanelState.set(newRuntimeState);

  // Update manager state
  panelManagerState.set({
    ...state,
    activePanel: state.activePanel === id ? undefined : state.activePanel,
    panelOrder: state.panelOrder.filter(panelId => panelId !== id)
  });
};

export const showPanel = (id: string): void => {
  updatePanel(id, {
    isVisible: true,
    zIndex: getNextZIndex()
  });

  setActivePanel(id);
};

export const hidePanel = (id: string): void => {
  updatePanel(id, { isVisible: false });

  const state = panelManagerState.get();
  if (state.activePanel === id) {
    // Find next visible panel to make active
    const visible = visiblePanels.get();
    const nextActive = visible.filter(p => p.id !== id)[0];

    panelManagerState.set({
      ...state,
      activePanel: nextActive?.id
    });
  }
};

export const togglePanel = (id: string): void => {
  const configs = panelConfigs.get();
  const panel = configs[id];

  if (!panel) {
    console.warn(`Panel ${id} does not exist`);
    return;
  }

  if (panel.isVisible) {
    hidePanel(id);
  } else {
    showPanel(id);
  }
};

export const minimizePanel = (id: string): void => {
  updatePanel(id, { isMinimized: true });
};

export const restorePanel = (id: string): void => {
  updatePanel(id, { isMinimized: false });
};

export const bringToFront = (id: string): void => {
  const zIndex = getNextZIndex();
  updatePanel(id, { zIndex });
  setActivePanel(id);
};

export const setActivePanel = (id: string | undefined): void => {
  const state = panelManagerState.get();
  panelManagerState.set({
    ...state,
    activePanel: id
  });
};

export const getNextZIndex = (): number => {
  const state = panelManagerState.get();
  const newZIndex = state.globalZIndex + 1;

  panelManagerState.set({
    ...state,
    globalZIndex: newZIndex
  });

  return newZIndex;
};

export const updatePanelPosition = (id: string, position: FloatingPanelPosition): void => {
  updatePanel(id, { position, snapEdge: null });

  // Save position with viewport adaptation (client-side only)
  if (import.meta.env.SSR) return;

  const configs = panelConfigs.get();
  const panel = configs[id];
  if (panel && storageStatus.get().available && persistenceManagerInstance) {
    persistenceManager.savePanelPosition(id, position, panel.size);
  }
};

export const updatePanelSize = (id: string, size: FloatingPanelSize): void => {
  updatePanel(id, { size });

  // Save size changes with position (client-side only)
  if (import.meta.env.SSR) return;

  const configs = panelConfigs.get();
  const panel = configs[id];
  if (panel && storageStatus.get().available && persistenceManagerInstance) {
    persistenceManager.savePanelPosition(id, panel.position, size);
  }
};

export const snapPanelToEdge = (id: string, edge: 'left' | 'right' | 'top' | 'bottom' | null): void => {
  updatePanel(id, { snapEdge: edge });
};

// Runtime state management
export const setPanelDragging = (id: string, isDragging: boolean): void => {
  const state = runtimePanelState.get();
  runtimePanelState.set({
    ...state,
    [id]: {
      ...state[id],
      isDragging
    }
  });
};

export const setPanelResizing = (id: string, isResizing: boolean): void => {
  const state = runtimePanelState.get();
  runtimePanelState.set({
    ...state,
    [id]: {
      ...state[id],
      isResizing
    }
  });
};

export const setPanelElement = (id: string, element: HTMLElement): void => {
  const state = runtimePanelState.get();
  runtimePanelState.set({
    ...state,
    [id]: {
      ...state[id],
      element
    }
  });
};

// Settings management
export const updateSettings = (settings: Partial<PanelManagerState['settings']>): void => {
  const state = panelManagerState.get();
  panelManagerState.set({
    ...state,
    settings: {
      ...state.settings,
      ...settings
    }
  });
};

export const resetPanelPosition = (id: string): void => {
  const state = panelManagerState.get();
  updatePanel(id, {
    position: state.settings.defaultPosition,
    snapEdge: null
  });
};

export const centerPanel = (id: string): void => {
  // Use defaults on SSR or if window not available
  const viewport = {
    width: !import.meta.env.SSR && typeof window !== 'undefined' ? window.innerWidth : 1200,
    height: !import.meta.env.SSR && typeof window !== 'undefined' ? window.innerHeight : 800
  };

  const configs = panelConfigs.get();
  const panel = configs[id];

  if (!panel) return;

  const position: FloatingPanelPosition = {
    x: (viewport.width - panel.size.width) / 2,
    y: (viewport.height - panel.size.height) / 2
  };

  updatePanel(id, { position, snapEdge: null });
};

// Utility functions
export const getPanelConfig = (id: string): FloatingPanelConfig | undefined => {
  return panelConfigs.get()[id];
};

export const getAllPanelConfigs = (): Record<string, FloatingPanelConfig> => {
  return panelConfigs.get();
};

export const getPanelRuntimeState = (id: string) => {
  return runtimePanelState.get()[id] || {
    isDragging: false,
    isResizing: false
  };
};

export const clearAllPanels = (): void => {
  panelConfigs.set({});
  runtimePanelState.set({});

  const state = panelManagerState.get();
  panelManagerState.set({
    ...state,
    activePanel: undefined,
    panelOrder: []
  });
};

// Panel ordering
export const reorderPanels = (newOrder: string[]): void => {
  const state = panelManagerState.get();
  panelManagerState.set({
    ...state,
    panelOrder: newOrder
  });
};

export const movePanelToIndex = (id: string, index: number): void => {
  const state = panelManagerState.get();
  const currentOrder = [...state.panelOrder];
  const currentIndex = currentOrder.indexOf(id);

  if (currentIndex === -1) return;

  // Remove from current position
  currentOrder.splice(currentIndex, 1);
  // Insert at new position
  currentOrder.splice(index, 0, id);

  reorderPanels(currentOrder);
};

// Panel recovery functions
export const recoverPanelFromPersistence = (id: string): boolean => {
  // Skip on SSR or if persistence not initialized
  if (import.meta.env.SSR || !persistenceManagerInstance || !storageStatus.get().available) return false;

  try {
    const stored = persistenceManager.loadPanelPosition(id);
    if (stored && stored.position && stored.size) {
      const recovered: FloatingPanelConfig = {
        id,
        position: stored.position,
        size: stored.size,
        isVisible: stored.isVisible ?? false,
        isMinimized: stored.isMinimized ?? false,
        zIndex: stored.zIndex ?? getNextZIndex(),
        snapEdge: stored.snapEdge ?? null,
        lastUpdated: stored.lastUpdated ?? Date.now()
      };

      panelConfigs.setKey(id, recovered);
      return true;
    }
  } catch (error) {
    console.error(`Failed to recover panel ${id}:`, error);
  }
  return false;
};

export const recoverAllPanelsFromSession = (): { recovered: number; failed: number } => {
  // Skip on SSR or if persistence not initialized
  if (import.meta.env.SSR || !persistenceManagerInstance || !storageStatus.get().available) {
    return { recovered: 0, failed: 0 };
  }

  const recovery = recoveryState.get();
  let recovered = 0;
  let failed = 0;

  if (recovery.hasRecoverableData) {
    try {
      const sessionData = persistenceManager.recoverSessionData({
        includePosition: true,
        includeWorkflow: false,
        includePreferences: false,
        validateData: true,
      });

      if (sessionData?.panelStates) {
        Object.entries(sessionData.panelStates).forEach(([panelId]) => {
          if (recoverPanelFromPersistence(panelId)) {
            recovered++;
          } else {
            failed++;
          }
        });
      }
    } catch (error) {
      console.error('Failed to recover panels from session:', error);
    }
  }

  return { recovered, failed };
};

export const createPanelRecoveryCheckpoint = (): void => {
  // Skip on SSR or if persistence not initialized
  if (import.meta.env.SSR || !persistenceManagerInstance || !storageStatus.get().available) return;

  try {
    const configs = panelConfigs.get();
    const panelStates: Record<string, PersistentPanelState> = {};

    Object.values(configs).forEach(panel => {
      panelStates[panel.id] = {
        id: panel.id,
        position: panel.position,
        size: panel.size,
        isVisible: panel.isVisible,
        isMinimized: panel.isMinimized,
        zIndex: panel.zIndex,
        snapEdge: panel.snapEdge ?? null,
        lastUpdated: panel.lastUpdated,
        sessionId: currentSession.get().sessionId,
        version: '1.0.0',
      };
    });

    persistenceManager.createRecoveryCheckpoint(panelStates);
  } catch (error) {
    console.error('Failed to create panel recovery checkpoint:', error);
  }
};

// Auto-create recovery checkpoints when panels change (client-side only)
let lastCheckpointTime = 0;
const CHECKPOINT_INTERVAL = 30000; // 30 seconds

if (!import.meta.env.SSR) {
  panelConfigs.subscribe(() => {
    const now = Date.now();
    if (now - lastCheckpointTime > CHECKPOINT_INTERVAL) {
      lastCheckpointTime = now;
      createPanelRecoveryCheckpoint();
    }
  });
}

// Export cleanup function for development/testing
export const __clearPersistentStorage = (): void => {
  // Skip on SSR
  if (import.meta.env.SSR) return;

  if (typeof localStorage !== 'undefined') {
    Object.keys(localStorage).forEach(key => {
      if (key.startsWith('floating-panels:') || key === 'panel-manager-state') {
        localStorage.removeItem(key);
      }
    });
  }

  // Clear new persistence system data if available
  if (persistenceManagerInstance) {
    persistenceManager.clearAllData();
  }

  // Reset to defaults
  panelConfigs.set({});
  panelManagerState.set({
    panelOrder: [],
    globalZIndex: 1000,
    settings: {
      enableAnimations: true,
      enableSnapping: true,
      snapThreshold: 50,
      enablePersistence: true,
      defaultPosition: DEFAULT_POSITION,
      defaultSize: DEFAULT_SIZE,
    }
  });
  runtimePanelState.set({});
};

