import { BaseStore } from "./baseStore";
import {
  Query,
  Permission,
  type Models,
  ExecutionMethod,
  Client,
  Databases,
} from "appwrite";
import {
  type MediaFolders,
  MediaFoldersSchema,
} from "@appwrite/schemas/mediaFolders";
import {
  APPWRITE_COLL_MEDIA_FOLDERS,
  APPWRITE_DB_FILE_METADATA,
  APPWRITE_FILE_METADATA_HANDLER_FUNCTION_ID,
} from "astro:env/client";
import { atom } from "nanostores";
import { FileMetadataSchema } from "@/schemas/fileMetadata";
import type { FileMetadata } from "@/schemas/fileMetadata";
import { Role } from "node-appwrite";
import { DateTime } from "luxon";
import { persistentAtom } from "@nanostores/persistent";

const CHUNK_SIZE = 5 * 1024 * 1024; // 5MB chunks
const MAX_RETRIES = 3;

export interface UploadProgress {
  progress: number;
  sizeUploaded: number; // in bytes
  totalSize: number; // in bytes
  chunksTotal: number;
  chunksUploaded: number;
  completed: boolean;
  error?: string;
}

// fileId -> timestamp -- only one regeneration per 5 minutes
export const lastRegeneratedThumbnail = persistentAtom<Record<string, number>>(
  "lastRegeneratedThumbnail",
  {},
  {
    encode: (val) => JSON.stringify(val),
    decode: (val) => {
      try {
        return JSON.parse(val);
      } catch (error) {
        return {};
      }
    },
  }
);

export const uploadProgress = atom<Record<string, UploadProgress>>({});

class MediaStore extends BaseStore<typeof MediaFoldersSchema> {
  constructor() {
    super(
      APPWRITE_COLL_MEDIA_FOLDERS,
      MediaFoldersSchema,
      "currentMediaFolder",
      "socialaize_data"
    );
  }

  async regenerateThumbnail(
    client: Client,
    bucketId: string,
    fileId: string,
    timestamp?: string
  ) {
    const lastRegenerated = lastRegeneratedThumbnail.get()?.[fileId];
    if (
      lastRegenerated &&
      DateTime.fromSeconds(lastRegenerated).diffNow("seconds").seconds > -300
    ) {
      const lastRegeneratedTime = DateTime.fromSeconds(lastRegenerated);
      const timeToWait = 300 + lastRegeneratedTime.diffNow("seconds").seconds; // Adding because diffNow returns negative value
      const minutesLeft = Math.ceil(timeToWait / 60);
      return {
        success: false,
        message: `Unable to regenerate thumbnail, please wait ${minutesLeft} ${
          minutesLeft === 1 ? "minute" : "minutes"
        }`,
      };
    }
    const functions = this.functions;
    await functions.createExecution(
      APPWRITE_FILE_METADATA_HANDLER_FUNCTION_ID,
      JSON.stringify({
        bucketId: bucketId,
        fileId: fileId,
        ...(timestamp ? { timestamp } : {}),
      }),
      true,
      "/generate-thumbnail",
      ExecutionMethod.POST,
      {
        "Content-Type": "application/json",

