import { ref, computed, provide } from "vue";
import { ACTIVE_TEAM_KEY, TEAM_LOADING_KEY } from "./useProvidedData";
import { useStore } from "@nanostores/vue";
import { type Models } from "appwrite";
import {
  selectedUserTeam,
  currentUserMemberships,
  setSelectedUserTeam,
  clearSelectedUserTeam,
  setCurrentUserMemberships,
  listAllTeams,
  createTeam,
  updateTeamName,
  updateTeamPrefs,
  listTeamMembers,
  createTeamMemberInvite,
  deleteTeamMember,
  deleteTeam,
  acceptTeamInvite,
  cancelTeamInvite,
  updateTeamMember,
  clearCurrentUserMemberships,
} from "@/stores/teamStore";
import { useUserAdmin, useUserTeamAdminOrOwner } from "./useUserAdmin";
import { checkUserInitialization } from "@/stores/authStore";
import { useAuth } from "./useAuth";

export function useTeams(shouldProvide: boolean = false) {
  const activeTeam = useStore(selectedUserTeam);
  const memberships = useStore(currentUserMemberships);
  const isAdmin = useUserAdmin();
  const isTeamAdminOrOwner = useUserTeamAdminOrOwner();
  const { user } = useAuth();

  const isLoading = ref(false);
  const error = ref<string | null>(null);

  const initializeDefaultTeam = async (userId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      // If we already have an active team, we're done
      if (activeTeam.value) return activeTeam.value;

      // Fetch teams
      try {
        const teams = await fetchTeams();
        if (!teams || teams.length === 0) {
          return null;
        }

        // Select the first team
        selectTeam(teams[0]);
        // Also fetch its members
        await fetchTeamMembers(teams[0].$id);

        return teams[0];
      } catch (err) {
        await checkUserInitialization(userId);
      }
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to initialize team";
      console.error("Failed to initialize default team:", err);
      return null;
    } finally {
      isLoading.value = false;
    }
  };

  const isTeamInitialized = computed(() => {
    return !!activeTeam.value && !!memberships.value;
  });

  // Computed
  const myRole = computed(() => {
    if (!activeTeam.value || !memberships.value) return "member";
    const membership = memberships.value.find(
      (m) => m.userId === user.value?.$id
    );
    if (!membership) return "member";
    if (membership.roles.some((role) => role.toLowerCase() === "owner"))
      return "owner";
    if (membership.roles.some((role) => role.toLowerCase() === "admin"))
      return "admin";
    if (membership.roles.some((role) => role.toLowerCase() === "manager"))
      return "manager";
    return "member";
  });

  const canManageTeam = computed(() => {
    return ["owner", "admin"].includes(myRole.value);
  });

  // Methods
  const fetchTeams = async () => {
    isLoading.value = true;
    error.value = null;
    try {
      const result = await listAllTeams();
      // Filter out OAuth teams and ensure unique IDs
      const uniqueTeams = result.teams
        .filter((team) => !team.$id.startsWith("oa_"))
        .reduce((acc: Models.Team<Models.Preferences>[], team) => {
          if (!acc.some((t) => t.$id === team.$id)) {
            acc.push(team);
          }
          return acc;
        }, []);
      return uniqueTeams;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to fetch teams";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const fetchTeamMembers = async (teamId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const result = await listTeamMembers(teamId);
      // Ensure unique member IDs
      const uniqueMembers = result.memberships.reduce(
        (acc: Models.Membership[], member) => {
          if (!acc.some((m) => m.userId === member.userId)) {
            acc.push(member);
          }
          return acc;
        },
        []
      );
      if (teamId === activeTeam.value?.$id) {
        setCurrentUserMemberships(uniqueMembers);
      }
      return uniqueMembers;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to fetch team members";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const selectTeam = (team: Models.Team<Models.Preferences>) => {
    if (!team.$id.startsWith("oa_")) {
      setSelectedUserTeam(team);
    }
  };

  const clearTeam = () => {
    clearSelectedUserTeam();
    clearCurrentUserMemberships();
  };

  const createNewTeam = async (name: string, id?: string, roles?: string[]) => {
    isLoading.value = true;
    error.value = null;
    try {
      const team = await createTeam(name, roles, id);
      return team;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to create team";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const updateTeam = async (teamId: string, name: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      return await updateTeamName(teamId, name);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to update team";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const inviteMember = async (
    teamId: string,
    email: string,
    name: string,
    roles: string[],
    userId?: string
  ) => {
    isLoading.value = true;
    error.value = null;
    try {
      return await createTeamMemberInvite(teamId, roles, email, name, userId);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to invite member";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const removeMember = async (teamId: string, userId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      await deleteTeamMember(teamId, userId);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to remove member";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const updateMember = async (
    teamId: string,
    userId: string,
    roles: string[]
  ) => {
    isLoading.value = true;
    error.value = null;
    try {
      return await updateTeamMember(teamId, userId, roles);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to update member";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const handleInvite = async (
    teamId: string,
    membershipId: string,
    userId: string,
    secret: string
  ) => {
    isLoading.value = true;
    error.value = null;
    try {
      return await acceptTeamInvite(teamId, membershipId, userId, secret);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to accept invite";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const cancelInvite = async (teamId: string, inviteId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      await cancelTeamInvite(teamId, inviteId);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to cancel invite";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const removeTeam = async (teamId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      await deleteTeam(teamId);
      if (activeTeam.value?.$id === teamId) {
        clearSelectedUserTeam();
      }
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to delete team";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  // Optionally provide the data for injection
  if (shouldProvide) {
    provide(ACTIVE_TEAM_KEY, activeTeam);
    provide(TEAM_LOADING_KEY, isLoading);
  }

  return {
    // State
    activeTeam,
    memberships,
    isLoading,
    error,
    isAdmin,
    isTeamAdminOrOwner,
    myRole,
    canManageTeam,
    isTeamInitialized,

    // Methods
    initializeDefaultTeam,
    fetchTeams,
    selectTeam,
    clearTeam,
    createNewTeam,
    updateTeam,
    fetchTeamMembers,
    inviteMember,
    removeMember,
    updateMember,
    handleInvite,
    cancelInvite,
    removeTeam,
  };
}

