import { useStore } from "@nanostores/vue";
import { computed, provide } from "vue";
import { OAUTH_ACCOUNTS_KEY, OAUTH_LOADING_KEY } from "./useProvidedData";
import {
  currentOAuthAccount,
  listForTeam,
  getOAuthRedirectUrl,
  listOAuthAccounts,
  getAccount,
  updateAccount,
  unlinkAccount,
} from "@/stores/oauthStore";
import { selectedUserTeam } from "@/stores/teamStore";
import type { TeamOAuthAccount } from "@/stores/oauthStore";
import type { OAuthAccount } from "@appwrite/schemas/oAuthAccount";
import type { PlatformAccount } from "@/utils/providerUtils";
import { persistentMap } from "@nanostores/persistent";
import { atom } from "nanostores";
import { getRelatedAccountsData, countActiveAccounts } from "@/utils/providerUtils";

// Create persistent store for OAuth accounts
const oauthAccountsCache = persistentMap<Record<string, TeamOAuthAccount[]>>(
  "oauth-accounts-cache:",
  {},
  {
    encode: JSON.stringify,
    decode: JSON.parse,
  }
);

// Create persistent store for related platform accounts
const platformAccountsCache = persistentMap<
  Record<string, Record<string, PlatformAccount>>
>(
  "platform-accounts-cache:",
  {},
  {
    encode: JSON.stringify,
    decode: JSON.parse,
  }
);

// Create atom for loading state
const loadingState = atom<boolean>(false);

// Create atom for last fetch timestamps
const lastFetchTimes = persistentMap<Record<string, number>>(
  "oauth-fetch-times:",
  {},
  {
    encode: JSON.stringify,
    decode: JSON.parse,
  }
);

export const clearOAuthCache = () => {
  oauthAccountsCache.set({});
  platformAccountsCache.set({});
  lastFetchTimes.set({});
};

export interface EnrichedOAuthAccount extends TeamOAuthAccount {
  platformAccount?: PlatformAccount;
}

export function useOAuth(shouldProvide: boolean = false) {
  const currentAccount = useStore(currentOAuthAccount);
  const currentTeam = useStore(selectedUserTeam);
  const loading = useStore(loadingState);
  const cachedAccounts = useStore(oauthAccountsCache);
  const cachedPlatformAccounts = useStore(platformAccountsCache);
  const fetchTimes = useStore(lastFetchTimes);

  const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

  const isCacheValid = (teamId: string) => {
    const lastFetch = fetchTimes.value[teamId];
    return lastFetch && Date.now() - lastFetch < CACHE_DURATION;
  };

  const listAccountsForTeam = async (
    teamId?: string,
    queries?: string[],
    forceRefresh = false
  ): Promise<TeamOAuthAccount[]> => {
    const targetTeamId = teamId || currentTeam.value?.$id;
    if (!targetTeamId) return [];

    // Return cached data if valid and not forcing refresh
    if (
      !forceRefresh &&
      isCacheValid(targetTeamId) &&
      cachedAccounts.value[targetTeamId] &&
      Array.isArray(cachedAccounts.value[targetTeamId]) // Validate cache structure
    ) {
      return cachedAccounts.value[targetTeamId] as TeamOAuthAccount[];
    }

    loadingState.set(true);
    try {
      const result = await listForTeam(targetTeamId, queries);

      // Fetch related platform accounts
      const enrichedAccounts = await getRelatedAccountsData(result.documents);

      // Update OAuth accounts cache
      oauthAccountsCache.setKey(targetTeamId, result.documents);

      // Update platform accounts cache
      const platformAccounts: Record<string, PlatformAccount> = {};
      enrichedAccounts.forEach((account) => {
        if (account.relatedAccount) {
          platformAccounts[account.$id!] = account.relatedAccount;
        }
      });
      platformAccountsCache.setKey(targetTeamId, platformAccounts);

      // Update timestamp
      lastFetchTimes.setKey(targetTeamId, Date.now());

      return enrichedAccounts;
    } catch (err) {
      console.error("Failed to load OAuth accounts:", err);
      // Clear invalid cache
      oauthAccountsCache.setKey(targetTeamId, []);
      platformAccountsCache.setKey(targetTeamId, {});
      lastFetchTimes.setKey(targetTeamId, 0);
      throw err;
    } finally {
      loadingState.set(false);
    }
  };

  const refreshTeamAccounts = async (teamId?: string) => {
    return listAccountsForTeam(teamId, undefined, true);
  };

  const getCachedTeamAccounts = computed(() => {
    const teamId = currentTeam.value?.$id;
    if (!teamId) return [];

    const accounts = cachedAccounts.value[teamId] || [];
    const platformAccounts = cachedPlatformAccounts.value[teamId] || {};

    if (!Array.isArray(accounts)) {
      console.error("Invalid cache structure for OAuth accounts");
      return [];
    }

    return accounts.map((account) => ({
      ...account,
      platformAccount: platformAccounts[account.$id!],
    }));
  });

  const getRedirectUrl = async (provider: string, teamId: string, redirectPath?: string) => {
    return getOAuthRedirectUrl(provider, teamId, redirectPath);
  };

  const getOAuthAccount = async (accountId: string) => {
    return getAccount(accountId);
  };

  const updateOAuthAccount = async (
    accountId: string,
    data: Partial<OAuthAccount>
  ) => {
    const result = await updateAccount(accountId, data);

    // Refresh caches for all teams that have this account
    const teamsToRefresh = Object.keys(cachedAccounts.value);
    await Promise.all(
      teamsToRefresh.map((teamId) => refreshTeamAccounts(teamId))
    );

    return result;
  };

  const unlink = async (accountId: string) => {
    const result = await unlinkAccount(accountId);

    // Refresh caches for all teams that had this account
    const teamsToRefresh = Object.keys(cachedAccounts.value);
    await Promise.all(
      teamsToRefresh.map((teamId) => refreshTeamAccounts(teamId))
    );

    return result;
  };

  const listAccounts = async (queries?: string[]) => {
    return listOAuthAccounts(queries);
  };

  const clearCache = (teamId?: string) => {
    if (teamId) {
      oauthAccountsCache.setKey(teamId, []);
      platformAccountsCache.setKey(teamId, {});
      lastFetchTimes.setKey(teamId, 0);
    } else {
      oauthAccountsCache.set({});
      platformAccountsCache.set({});
      lastFetchTimes.set({});
    }
  };

  const getActiveAccountCount = (teamId?: string) => {
    const targetTeamId = teamId || currentTeam.value?.$id;
    if (!targetTeamId) return 0;

    const accounts = cachedAccounts.value[targetTeamId] || [];
    return countActiveAccounts(accounts.map(account => ({ ...account })) as OAuthAccount[], targetTeamId);
  };

  // Optionally provide the data for injection
  if (shouldProvide) {
    provide(OAUTH_ACCOUNTS_KEY, getCachedTeamAccounts);
    provide(OAUTH_LOADING_KEY, loading);
  }

  return {
    currentAccount,
    listAccountsForTeam,
    getRedirectUrl,
    getOAuthAccount,
    updateOAuthAccount,
    unlink,
    listAccounts,
    loading,
    getCachedTeamAccounts,
    refreshTeamAccounts,
    clearCache,
    getActiveAccountCount,
    countActiveAccounts: (accounts: OAuthAccount[], teamId?: string) => countActiveAccounts(accounts, teamId),
  };
}

