import { ref, computed } from "vue";
import { useStore } from "@nanostores/vue";
import { atom } from "nanostores";
import type { Models } from "appwrite";
import { Query } from "appwrite";
import type { MediaFolders } from "@appwrite/schemas/mediaFolders";
import {
  listFolders,
  listFiles,
  getFolder,
  createFolder,
  deleteFile,
  updateMediaFileName,
  uploadFiles,
  uploadFile,
  getFileMetadata,
  getFilesMetadata,
  getFileThumbnailOrPreview,
  getFilePreview,
  ensureRootMediaFolder,
  updateFolderName,
  uploadFileFast,
  type UploadProgress,
  regenerateThumbnail as regenThumbnail,
} from "@/stores/mediaStore";
import { persistentAtom } from "@nanostores/persistent";
import { ensureTeamStorage } from "@/stores/teamStore";
import { useAppwriteClient } from "./useAppwriteClient";

// Cache interfaces
interface FileCache {
  file: Models.File;
  metadata?: any;
  thumbnailUrl?: string;
  lastUpdated: number;
}

interface MediaState {
  currentFolderId: string | null;
  currentFolder: MediaFolders | null;
  folderPath: MediaFolders[];
  items: (Models.File | MediaFolders)[];
  sortBy: string;
  sortOrder: "asc" | "desc";
  search: string;
  selectedItems: Models.File[];
  fileCache: Record<string, FileCache>;
  folderCache: Record<string, MediaFolders>;
}

const teamBucketValidCache = atom<Record<string, boolean>>({});

const CACHE_DURATION = 1000 * 60 * 5; // 5 minutes
const THUMBNAIL_CACHE_DURATION = 1000 * 60 * 30; // 30 minutes

const defaultState: MediaState = {
  currentFolderId: null,
  currentFolder: null,
  folderPath: [],
  items: [],
  sortBy: "$createdAt",
  sortOrder: "desc",
  search: "",
  selectedItems: [],
  fileCache: {},
  folderCache: {},
};

const teamLastRefreshTimes = persistentAtom<Record<string, number>>(
  "media-fetch-times:",
  {},
  {
    encode: JSON.stringify,
    decode: (value: string) => {
      try {
        return JSON.parse(value);
      } catch (err) {
        console.error("Failed to decode media fetch times:", err);
        return {};
      }
    },
  }
);

const mediaStore = persistentAtom<MediaState>(
  "mediaStore",
  { ...defaultState },
  {
    encode: JSON.stringify,
    decode: (value: string) => {
      try {
        return JSON.parse(value);
      } catch (err) {
        console.error("Failed to decode mediaStore:", err);
        return defaultState;
      }
    },
  }
);

export const clearMediaCache = () => {
  // Clear media store cache
  mediaStore.set({ ...defaultState });

  // Clear team bucket cache
  teamBucketValidCache.set({});

  // Clear last refresh times
  teamLastRefreshTimes.set({});
  useMediaStore().clearCache();
};

export function useMediaStore(itemsPerPage = 32) {
  const isLoading = ref(false);
  const error = ref<string | null>(null);
  const hasMoreItems = ref(false);
  const currentPage = ref(1);
  const store = useStore(mediaStore);

  // Cache management
  const isCacheValid = (timestamp: number, duration = CACHE_DURATION) => {
    return Date.now() - timestamp < duration;
  };

  const getCachedFile = (fileId: string): FileCache | null => {
    const cached = store.value.fileCache[fileId];
    if (cached && isCacheValid(cached.lastUpdated)) {
      return cached as FileCache;
    }
    return null;
  };

  const getCachedFolder = (folderId: string): MediaFolders | null => {
    return store.value.folderCache[folderId] as MediaFolders | null;
  };

  const updateStore = (updates: Partial<MediaState>) => {
    mediaStore.set({ ...(store.value as MediaState), ...updates });
  };

  const regenerateThumbnail = async (
    bucketId: string,
    fileId: string,
    timestamp?: string
  ) => {
    try {
      const { client } = useAppwriteClient();
      return await regenThumbnail(client, bucketId, fileId, timestamp);
    } catch (err) {
      console.error(`Failed to replace thumbnail for file ${fileId}:`, err);
      return { success: false, message: "Failed to regenerate thumbnail" };
    }
  };

  const cacheFile = async (teamId: string, file: Models.File) => {
    const cached = getCachedFile(file.$id);
    if (cached) return cached;

    try {
      const [metadata, thumbnailUrl] = await Promise.all([
        getFileMetadata(teamId, file.$id),
        getFileThumbnailOrPreview(teamId, file.$id),
      ]);

      const fileCache: FileCache = {
        file,
        metadata,
        thumbnailUrl,
        lastUpdated: Date.now(),
      };

      updateStore({
        fileCache: {
          ...(store.value.fileCache as Record<string, FileCache>),
          [file.$id]: fileCache,
        },
      });

      return fileCache;
    } catch (err) {
      console.error(`Failed to cache file ${file.$id}:`, err);
      return null;
    }
  };

  const cacheFolder = (folder: MediaFolders) => {
    updateStore({
      folderCache: {
        ...(store.value.folderCache as Record<string, MediaFolders>),
        [folder.$id!]: folder,
      },
    });
  };

  const setSelectedItems = (items: Models.File[]) => {
    updateStore({ selectedItems: items });
  };

  const setFolderPath = (path: MediaFolders[]) => {
    updateStore({ folderPath: path });
    path.forEach(cacheFolder);
  };

  const setCurrentFolder = (folder: MediaFolders | null) => {
    updateStore({ currentFolder: folder });
    if (folder) cacheFolder(folder);
  };

  const fetchItems = async (append = false, teamId: string) => {
    if (!teamId) return;

    isLoading.value = true;
    error.value = null;

    try {
      const queries = {
        base: [
          Query.limit(itemsPerPage),
          Query.offset((currentPage.value - 1) * itemsPerPage),
        ],
        sort: [
          store.value.sortOrder === "asc"
            ? Query.orderAsc(store.value.sortBy)
            : Query.orderDesc(store.value.sortBy),
        ],
        search: store.value.search
          ? [Query.contains("name", store.value.search)]
          : [],
        folder: [
          store.value.currentFolderId
            ? Query.equal("parentId", store.value.currentFolderId)
            : Query.isNull("parentId"),
        ],
      };

      const [foldersResponse, filesResponse] = await Promise.all([
        listFolders(teamId, [
          ...queries.base,
          ...queries.sort,
          ...queries.search,
          ...queries.folder,
        ]),
        listFiles(teamId, store.value.currentFolderId, [
          ...queries.base,
          ...queries.sort,
          ...queries.search,
        ]),
      ]);

      // Cache new items
      await Promise.all([
        ...foldersResponse.map((folder) => cacheFolder(folder)),
        ...filesResponse.files.map((file) => cacheFile(teamId, file)),
      ]);

      const newItems = [...foldersResponse, ...filesResponse.files];

      updateStore({
        items: append
          ? [
              ...(store.value.items as (Models.File | MediaFolders)[]),
              ...newItems,
            ]
          : newItems,
      });

      hasMoreItems.value =
        foldersResponse.length + filesResponse.files.length >
        currentPage.value * itemsPerPage;
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to fetch items";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const navigateToFolder = async (folderId: string | null, teamId: string) => {
    currentPage.value = 1;

    try {
      let folder: MediaFolders;

      if (folderId === null) {
        const root = await ensureRootMediaFolder(teamId);
        if (!root) {
          throw new Error("Failed to ensure root media folder");
        }
        folder = root;
        folderId = root.$id || null;
      } else {
        const cachedFolder = getCachedFolder(folderId);
        const finalFolder = cachedFolder || (await getFolder(teamId, folderId));
        if (!finalFolder) {
          throw new Error("Failed to get folder");
        }
        folder = finalFolder;
        if (!cachedFolder) {
          cacheFolder(folder);
        }
      }

      updateStore({
        currentFolderId: folderId,
        currentFolder: folder,
        items: [],
      });

      await fetchItems(false, teamId);
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to navigate";
      throw err;
    }
  };

  const loadMoreItems = async (teamId: string) => {
    if (!hasMoreItems.value) return;
    currentPage.value++;
    await fetchItems(true, teamId);
  };

  const isFetchCacheValid = (teamId: string, duration = CACHE_DURATION) => {
    const lastFetch = teamLastRefreshTimes.get()[teamId];
    return lastFetch && Date.now() - lastFetch < duration;
  };

  const updateLastFetchTime = (teamId: string) => {
    teamLastRefreshTimes.set({
      ...teamLastRefreshTimes.get(),
      [teamId]: Date.now(),
    });
  };

  const refreshMediaStore = async (
    teamId: string,
    forceRootCheck = false,
    userId?: string
  ) => {
    if (!teamId) return;

    isLoading.value = true;
    error.value = null;

    try {
      // Always clear the team bucket cache to ensure fresh bucket status
      clearTeamBucketCache(teamId);

      // If forceRootCheck is true, or we don't have a current folder, do a full initialization
      if (forceRootCheck || !store.value.currentFolder) {
        await checkForTeamBucket(teamId, userId);
        const root = await ensureRootMediaFolder(teamId, userId);
        if (!root) {
          throw new Error("Failed to ensure root media folder");
        }
        updateStore({
          currentFolderId: root.$id,
          currentFolder: root,
        });
        cacheFolder(root);
      }

      // Reset to the first page
      currentPage.value = 1;

      // Fetch fresh items
      await fetchItems(false, teamId);

      // Update last fetch time
      updateLastFetchTime(teamId);
    } catch (err) {
      error.value =
        err instanceof Error ? err.message : "Failed to refresh media store";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const initialize = async (teamId: string, userId?: string) => {
    if (!teamId) return;

    // Check if we have a valid cache and current folder already
    if (
      isFetchCacheValid(teamId) &&
      store.value.currentFolder &&
      store.value.currentFolderId
    ) {
      // Just refresh items without re-initializing folders
      await refreshMediaStore(teamId, false, userId);
      return;
    }

    // Otherwise do a full initialization
    isLoading.value = true;
    try {
      await checkForTeamBucket(teamId, userId);
      const root = await ensureRootMediaFolder(teamId, userId);
      if (!root) {
        throw new Error("Failed to ensure root media folder");
      }
      updateStore({
        currentFolderId: root.$id,
        currentFolder: root,
      });
      cacheFolder(root);
      await fetchItems(false, teamId);

      // Update last fetch time
      updateLastFetchTime(teamId);
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to initialize";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const checkForTeamBucket = async (teamId: string, userId?: string) => {
    const cached = teamBucketValidCache.get()[teamId];
    if (cached) return cached;

    const bucketResponse = await ensureTeamStorage(teamId, userId);
    if (!bucketResponse) {
      throw new Error("Failed to ensure team storage");
    }
    teamBucketValidCache.set({
      ...teamBucketValidCache.get(),
      [teamId]: true,
    });
  };

  const clearTeamBucketCache = (teamId?: string, userId?: string) => {
    if (teamId) {
      teamBucketValidCache.set({
        ...teamBucketValidCache.get(),
        [teamId]: false,
      });
    } else {
      teamBucketValidCache.set({});
    }
  };

  const getFileData = async (teamId: string, fileId: string) => {
    const cached = getCachedFile(fileId);
    if (cached) return cached;

    const file = store.value.items.find(
      (item): item is Models.File =>
        !("parentId" in item) && item.$id === fileId
    );
    if (!file) return null;

    return await cacheFile(teamId, file);
  };

  const clearCache = (fileId?: string) => {
    if (fileId) {
      const { [fileId]: _, ...restFileCache } = store.value.fileCache as Record<
        string,
        FileCache
      >;
      updateStore({ fileCache: restFileCache });
    } else {
      updateStore({ fileCache: {}, folderCache: {} });
    }
    clearTeamBucketCache();
  };

  const isRootFolder = computed(() => {
    if (!store.value.currentFolder) return true;
    return (
      store.value.currentFolder.path === "/" &&
      store.value.currentFolder.name.toLowerCase() === "home"
    );
  });

  return {
    store,
    isLoading,
    error,
    hasMoreItems,
    isRootFolder,
    fetchItems,
    navigateToFolder,
    loadMoreItems,
    initialize,
    updateFolderName,
    setFolderPath,
    setSelectedItems,
    setCurrentFolder,
    deleteFile,
    updateMediaFileName,
    getFileData,
    clearCache,
    getCachedFile,
    getCachedFolder,
    updateStore,
    uploadFile,
    uploadFiles,
    createFolder,
    getFileThumbnailOrPreview,
    listFiles,
    listFolders,
    getFileMetadata,
    getFilesMetadata,
    getFilePreview,
    getFolder,
    refreshMediaStore,
    regenerateThumbnail,
  };
}

