import { Account, Avatars, Client, Databases, Functions, Storage, Teams, type Models } from "appwrite";
import { APPWRITE_ENDPOINT, APPWRITE_PROJECT_ID, SESSION_COOKIE_NAME } from "astro:env/client";
import { ref, computed } from "vue";
import { createGlobalState, useLocalStorage } from "@vueuse/core";
// IMPORTANT: Do not import authStore here to avoid circular deps during hydration.
// We'll lazy-import when we need to update those stores.

// New global-state composable using @vueuse/core
export const useAppwriteClient = createGlobalState(() => {
  // Instances (kept inside the factory rather than module-level constants)
  let client: Client | null = null;
  let account: Account | null = null;
  let databases: Databases | null = null;
  let functions: Functions | null = null;
  let storage: Storage | null = null;
  let teams: Teams | null = null;
  let avatars: Avatars | null = null;

  // Reactive state
  const isInitialized = ref(false);
  const isAuthenticated = ref(false);
  const sessionToken = ref<string | undefined>(undefined);

  // Persisted cookie fallback (used throughout the app)
  const fallbackCookies = useLocalStorage<Record<string, string>>("fallbackCookies", {});

  // Helper function to validate a token
  const isValidToken = (token: string | undefined | null): boolean => {
    return !!token && token !== "undefined" && token !== "null" && token.trim() !== "";
  };

  const initClient = () => {
    if (!client) {
      client = new Client().setEndpoint(APPWRITE_ENDPOINT).setProject(APPWRITE_PROJECT_ID);
      account = new Account(client);
      databases = new Databases(client);
      functions = new Functions(client);
      storage = new Storage(client);
      teams = new Teams(client);
      avatars = new Avatars(client);

      // Initialize session from persisted cookies if available
      let token = fallbackCookies.value?.[SESSION_COOKIE_NAME];

      // Also check Appwrite SDK's cookieFallback as fallback
      if (!token && typeof localStorage !== 'undefined') {
        try {
          const legacyFallback = localStorage.getItem("cookieFallback");
          if (legacyFallback) {
            const parsed = JSON.parse(legacyFallback);
            token = parsed[`a_session_${APPWRITE_PROJECT_ID}`];
          }
        } catch (e) {
          // Ignore parse errors
        }
      }

      if (isValidToken(token)) {
        try {
          client.setSession(token!);
          isAuthenticated.value = true;
          sessionToken.value = token!;
        } catch {
          // ignore
        }
      }
      isInitialized.value = true;
    }
    return client;
  };

  const refreshClientSession = () => {
    if (!client) return;
    const token = fallbackCookies.value?.[SESSION_COOKIE_NAME];
    if (isValidToken(token)) {
      try {
        client.setSession(token!);
        isAuthenticated.value = true;
        sessionToken.value = token!;
      } catch {
        // ignore
      }
    }
  };

  const setFallbackCookies = (cookies: Record<string, string>) => {
    fallbackCookies.value = cookies;
    refreshClientSession();
  };

  const clearFallbackCookies = () => {
    fallbackCookies.value = {};
    isAuthenticated.value = false;
    if (client) {
      try {
        client.setSession("");
      } catch {
        // ignore
      }
    }

