     1‚Üíimport { ref, reactive, computed, watch, onMounted, onUnmounted, readonly, type Ref } from 'vue';
     2‚Üíimport { useAppwriteClient } from './useAppwriteClient';
     3‚Üíimport { useAuth } from './useAuth';
     4‚Üíimport { useRealtime, type RealtimeEvent, type RealtimeEventType } from './useRealtime';
     5‚Üíimport { ExecutionMethod } from 'appwrite';
     6‚Üí
     7‚Üí// TypeScript interfaces
     8‚Üíexport type NotificationType = 'success' | 'error' | 'warning' | 'info' | 'default';
     9‚Üíexport type NotificationPriority = 'low' | 'medium' | 'high' | 'urgent';
    10‚Üí
    11‚Üíexport interface NotificationAction {
    12‚Üí  id: string;
    13‚Üí  label: string;
    14‚Üí  icon?: string;
    15‚Üí  variant?: 'primary' | 'secondary' | 'danger' | 'ghost';
    16‚Üí  loading?: boolean;
    17‚Üí  handler: (notificationId: string) => Promise<void> | void;
    18‚Üí}
    19‚Üí
    20‚Üíexport interface NotificationMessage {
    21‚Üí  text?: string;
    22‚Üí  html?: string;
    23‚Üí}
    24‚Üí
    25‚Üíexport interface NotificationDetails {
    26‚Üí  source?: string;
    27‚Üí  metadata?: Record<string, any>;
    28‚Üí  expandable?: boolean;
    29‚Üí}
    30‚Üí
    31‚Üíexport interface SmartNotification {
    32‚Üí  id: string;
    33‚Üí  type: NotificationType;
    34‚Üí  priority: NotificationPriority;
    35‚Üí  title: string;
    36‚Üí  message: string | NotificationMessage;
    37‚Üí  icon: string;
    38‚Üí  timestamp: Date;
    39‚Üí  autoHide?: boolean;
    40‚Üí  persistent?: boolean;
    41‚Üí  progress?: number;
    42‚Üí  actions?: NotificationAction[];
    43‚Üí  details?: NotificationDetails;
    44‚Üí  expanded?: boolean;
    45‚Üí  isPaused?: boolean;
    46‚Üí  aiPriority?: boolean;
    47‚Üí  aiScore?: number;
    48‚Üí  showAiProcessing?: boolean;
    49‚Üí  groupId?: string;
    50‚Üí  metadata?: Record<string, any>;
    51‚Üí}
    52‚Üí
    53‚Üíexport interface NotificationGroup {
    54‚Üí  id: string;
    55‚Üí  title: string;
    56‚Üí  count: number;
    57‚Üí  priority: NotificationPriority;
    58‚Üí  lastNotification: SmartNotification;
    59‚Üí  collapsed?: boolean;
    60‚Üí}
    61‚Üí
    62‚Üí// AI prioritization system
    63‚Üíinterface AiPrioritizationConfig {
    64‚Üí  enabled: boolean;
    65‚Üí  scoreThreshold: number;
    66‚Üí  priorityBoosts: Record<string, number>;
    67‚Üí  contextWeights: Record<string, number>;
    68‚Üí}
    69‚Üí
    70‚Üí// Stack prevention system  
    71‚Üíinterface StackPreventionConfig {
    72‚Üí  enabled: boolean;
    73‚Üí  similarityThreshold: number;
    74‚Üí  maxSimilarNotifications: number;
    75‚Üí  groupingTimeWindow: number; // milliseconds
    76‚Üí}
    77‚Üí
    78‚Üíexport interface NotificationConfig {
    79‚Üí  maxVisible?: number;
    80‚Üí  defaultAutoHideDelay?: number;
    81‚Üí  enableAiPrioritization?: boolean;
    82‚Üí  enableStackingPrevention?: boolean;
    83‚Üí  enableSmartGrouping?: boolean;
    84‚Üí  enableRealTimeSync?: boolean;
    85‚Üí}
    86‚Üí
    87‚Üíexport const useNotifications = (config: NotificationConfig = {}) => {
    88‚Üí  // Configuration with defaults
    89‚Üí  const settings = reactive({
    90‚Üí    maxVisible: config.maxVisible || 5,
    91‚Üí    defaultAutoHideDelay: config.defaultAutoHideDelay || 5000,
    92‚Üí    enableAiPrioritization: config.enableAiPrioritization ?? true,
    93‚Üí    enableStackingPrevention: config.enableStackingPrevention ?? true,
    94‚Üí    enableSmartGrouping: config.enableSmartGrouping ?? true,
    95‚Üí    enableRealTimeSync: config.enableRealTimeSync ?? true
    96‚Üí  });
    97‚Üí
    98‚Üí  // State
    99‚Üí  const notifications: Ref<SmartNotification[]> = ref([]);
   100‚Üí  const groups: Ref<NotificationGroup[]> = ref([]);
   101‚Üí  const queuedNotifications: Ref<SmartNotification[]> = ref([]);
   102‚Üí  const dismissedIds = new Set<string>();
   103‚Üí  const autoHideTimers = new Map<string, NodeJS.Timeout>();
   104‚Üí  const pausedTimers = new Map<string, Date>();
   105‚Üí
   106‚Üí  // Composables
   107‚Üí  const { functions } = useAppwriteClient();
   108‚Üí  const { user } = useAuth();
   109‚Üí  const { subscribe: subscribeRealtime, isConnected } = useRealtime();
   110‚Üí
   111‚Üí  // AI Prioritization System
   112‚Üí  const aiConfig = reactive<AiPrioritizationConfig>({
   113‚Üí    enabled: settings.enableAiPrioritization,
   114‚Üí    scoreThreshold: 0.7,
   115‚Üí    priorityBoosts: {
   116‚Üí      'billing.credit.exhausted': 1.0,
   117‚Üí      'platform.error': 0.8,
   118‚Üí      'post.failed': 0.7,
   119‚Üí      'oauth.expired': 0.6,
   120‚Üí      'workflow.failed': 0.5
   121‚Üí    },
   122‚Üí    contextWeights: {
   123‚Üí      time_sensitivity: 0.3,
   124‚Üí      user_importance: 0.25,
   125‚Üí      business_impact: 0.25,
   126‚Üí      frequency: 0.2
   127‚Üí    }
   128‚Üí  });
   129‚Üí
   130‚Üí  // Stack Prevention System
   131‚Üí  const stackConfig = reactive<StackPreventionConfig>({
   132‚Üí    enabled: settings.enableStackingPrevention,
   133‚Üí    similarityThreshold: 0.8,
   134‚Üí    maxSimilarNotifications: 2,
   135‚Üí    groupingTimeWindow: 60000 // 1 minute
   136‚Üí  });
   137‚Üí
   138‚Üí  // AI-powered priority scoring
   139‚Üí  const calculateAiPriority = async (notification: Partial<SmartNotification>): Promise<number> => {
   140‚Üí    if (!aiConfig.enabled) return 0.5;
   141‚Üí
   142‚Üí    try {
   143‚Üí      const context = {
   144‚Üí        type: notification.type,
   145‚Üí        title: notification.title,
   146‚Üí        message: typeof notification.message === 'string' ? notification.message : notification.message?.text,
   147‚Üí        timestamp: notification.timestamp?.toISOString(),
   148‚Üí        metadata: notification.metadata,
   149‚Üí        user_context: {
   150‚Üí          id: user.value?.$id,
   151‚Üí          team_id: user.value?.prefs?.selectedTeamId,
   152‚Üí          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
   153‚Üí          current_time: new Date().toISOString()
   154‚Üí        }
   155‚Üí      };
   156‚Üí
   157‚Üí      const response = await functions.createExecution(
   158‚Üí        'AI_API',
   159‚Üí        JSON.stringify({
   160‚Üí          action: 'prioritize_notification',
   161‚Üí          context,
   162‚Üí          config: aiConfig
   163‚Üí        }),
   164‚Üí        false,
   165‚Üí        '/action',
   166‚Üí        ExecutionMethod.POST,
   167‚Üí      );
   168‚Üí
   169‚Üí      const result = JSON.parse(response.responseBody);
   170‚Üí      
   171‚Üí      if (result.error) {
   172‚Üí        console.warn('AI prioritization failed:', result.message);
   173‚Üí        return 0.5;
   174‚Üí      }
   175‚Üí
   176‚Üí      return Math.max(0, Math.min(1, result.priority_score || 0.5));
   177‚Üí    } catch (error) {
   178‚Üí      console.warn('AI prioritization error:', error);
   179‚Üí      return 0.5;
   180‚Üí    }
   181‚Üí  };
   182‚Üí
   183‚Üí  // Smart grouping system
   184‚Üí  const calculateSimilarity = (notif1: SmartNotification, notif2: SmartNotification): number => {
   185‚Üí    let similarity = 0;
   186‚Üí    let factors = 0;
   187‚Üí
   188‚Üí    // Type similarity
   189‚Üí    if (notif1.type === notif2.type) {
   190‚Üí      similarity += 0.3;
   191‚Üí    }
   192‚Üí    factors += 0.3;
   193‚Üí
   194‚Üí    // Title similarity (simple word matching)
   195‚Üí    const words1 = notif1.title.toLowerCase().split(' ');
   196‚Üí    const words2 = notif2.title.toLowerCase().split(' ');
   197‚Üí    const commonWords = words1.filter(word => words2.includes(word));
   198‚Üí    const titleSimilarity = commonWords.length / Math.max(words1.length, words2.length);
   199‚Üí    similarity += titleSimilarity * 0.4;
   200‚Üí    factors += 0.4;
   201‚Üí
   202‚Üí    // Time proximity
   203‚Üí    const timeDiff = Math.abs(notif1.timestamp.getTime() - notif2.timestamp.getTime());
   204‚Üí    const timeProximity = Math.max(0, 1 - timeDiff / stackConfig.groupingTimeWindow);
   205‚Üí    similarity += timeProximity * 0.3;
   206‚Üí    factors += 0.3;
   207‚Üí
   208‚Üí    return similarity / factors;
   209‚Üí  };
   210‚Üí
   211‚Üí  const shouldGroupNotification = (newNotification: SmartNotification): string | null => {
   212‚Üí    if (!stackConfig.enabled) return null;
   213‚Üí
   214‚Üí    const recentNotifications = notifications.value.filter(n => {
   215‚Üí      const timeDiff = Date.now() - n.timestamp.getTime();
   216‚Üí      return timeDiff < stackConfig.groupingTimeWindow;
   217‚Üí    });
   218‚Üí
   219‚Üí    for (const existing of recentNotifications) {
   220‚Üí      const similarity = calculateSimilarity(newNotification, existing);
   221‚Üí      if (similarity > stackConfig.similarityThreshold) {
   222‚Üí        // Check if we already have too many similar notifications
   223‚Üí        const similarCount = notifications.value.filter(n => 
   224‚Üí          calculateSimilarity(n, existing) > stackConfig.similarityThreshold
   225‚Üí        ).length;
   226‚Üí
   227‚Üí        if (similarCount >= stackConfig.maxSimilarNotifications) {
   228‚Üí          return existing.groupId || existing.id;
   229‚Üí        }
   230‚Üí      }
   231‚Üí    }
   232‚Üí
   233‚Üí    return null;
   234‚Üí  };
   235‚Üí
   236‚Üí  // Core notification methods
   237‚Üí  const addNotification = async (
   238‚Üí    notificationData: Omit<SmartNotification, 'id' | 'timestamp' | 'aiScore' | 'aiPriority'>
   239‚Üí  ): Promise<string> => {
   240‚Üí    const id = crypto.randomUUID();
   241‚Üí    
   242‚Üí    const notification: SmartNotification = {
   243‚Üí      id,
   244‚Üí      timestamp: new Date(),
   245‚Üí      autoHide: notificationData.autoHide ?? true,
   246‚Üí      persistent: notificationData.persistent ?? false,
   247‚Üí      aiScore: 0.5,
   248‚Üí      aiPriority: false,
   249‚Üí      ...notificationData
   250‚Üí    };
   251‚Üí
   252‚Üí    // AI prioritization
   253‚Üí    if (aiConfig.enabled) {
   254‚Üí      notification.showAiProcessing = true;
   255‚Üí      const aiScore = await calculateAiPriority(notification);
   256‚Üí      notification.aiScore = aiScore;
   257‚Üí      notification.aiPriority = aiScore > aiConfig.scoreThreshold;
   258‚Üí      notification.showAiProcessing = false;
   259‚Üí
   260‚Üí      // Adjust priority based on AI score
   261‚Üí      if (aiScore > 0.9) notification.priority = 'urgent';
   262‚Üí      else if (aiScore > 0.7) notification.priority = 'high';
   263‚Üí      else if (aiScore > 0.5) notification.priority = 'medium';
   264‚Üí      else notification.priority = 'low';
   265‚Üí    }
   266‚Üí
   267‚Üí    // Smart grouping
   268‚Üí    const groupId = shouldGroupNotification(notification);
   269‚Üí    if (groupId) {
   270‚Üí      notification.groupId = groupId;
   271‚Üí      updateNotificationGroup(groupId, notification);
   272‚Üí    }
   273‚Üí
   274‚Üí    // Add to appropriate queue
   275‚Üí    if (notifications.value.length >= settings.maxVisible) {
   276‚Üí      queuedNotifications.value.push(notification);
   277‚Üí    } else {
   278‚Üí      notifications.value.push(notification);
   279‚Üí      
   280‚Üí      // Sort by priority and AI score
   281‚Üí      sortNotifications();
   282‚Üí      
   283‚Üí      // Set up auto-hide if enabled
   284‚Üí      if (notification.autoHide && !notification.persistent) {
   285‚Üí        scheduleAutoHide(notification);
   286‚Üí      }
   287‚Üí    }
   288‚Üí
   289‚Üí    console.log(`üì¢ Added notification: ${notification.title} (AI Score: ${notification.aiScore})`);
   290‚Üí    
   291‚Üí    return id;
   292‚Üí  };
   293‚Üí
   294‚Üí  const dismissNotification = (id: string): void => {
   295‚Üí    const index = notifications.value.findIndex(n => n.id === id);
   296‚Üí    if (index !== -1) {
   297‚Üí      const notification = notifications.value[index];
   298‚Üí      notifications.value.splice(index, 1);
   299‚Üí      
   300‚Üí      // Cancel auto-hide timer
   301‚Üí      if (autoHideTimers.has(id)) {
   302‚Üí        clearTimeout(autoHideTimers.get(id)!);
   303‚Üí        autoHideTimers.delete(id);
   304‚Üí      }
   305‚Üí      
   306‚Üí      // Remove from paused timers
   307‚Üí      pausedTimers.delete(id);
   308‚Üí      
   309‚Üí      // Remember dismissal
   310‚Üí      dismissedIds.add(id);
   311‚Üí      
   312‚Üí      // Process queue
   313‚Üí      processQueue();
   314‚Üí      
   315‚Üí      console.log(`üóëÔ∏è Dismissed notification: ${notification.title}`);
   316‚Üí    }
   317‚Üí  };
   318‚Üí
   319‚Üí  const updateNotification = (id: string, updates: Partial<SmartNotification>): void => {
   320‚Üí    const notification = notifications.value.find(n => n.id === id);
   321‚Üí    if (notification) {
   322‚Üí      Object.assign(notification, updates);
   323‚Üí      
   324‚Üí      // Re-sort if priority changed
   325‚Üí      if ('priority' in updates || 'aiScore' in updates) {
   326‚Üí        sortNotifications();
   327‚Üí      }
   328‚Üí    }
   329‚Üí  };
   330‚Üí
   331‚Üí  const clearAllNotifications = (): void => {
   332‚Üí    // Clear all timers
   333‚Üí    autoHideTimers.forEach(timer => clearTimeout(timer));
   334‚Üí    autoHideTimers.clear();
   335‚Üí    pausedTimers.clear();
   336‚Üí    
   337‚Üí    // Clear arrays
   338‚Üí    notifications.value = [];
   339‚Üí    queuedNotifications.value = [];
   340‚Üí    groups.value = [];
   341‚Üí    
   342‚Üí    console.log('üßπ Cleared all notifications');
   343‚Üí  };
   344‚Üí
   345‚Üí  // Notification grouping
   346‚Üí  const updateNotificationGroup = (groupId: string, notification: SmartNotification): void => {
   347‚Üí    let group = groups.value.find(g => g.id === groupId);
   348‚Üí    
   349‚Üí    if (!group) {
   350‚Üí      group = {
   351‚Üí        id: groupId,
   352‚Üí        title: `Similar to "${notification.title}"`,
   353‚Üí        count: 1,
   354‚Üí        priority: notification.priority,
   355‚Üí        lastNotification: notification,
   356‚Üí        collapsed: true
   357‚Üí      };
   358‚Üí      groups.value.push(group);
   359‚Üí    } else {
   360‚Üí      group.count++;
   361‚Üí      group.lastNotification = notification;
   362‚Üí      
   363‚Üí      // Update priority to highest in group
   364‚Üí      if (notification.priority === 'urgent' || 
   365‚Üí          (notification.priority === 'high' && group.priority !== 'urgent') ||
   366‚Üí          (notification.priority === 'medium' && !['urgent', 'high'].includes(group.priority))) {
   367‚Üí        group.priority = notification.priority;
   368‚Üí      }
   369‚Üí    }
   370‚Üí  };
   371‚Üí
   372‚Üí  // Timer management
   373‚Üí  const scheduleAutoHide = (notification: SmartNotification): void => {
   374‚Üí    if (autoHideTimers.has(notification.id)) {
   375‚Üí      clearTimeout(autoHideTimers.get(notification.id)!);
   376‚Üí    }
   377‚Üí
   378‚Üí    const delay = settings.defaultAutoHideDelay * (notification.priority === 'urgent' ? 2 : 1);
   379‚Üí    
   380‚Üí    const timer = setTimeout(() => {
   381‚Üí      dismissNotification(notification.id);
   382‚Üí    }, delay);
   383‚Üí    
   384‚Üí    autoHideTimers.set(notification.id, timer);
   385‚Üí  };
   386‚Üí
   387‚Üí  const pauseAutoHide = (id: string): void => {
   388‚Üí    const notification = notifications.value.find(n => n.id === id);
   389‚Üí    if (notification && autoHideTimers.has(id)) {
   390‚Üí      clearTimeout(autoHideTimers.get(id)!);
   391‚Üí      autoHideTimers.delete(id);
   392‚Üí      pausedTimers.set(id, new Date());
   393‚Üí      notification.isPaused = true;
   394‚Üí    }
   395‚Üí  };
   396‚Üí
   397‚Üí  const resumeAutoHide = (id: string): void => {
   398‚Üí    const notification = notifications.value.find(n => n.id === id);
   399‚Üí    if (notification && pausedTimers.has(id) && notification.autoHide && !notification.persistent) {
   400‚Üí      const pausedAt = pausedTimers.get(id)!;
   401‚Üí      const pauseDuration = Date.now() - pausedAt.getTime();
   402‚Üí      const originalDelay = settings.defaultAutoHideDelay * (notification.priority === 'urgent' ? 2 : 1);
   403‚Üí      const remainingDelay = Math.max(1000, originalDelay - pauseDuration);
   404‚Üí      
   405‚Üí      const timer = setTimeout(() => {
   406‚Üí        dismissNotification(id);
   407‚Üí      }, remainingDelay);
   408‚Üí      
   409‚Üí      autoHideTimers.set(id, timer);
   410‚Üí      pausedTimers.delete(id);
   411‚Üí      notification.isPaused = false;
   412‚Üí    }
   413‚Üí  };
   414‚Üí
   415‚Üí  const getTimerProgress = (id: string): number => {
   416‚Üí    const notification = notifications.value.find(n => n.id === id);
   417‚Üí    if (!notification || !notification.autoHide || notification.persistent || notification.isPaused) {
   418‚Üí      return 0;
   419‚Üí    }
   420‚Üí
   421‚Üí    const elapsed = Date.now() - notification.timestamp.getTime();
   422‚Üí    const total = settings.defaultAutoHideDelay * (notification.priority === 'urgent' ? 2 : 1);
   423‚Üí    
   424‚Üí    return Math.min(100, (elapsed / total) * 100);
   425‚Üí  };
   426‚Üí
   427‚Üí  // Queue management
   428‚Üí  const processQueue = (): void => {
   429‚Üí    while (notifications.value.length < settings.maxVisible && queuedNotifications.value.length > 0) {
   430‚Üí      const next = queuedNotifications.value.shift();
   431‚Üí      if (next && !dismissedIds.has(next.id)) {
   432‚Üí        notifications.value.push(next);
   433‚Üí        
   434‚Üí        if (next.autoHide && !next.persistent) {
   435‚Üí          scheduleAutoHide(next);
   436‚Üí        }
   437‚Üí      }
   438‚Üí    }
   439‚Üí    
   440‚Üí    sortNotifications();
   441‚Üí  };
   442‚Üí
   443‚Üí  const sortNotifications = (): void => {
   444‚Üí    notifications.value.sort((a, b) => {
   445‚Üí      // Priority order: urgent > high > medium > low
   446‚Üí      const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
   447‚Üí      const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
   448‚Üí      
   449‚Üí      if (priorityDiff !== 0) return priorityDiff;
   450‚Üí      
   451‚Üí      // Then by AI score
   452‚Üí      const aiScoreDiff = (b.aiScore || 0) - (a.aiScore || 0);
   453‚Üí      if (aiScoreDiff !== 0) return aiScoreDiff;
   454‚Üí      
   455‚Üí      // Then by timestamp (newer first)
   456‚Üí      return b.timestamp.getTime() - a.timestamp.getTime();
   457‚Üí    });
   458‚Üí  };
   459‚Üí
   460‚Üí  // Action execution
   461‚Üí  const executeNotificationAction = async (notificationId: string, action: NotificationAction): Promise<void> => {
   462‚Üí    const notification = notifications.value.find(n => n.id === notificationId);
   463‚Üí    if (!notification) return;
   464‚Üí
   465‚Üí    // Set loading state
   466‚Üí    action.loading = true;
   467‚Üí
   468‚Üí    try {
   469‚Üí      await action.handler(notificationId);
   470‚Üí      
   471‚Üí      // Auto-dismiss after successful action unless persistent
   472‚Üí      if (!notification.persistent) {
   473‚Üí        setTimeout(() => dismissNotification(notificationId), 1000);
   474‚Üí      }
   475‚Üí    } catch (error) {
   476‚Üí      console.error('Notification action failed:', error);
   477‚Üí      
   478‚Üí      // Show error notification
   479‚Üí      addNotification({
   480‚Üí        type: 'error',
   481‚Üí        priority: 'high',
   482‚Üí        title: 'Action Failed',
   483‚Üí        message: `Failed to execute "${action.label}": ${error instanceof Error ? error.message : 'Unknown error'}`,
   484‚Üí        icon: 'fluent:error-circle-24-filled',
   485‚Üí        autoHide: true
   486‚Üí      });
   487‚Üí    } finally {
   488‚Üí      action.loading = false;
   489‚Üí    }
   490‚Üí  };
   491‚Üí
   492‚Üí  // Realtime integration
   493‚Üí  const handleRealtimeEvent = (event: RealtimeEvent): void => {
   494‚Üí    if (!settings.enableRealTimeSync) return;
   495‚Üí
   496‚Üí    // Map realtime events to notifications
   497‚Üí    const notificationMapping: Record<string, Partial<SmartNotification>> = {
   498‚Üí      'post.published': {
   499‚Üí        type: 'success',
   500‚Üí        title: 'Post Published',
   501‚Üí        message: 'Your post has been successfully published',
   502‚Üí        icon: 'fluent:checkmark-circle-24-filled',
   503‚Üí        priority: 'medium'
   504‚Üí      },
   505‚Üí      'post.failed': {
   506‚Üí        type: 'error',
   507‚Üí        title: 'Post Failed',
   508‚Üí        message: 'Failed to publish your post',
   509‚Üí        icon: 'fluent:error-circle-24-filled',
   510‚Üí        priority: 'high',
   511‚Üí        actions: [{
   512‚Üí          id: 'retry',
   513‚Üí          label: 'Retry',
   514‚Üí          icon: 'fluent:arrow-clockwise-24-regular',
   515‚Üí          variant: 'primary',
   516‚Üí          handler: async () => {
   517‚Üí            // Retry logic would go here
   518‚Üí            console.log('Retrying post...');
   519‚Üí          }
   520‚Üí        }]
   521‚Üí      },
   522‚Üí      'billing.credit.low': {
   523‚Üí        type: 'warning',
   524‚Üí        title: 'Credits Running Low',
   525‚Üí        message: 'You have less than 100 AI credits remaining',
   526‚Üí        icon: 'fluent:warning-24-filled',
   527‚Üí        priority: 'high',
   528‚Üí        persistent: true,
   529‚Üí        actions: [{
   530‚Üí          id: 'upgrade',
   531‚Üí          label: 'Upgrade Plan',
   532‚Üí          icon: 'fluent:arrow-up-24-regular',
   533‚Üí          variant: 'primary',
   534‚Üí          handler: async () => {
   535‚Üí            window.location.href = '/dashboard/billing';
   536‚Üí          }
   537‚Üí        }]
   538‚Üí      },
   539‚Üí      'oauth.expired': {
   540‚Üí        type: 'error',
   541‚Üí        title: 'Account Disconnected',
   542‚Üí        message: 'A social media account has been disconnected',
   543‚Üí        icon: 'fluent:plug-disconnected-24-filled',
   544‚Üí        priority: 'high',
   545‚Üí        actions: [{
   546‚Üí          id: 'reconnect',
   547‚Üí          label: 'Reconnect',
   548‚Üí          icon: 'fluent:link-24-regular',
   549‚Üí          variant: 'primary',
   550‚Üí          handler: async () => {
   551‚Üí            window.location.href = '/dashboard/accounts';
   552‚Üí          }
   553‚Üí        }]
   554‚Üí      }
   555‚Üí    };
   556‚Üí
   557‚Üí    const template = notificationMapping[event.type];
   558‚Üí    if (template) {
   559‚Üí      addNotification({
   560‚Üí        type: template.type || 'info',
   561‚Üí        priority: template.priority || 'medium',
   562‚Üí        title: template.title || 'Notification',
   563‚Üí        message: template.message || 'You have a new notification',
   564‚Üí        icon: template.icon || 'fluent:info-24-filled',
   565‚Üí        actions: template.actions || [],
   566‚Üí        metadata: {
   567‚Üí          realtimeEvent: event,
   568‚Üí          source: 'realtime'
   569‚Üí        }
   570‚Üí      });
   571‚Üí    }
   572‚Üí  };
   573‚Üí
   574‚Üí  // Computed properties
   575‚Üí  const visibleNotifications = computed(() => notifications.value.slice(0, settings.maxVisible));
   576‚Üí  const hasQueuedNotifications = computed(() => queuedNotifications.value.length > 0);
   577‚Üí  const totalNotifications = computed(() => notifications.value.length + queuedNotifications.value.length);
   578‚Üí
   579‚Üí  // Predefined notification helpers
   580‚Üí  const showSuccess = (title: string, message: string, options?: Partial<SmartNotification>) => {
   581‚Üí    return addNotification({
   582‚Üí      type: 'success',
   583‚Üí      title,
   584‚Üí      message,
   585‚Üí      icon: 'fluent:checkmark-circle-24-filled',
   586‚Üí      priority: 'medium',
   587‚Üí      ...options
   588‚Üí    });
   589‚Üí  };
   590‚Üí
   591‚Üí  const showError = (title: string, message: string, options?: Partial<SmartNotification>) => {
   592‚Üí    return addNotification({
   593‚Üí      type: 'error',
   594‚Üí      title,
   595‚Üí      message,
   596‚Üí      icon: 'fluent:error-circle-24-filled',
   597‚Üí      priority: 'high',
   598‚Üí      ...options
   599‚Üí    });
   600‚Üí  };
   601‚Üí
   602‚Üí  const showWarning = (title: string, message: string, options?: Partial<SmartNotification>) => {
   603‚Üí    return addNotification({
   604‚Üí      type: 'warning',
   605‚Üí      title,
   606‚Üí      message,
   607‚Üí      icon: 'fluent:warning-24-filled',
   608‚Üí      priority: 'medium',
   609‚Üí      ...options
   610‚Üí    });
   611‚Üí  };
   612‚Üí
   613‚Üí  const showInfo = (title: string, message: string, options?: Partial<SmartNotification>) => {
   614‚Üí    return addNotification({
   615‚Üí      type: 'info',
   616‚Üí      title,
   617‚Üí      message,
   618‚Üí      icon: 'fluent:info-24-filled',
   619‚Üí      priority: 'low',
   620‚Üí      ...options
   621‚Üí    });
   622‚Üí  };
   623‚Üí
   624‚Üí  // Lifecycle
   625‚Üí  onMounted(() => {
   626‚Üí    // Subscribe to realtime events if enabled
   627‚Üí    if (settings.enableRealTimeSync && isConnected.value) {
   628‚Üí      const eventTypes: RealtimeEventType[] = [
   629‚Üí        'post.published', 'post.failed', 'billing.credit.low', 
   630‚Üí        'oauth.expired', 'platform.error', 'workflow.completed'
   631‚Üí      ];
   632‚Üí      
   633‚Üí      eventTypes.forEach(type => {
   634‚Üí        subscribeRealtime(type as any, handleRealtimeEvent);
   635‚Üí      });
   636‚Üí    }
   637‚Üí  });
   638‚Üí
   639‚Üí  onUnmounted(() => {
   640‚Üí    // Clear all timers
   641‚Üí    autoHideTimers.forEach(timer => clearTimeout(timer));
   642‚Üí    autoHideTimers.clear();
   643‚Üí    pausedTimers.clear();
   644‚Üí  });
   645‚Üí
   646‚Üí  return {
   647‚Üí    // State
   648‚Üí    notifications: readonly(notifications),
   649‚Üí    groups: readonly(groups),
   650‚Üí    queuedNotifications: readonly(queuedNotifications),
   651‚Üí    settings: readonly(settings),
   652‚Üí
   653‚Üí    // Computed
   654‚Üí    visibleNotifications,
   655‚Üí    hasQueuedNotifications,
   656‚Üí    totalNotifications,
   657‚Üí
   658‚Üí    // Core methods
   659‚Üí    addNotification,
   660‚Üí    dismissNotification,
   661‚Üí    updateNotification,
   662‚Üí    clearAllNotifications,
   663‚Üí
   664‚Üí    // Timer management
   665‚Üí    pauseAutoHide,
   666‚Üí    resumeAutoHide,
   667‚Üí    getTimerProgress,
   668‚Üí
   669‚Üí    // Action execution
   670‚Üí    executeNotificationAction,
   671‚Üí
   672‚Üí    // Convenience methods
   673‚Üí    showSuccess,
   674‚Üí    showError,
   675‚Üí    showWarning,
   676‚Üí    showInfo,
   677‚Üí
   678‚Üí    // Configuration
   679‚Üí    aiConfig: readonly(aiConfig),
   680‚Üí    stackConfig: readonly(stackConfig)
   681‚Üí  };
   682‚Üí};
   683‚Üí
   684‚Üíexport default useNotifications;

