    94→<script setup lang="ts">
    95→import { ref, computed, onMounted, onUnmounted, nextTick } from "vue";
    96→import { useMounted, useEventListener, useLocalStorage } from "@vueuse/core";
    97→import { Icon } from "@iconify/vue";
    98→import Logo from "@assets/images/logo.svg?raw";
    99→import PrimaryButton from "@vue/buttons/PrimaryButton.vue";
   100→import OutlineButton from "@vue/buttons/OutlineButton.vue";
   101→import { navigate } from "astro:transitions/client";
   102→import { useAuth } from "@composables/useAuth";
   103→import { useSideMenu } from "@composables/useSideMenu";
   104→import { useNotification } from "@/utils/useNotification";
   105→
   106→import type { Models } from "appwrite";
   107→
   108→const mounted = useMounted();
   109→
   110→const props = defineProps<{
   111→  user?: Models.User<Models.Preferences>,
   112→  variant?: 'default' | 'landing',
   113→}>();
   114→const navClass = computed(() =>
   115→  props.variant === 'landing'
   116→    ? 'bg-transparent shadow-none'
   117→    : 'bg-white dark:bg-gray-700 shadow-md'
   118→);
   119→
   120→const { user, isAuthenticated, logout, isLoading, getCurrentUser } = useAuth();
   121→const { isOpen, toggleSideMenu } = useSideMenu();
   122→const { success: showSuccess } = useNotification();
   123→
   124→const activeUser = computed(() => props.user || user.value);
   125→const documentRef = computed(() => mounted.value ? document : null);
   126→const currentTheme = useLocalStorage("theme", mounted.value ? document.documentElement.getAttribute("data-theme") || "light" : "light");
   127→const themeListener = useEventListener(documentRef, "set-theme", (event: CustomEvent) => {
   128→  currentTheme.value = event.detail;
   129→});
   130→
   131→const currentPath = computed(() => {
   132→  if (mounted.value) {
   133→    return window.location.pathname;
   134→  }
   135→  return undefined;
   136→});
   137→
   138→const showSideMenuButton = computed(() => {
   139→  if (!mounted.value) return false;
   140→  return currentPath.value?.includes("dashboard");
   141→});
   142→
   143→const isLoginPage = computed(() => {
   144→  if (!mounted.value) return false;
   145→  return currentPath.value?.includes("/login");
   146→});
   147→
   148→// Initialize auth state
   149→onMounted(async () => {
   150→  // Only initialize auth state if not on auth pages to prevent auto-login after logout
   151→  const isAuthPage = currentPath.value?.includes('/login') || 
   152→                    currentPath.value?.includes('/register') || 
   153→                    currentPath.value?.includes('/reset-password');
   154→  
   155→  if (!activeUser.value && !isAuthPage) {
   156→    await getCurrentUser();
   157→  }
   158→  
   159→  currentTheme.value = document.documentElement.getAttribute("data-theme") || "light";
   160→});
   161→
   162→onUnmounted(() => {
   163→  themeListener();
   164→});
   165→
   166→const handleLogout = async () => {
   167→  console.log("🚪 Starting logout process...");
   168→  
   169→  // Set logout flag IMMEDIATELY to prevent any auto-navigation
   170→  const { isLoggingOut, recentLogout } = await import('@/stores/authStore');
   171→  isLoggingOut.set(true);
   172→  recentLogout.set(Date.now());
   173→  
   174→  await logout();
   175→  
   176→  showSuccess("Successfully logged out, redirecting to login...");
   177→  
   178→  // Force a full page reload to ensure complete state clearing
   179→  // This prevents any cached JavaScript state from interfering
   180→  setTimeout(() => {
   181→    if (typeof window !== 'undefined') {
   182→      window.location.href = '/login';
   183→    } else {
   184→      navigate('/login');
   185→    }
   186→  }, 500);
   187→};
   188→
   189→const goToDashboard = () => {
   190→  navigate("/dashboard");
   191→};
   192→
   193→const loginButton = () => {
   194→  navigate(isLoginPage.value ? "/register" : "/login");
   195→};
   196→
   197→const toggleTheme = async () => {
   198→  if (mounted.value) {
   199→    // Get the current actual theme from the document
   200→    const actualTheme = document.documentElement.getAttribute("data-theme") || "light";
   201→
   202→    // Calculate the new theme based on the actual current theme
   203→    const newTheme = actualTheme === "dark" ? "light" : "dark";
   204→
   205→    // Update the ref to match
   206→    currentTheme.value = newTheme;
   207→
   208→    // Apply the theme change to the document
   209→    document.documentElement.setAttribute("data-theme", newTheme);
   210→
   211→    // Dispatch the event for other components
   212→    document.dispatchEvent(new CustomEvent("set-theme", { detail: newTheme }));
   213→
   214→    // Force a re-render to ensure the icon updates correctly
   215→    await nextTick();
   216→  }
   217→};
   218→</script>

