          "account-group",
          "auto-fix",
          "content-copy",
          "swap-horizontal",
          "arrow-right",
          "account-voice",
          "chart-line",
          "calendar-clock",
          "trending-up",
          "refresh-auto",
          "image-edit",
          "image-multiple",
          "rocket-launch",
        ],
      },
    }),
    partytown(),
    sitemap({
      filter(page) {
        return !page.includes("api") && !page.includes("admin");
      },
    }),
  ],
});

