import typography from '@tailwindcss/typography';

/** @type {import('tailwindcss').Config} */
export default {
  darkMode: ["class", '[data-theme="dark"]'],
  content: ["./src/**/*.{astro,html,js,jsx,md,mdx,svelte,ts,tsx,vue}"],
  theme: {
    extend: {
      colors: {
        primary: {
          100: "#cce5e7",
          200: "#99cbd0",
          300: "#66b0b8",
          400: "#3396a1",
          500: "#007c89",
          600: "#00636e",
          700: "#004a52",
          800: "#003237",
          900: "#00191b",
        },

        // Accent colors from research
        accent: {
          purple: {
            50: '#faf5ff',
            100: '#f3e8ff',
            200: '#e9d5ff',
            300: '#d8b4fe',
            400: '#c084fc',
            500: '#7c3aed',  // AI features
            600: '#6d28d9',
            700: '#5b21b6',
            800: '#4c1d95',
            900: '#3b0764',
          },
          orange: {
            50: '#fff7ed',
            100: '#ffedd5',
            200: '#fed7aa',
            300: '#fdba74',
            400: '#fb923c',
            500: '#f59e0b',  // CTAs/highlights
            600: '#d97706',
            700: '#b45309',
            800: '#92400e',
            900: '#78350f',
          },
        },

        // Dark mode optimized colors
        dark: {
          bg: {
            primary: '#0D1117',     // Near-black (not pure black)
            secondary: '#161B22',
            tertiary: '#1F2937',
          },
          border: '#30363d',
          text: {
            primary: '#f9fafb',
            secondary: '#d1d5db',
            tertiary: '#9ca3af',
          },
        },
      },

      // Fluid typography with clamp()
      fontSize: {
        'display-2xl': 'clamp(3rem, 5vw, 4.5rem)',    // 48-72px hero
        'display-xl': 'clamp(2.5rem, 4vw, 3.5rem)',   // 40-56px
        '3xl': 'clamp(1.875rem, 3vw, 2.25rem)',       // 30-36px
      },

      // Letter spacing for headlines
      letterSpacing: {
        tighter: '-0.05em',
        tight: '-0.025em',
      },

      // Animation durations - synced with src/constants/animations.ts
      transitionDuration: {
        'instant': '100ms',  // Quick feedback
        'fast': '200ms',     // Hover states, simple selections
        'normal': '300ms',   // Standard transitions
        'slow': '500ms',     // Page transitions (max)
        'celebration': '2000ms' // Success celebrations
      },

      // Animation timing functions - synced with src/constants/animations.ts
      transitionTimingFunction: {
        'ease-out-custom': 'cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'ease-in-out-custom': 'cubic-bezier(0.42, 0, 0.58, 1)',
        'bounce-custom': 'cubic-bezier(0.34, 1.56, 0.64, 1)',
        'smooth': 'cubic-bezier(0.16, 1, 0.3, 1)',
        'sharp': 'cubic-bezier(0.4, 0, 0.2, 1)',
        'elastic': 'cubic-bezier(0.68, -0.55, 0.265, 1.55)'
      },

      // Minimal utility animations (for static decorative elements only)
      // Use useAnimateElement composable for all interactive/controlled animations
      animation: {
        'fade-in': 'fadeIn 300ms cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'slide-in-up': 'slideInUp 300ms cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'pulse-slow': 'pulse 3s cubic-bezier(0.42, 0, 0.58, 1) infinite',
        'spin-slow': 'spin 8s linear infinite',
        'float-slow': 'floatSlow 20s cubic-bezier(0.42, 0, 0.58, 1) infinite',
        'shimmer': 'shimmer 2s linear infinite',
      },

      keyframes: {
        fadeIn: {
          '0%': { opacity: '0' },
          '100%': { opacity: '1' }
        },
        slideInUp: {
          '0%': { opacity: '0', transform: 'translateY(20px)' },
          '100%': { opacity: '1', transform: 'translateY(0)' }
        },
        floatSlow: {
          '0%, 100%': { transform: 'translate(0, 0)' },
          '50%': { transform: 'translate(20px, -20px)' }
        },
        shimmer: {
          '0%': { transform: 'translateX(-100%)' },
          '100%': { transform: 'translateX(100%)' }
        }
      },

      // Custom typography styling for documentation
      typography: ({ theme }) => ({
        DEFAULT: {
          css: {
            '--tw-prose-body': theme('colors.gray[700]'),
            '--tw-prose-headings': theme('colors.gray[900]'),
            '--tw-prose-lead': theme('colors.gray[600]'),
            '--tw-prose-links': theme('colors.primary[600]'),
            '--tw-prose-bold': theme('colors.gray[900]'),
            '--tw-prose-counters': theme('colors.gray[500]'),
            '--tw-prose-bullets': theme('colors.gray[400]'),
            '--tw-prose-hr': theme('colors.gray[200]'),
            '--tw-prose-quotes': theme('colors.gray[900]'),
            '--tw-prose-quote-borders': theme('colors.primary[200]'),
            '--tw-prose-captions': theme('colors.gray[600]'),
            '--tw-prose-code': theme('colors.gray[900]'),
            '--tw-prose-pre-code': theme('colors.gray[100]'),
            '--tw-prose-pre-bg': theme('colors.gray[900]'),
            '--tw-prose-th-borders': theme('colors.gray[300]'),
            '--tw-prose-td-borders': theme('colors.gray[200]'),

            // Improved spacing and line heights
            lineHeight: '1.75',
            fontSize: '1.0625rem', // 17px base

            // Headings
            h1: {
              fontWeight: '800',
              letterSpacing: theme('letterSpacing.tight'),
              marginTop: '0',
              marginBottom: '0.875em',
              fontSize: '2.25em',
              lineHeight: '1.1',
            },
            h2: {
              fontWeight: '700',
              letterSpacing: theme('letterSpacing.tight'),
              marginTop: '2em',
              marginBottom: '1em',
              fontSize: '1.75em',
              lineHeight: '1.25',
            },
            h3: {
              fontWeight: '600',
              marginTop: '1.6em',
              marginBottom: '0.6em',
              fontSize: '1.375em',
              lineHeight: '1.4',
            },
            h4: {
              fontWeight: '600',
              marginTop: '1.5em',
              marginBottom: '0.5em',
              fontSize: '1.125em',
              lineHeight: '1.5',
            },

            // Paragraphs
            p: {
              marginTop: '1.25em',
              marginBottom: '1.25em',
            },

            // Lists
            'ul, ol': {
              paddingLeft: '1.625em',
              marginTop: '1.25em',
              marginBottom: '1.25em',
            },
            li: {
              marginTop: '0.5em',
              marginBottom: '0.5em',
            },
            'li > p': {
              marginTop: '0.75em',
              marginBottom: '0.75em',
            },
            'ul > li': {
              paddingLeft: '0.375em',
            },
            'ul > li::marker': {
              color: theme('colors.primary[500]'),
            },

            // Links
            a: {
              fontWeight: '500',
              textDecoration: 'none',
              borderBottom: `1px solid ${theme('colors.primary[300]')}`,
              transition: 'all 0.2s ease',
              '&:hover': {
                color: theme('colors.primary[700]'),
                borderBottomColor: theme('colors.primary[500]'),
              },
            },

            // Code blocks
            'code::before': {
              content: '""',
            },
            'code::after': {
              content: '""',
            },
            code: {
              fontWeight: '500',
              fontSize: '0.875em',
              padding: '0.25em 0.375em',
              borderRadius: '0.25rem',
              backgroundColor: theme('colors.gray[100]'),
              border: `1px solid ${theme('colors.gray[200]')}`,
            },
            pre: {
              borderRadius: '0.5rem',
              padding: '1.25em 1.5em',
              fontSize: '0.875em',
              lineHeight: '1.7',
              border: `1px solid ${theme('colors.gray[700]')}`,
              boxShadow: theme('boxShadow.sm'),
            },
            'pre code': {
              backgroundColor: 'transparent',
              border: 'none',
              padding: '0',
              fontSize: 'inherit',
              fontWeight: 'inherit',
            },

            // Blockquotes
            blockquote: {
              fontStyle: 'normal',
              fontWeight: '400',
              borderLeftWidth: '4px',
              borderLeftColor: theme('colors.primary[500]'),
              paddingLeft: '1.5em',
              marginTop: '1.5em',
              marginBottom: '1.5em',
              backgroundColor: theme('colors.primary[50]'),
              padding: '1em 1.5em',
              borderRadius: '0.375rem',
            },
            'blockquote p:first-of-type::before': {
              content: '""',
            },
            'blockquote p:last-of-type::after': {
              content: '""',
            },

            // Tables
            table: {
              fontSize: '0.875em',
              borderRadius: '0.5rem',
              overflow: 'hidden',
            },
            thead: {
              backgroundColor: theme('colors.gray[50]'),
              borderBottomWidth: '2px',
              borderBottomColor: theme('colors.gray[300]'),
            },
            'thead th': {
              fontWeight: '600',
              verticalAlign: 'bottom',
              paddingTop: '0.75em',
              paddingBottom: '0.75em',
            },
            'tbody tr': {
              borderBottomWidth: '1px',
              borderBottomColor: theme('colors.gray[200]'),
            },
            'tbody tr:last-child': {
              borderBottomWidth: '0',
            },

            // Horizontal rules
            hr: {
              marginTop: '3em',
              marginBottom: '3em',
              borderTopWidth: '2px',
              borderColor: theme('colors.gray[200]'),
            },

            // Details/Summary (collapsible sections)
            details: {
              marginTop: '1.5em',
              marginBottom: '1.5em',
              padding: '1em 1.5em',
              backgroundColor: theme('colors.gray[50]'),
              borderRadius: '0.5rem',
              border: `1px solid ${theme('colors.gray[200]')}`,
            },
            summary: {
              fontWeight: '600',
              cursor: 'pointer',
              userSelect: 'none',
              marginBottom: '0.75em',
              color: theme('colors.gray[900]'),
              '&:hover': {
                color: theme('colors.primary[600]'),
              },
            },
            'details[open] summary': {
              marginBottom: '1em',
              paddingBottom: '0.75em',
              borderBottom: `1px solid ${theme('colors.gray[200]')}`,
            },
          },
        },
        // Dark mode overrides
        invert: {
          css: {
            '--tw-prose-body': theme('colors.gray[300]'),
            '--tw-prose-headings': theme('colors.white'),
            '--tw-prose-lead': theme('colors.gray[400]'),
            '--tw-prose-links': theme('colors.primary[400]'),
            '--tw-prose-bold': theme('colors.white'),
            '--tw-prose-counters': theme('colors.gray[400]'),
            '--tw-prose-bullets': theme('colors.gray[600]'),
            '--tw-prose-hr': theme('colors.gray[700]'),
            '--tw-prose-quotes': theme('colors.gray[100]'),
            '--tw-prose-quote-borders': theme('colors.primary[700]'),
            '--tw-prose-captions': theme('colors.gray[400]'),
            '--tw-prose-code': theme('colors.white'),
            '--tw-prose-pre-code': theme('colors.gray[300]'),
            '--tw-prose-pre-bg': theme('colors.gray[900]'),
            '--tw-prose-th-borders': theme('colors.gray[600]'),
            '--tw-prose-td-borders': theme('colors.gray[700]'),

            code: {
              backgroundColor: theme('colors.gray[800]'),
              border: `1px solid ${theme('colors.gray[700]')}`,
            },
            pre: {
              backgroundColor: theme('colors.gray[900]'),
              border: `1px solid ${theme('colors.gray[700]')}`,
            },
            blockquote: {
              backgroundColor: theme('colors.gray[800]'),
              borderLeftColor: theme('colors.primary[500]'),
            },
            details: {
              backgroundColor: theme('colors.gray[800]'),
              border: `1px solid ${theme('colors.gray[700]')}`,
            },
            'details[open] summary': {
              borderBottom: `1px solid ${theme('colors.gray[700]')}`,
            },
            a: {
              borderBottomColor: theme('colors.primary[600]'),
              '&:hover': {
                color: theme('colors.primary[300]'),
                borderBottomColor: theme('colors.primary[400]'),
              },
            },
            'ul > li::marker': {
              color: theme('colors.primary[400]'),
            },
            thead: {
              backgroundColor: theme('colors.gray[800]'),
              borderBottomColor: theme('colors.gray[600]'),
            },
            'tbody tr': {
              borderBottomColor: theme('colors.gray[700]'),
            },
          },
        },
        // Large variant for better readability
        lg: {
          css: {
            fontSize: '1.125rem',
            lineHeight: '1.75',
          },
        },
        xl: {
          css: {
            fontSize: '1.25rem',
            lineHeight: '1.8',
          },
        },
      }),
    },
  },
  plugins: [
    typography(),
  ],
};

