   157â†’};
   158â†’
   159â†’// Calculate radial positions for platforms - using percentage positioning from center
   160â†’const getPlatformPosition = (index: number) => {
   161â†’  const angle = (index / props.platforms.length) * 2 * Math.PI - Math.PI / 2; // Start from top
   162â†’  const x = Math.cos(angle) * props.radius;
