// Calculate actual beam angles based on platform card positions in 2x2 grid
// Right side cards are positioned in a grid, so we calculate angles to each
const beamAngles = computed(() => {
  // Platform cards are in a 2x2 grid on the right side
  // Approximating positions: top-left, top-right, bottom-left, bottom-right
  // From center (0,0) to right side positions

  // Card positions relative to center (approximate for right-side 2x2 grid)
  // x is distance to right (~400px), y varies for grid positions
  const gridGap = 12 // 3 * 4px (gap-3)
  const cardWidth = 128 // md:w-32
  const cardHeight = 160 // md:h-40

  // Calculate center positions of each card in the 2x2 grid
  const rightOffset = 400 // Distance from center to right side
  const topOffset = -((cardHeight + gridGap / 2) / 2) // Top row
  const bottomOffset = (cardHeight + gridGap / 2) / 2 // Bottom row
  const leftCol = rightOffset - (cardWidth + gridGap / 2) / 2 // Left column
  const rightCol = rightOffset + (cardWidth + gridGap / 2) / 2 // Right column

  return [
    Math.atan2(topOffset, leftCol) * 180 / Math.PI,      // Top-left card
    Math.atan2(topOffset, rightCol) * 180 / Math.PI,     // Top-right card
    Math.atan2(bottomOffset, leftCol) * 180 / Math.PI,   // Bottom-left card
    Math.atan2(bottomOffset, rightCol) * 180 / Math.PI   // Bottom-right card
  ]
})

