import { computed, reactive, ref, watch, unref, onUnmounted, nextTick, type Ref, type ComputedRef } from 'vue'
import {
  useElementVisibility,
  useDocumentVisibility,
  usePreferredReducedMotion,
  useRafFn,
  useEventBus,
  useMounted,
  useBattery,
  useFps,
  useIdle,
  usePageLeave,
  useAnimate,
  useCycleList,
  useScroll,
  type EventBusKey,
  type MaybeRefOrGetter,
  type UseAnimateKeyframes,
  type UseAnimateOptions,
} from '@vueuse/core'

// Import existing presets and constants
import { animationPresets, type AnimationPreset } from './useAnimateElement'
import { ANIMATION_DURATION, EASING, FILL_MODE } from '@/constants/animations'

/**
 * Animation state cache for shared state management
 */
const stateCache = new Map<string, any>()
const initialStateCache = new Map<string, any>()

/**
 * Event bus registry for shared event coordination
 */
const busRegistry = new Map<string, Map<string, ReturnType<typeof useEventBus>>>()

/**
 * Global environment cache (singleton)
 */
let cachedEnvironment: {
  prefersReducedMotion: Ref<'reduce' | 'no-preference'>
  batteryInfo: ReturnType<typeof useBattery>
  fps: Ref<number> | null
  idle: { idle: Ref<boolean>; lastActive: Ref<number>; reset: () => void } | null
  isPageLeft: Ref<boolean> | null
} | null = null

/**
 * Autoplay configuration
 */
export interface UseAnimationAutoplayOptions {
  threshold?: number
  enabled?: Ref<boolean>
  includeNavigation?: boolean
}

/**
 * Event system configuration
 */
export interface UseAnimationEventsOptions {
  busName?: string
  emitOnStart?: string
  emitOnStop?: string
  listenForStop?: string
}

/**
 * Environment respect configuration
 */
export interface UseAnimationEnvironmentOptions {
  reducedMotion?: boolean
  battery?: boolean
  fps?: boolean
  idle?: boolean
  pageLeave?: boolean
}

/**
 * Web Animations API configuration
 */
export interface UseAnimationWebAnimationOptions {
  preset?: keyof typeof animationPresets
  keyframes?: UseAnimateKeyframes
  duration?: number
  easing?: string
}

/**
 * Timed sequence configuration (for RAF-based element activation)
 */
export interface UseAnimationSequenceOptions<T = any> {
  elements: T[] | Ref<T[]>
  interval: number | Ref<number> // Duration per element (ms)
  mode?: 'sequence' | 'loop' | 'stagger' | 'unison'
  onActivate: (element: T, index: number, context: {
    cycleIndex: number
    elapsedInCycle: number
    totalElapsed: number
  }) => void
  onCycleComplete?: () => void
}

/**
 * Main useAnimation options
 */
export interface UseAnimationOptions<T = any> {
  // Auto-play configuration
  autoplay?: boolean | UseAnimationAutoplayOptions

  // Event system (replaces useAnimationBus)
  events?: UseAnimationEventsOptions

  // State management (replaces useAnimationState)
  stateName?: string
  initialState?: Record<string, any>

  // Environment (replaces useAnimationEnvironment)
  respectEnvironment?: boolean | UseAnimationEnvironmentOptions

  // Web Animations (replaces useAnimateElement)
  animation?: UseAnimationWebAnimationOptions

  // Timed sequence (NEW - replaces useTimedSequence)
  sequence?: UseAnimationSequenceOptions<T>

  // Callbacks
  onStart?: () => void
  onStop?: () => void
  onComplete?: () => void
}

/**
 * Animation quality level
 */
export type AnimationQuality = 'none' | 'low' | 'medium' | 'high'

/**
 * useAnimation return type
 */
export interface UseAnimationReturn<TState = any, TElement = any> {
  // Playback controls
  play: () => void
  pause: () => void
  stop: () => void
  resume: () => void

  // State access
  isPlaying: Ref<boolean>
  state: TState

  // Event emitter
  emit: <K extends string>(event: K, payload?: any) => void
  on: <K extends string>(event: K, handler: (payload: any) => void) => () => void

  // Environment info
  canAnimate: ComputedRef<boolean>
  animationQuality: ComputedRef<AnimationQuality>
  shouldPauseAnimations: ComputedRef<boolean>

  // Animation instance (if Web Animations enabled)
  animate?: Ref<Animation | undefined>
  animateIn?: (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>
  animateOut?: (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>

  // Sequence controls (if sequence enabled)
  sequence?: {
    currentIndex: Ref<number>
    currentElement: ComputedRef<TElement | undefined>
    cycleIndex: Ref<number>
    next: () => void
    prev: () => void
    goTo: (index: number) => void
  }

  // Cleanup
  cleanup: () => void
}

/**
 * Universal animation orchestrator that consolidates:
 * - useAnimationBus (event coordination)
 * - useAnimationState (state management)
 * - useAnimationEnvironment (environment detection)
 * - useAnimateElement (Web Animations API)
 */
export function useAnimation<TState = any, TElement = any>(
  target: MaybeRefOrGetter<HTMLElement | undefined>,
  options: UseAnimationOptions<TElement> = {}
): UseAnimationReturn<TState, TElement> {
  const {
    autoplay = false,
    events,
    stateName,
    initialState,
    respectEnvironment = true,
    animation,
    sequence,
    onStart,
    onStop,
    onComplete,
  } = options

  const isMounted = useMounted()

  // Parse autoplay options
  const autoplayOptions: UseAnimationAutoplayOptions =
    typeof autoplay === 'boolean'
      ? { threshold: 0.25, enabled: ref(autoplay) }
      : { threshold: autoplay.threshold || 0.25, enabled: autoplay.enabled || ref(true) }

  // Parse environment options
  const envOptions: UseAnimationEnvironmentOptions =
    typeof respectEnvironment === 'boolean'
      ? { reducedMotion: true, battery: true, fps: true, idle: false, pageLeave: true }
      : {
          reducedMotion: respectEnvironment.reducedMotion !== false,
          battery: respectEnvironment.battery !== false,
          fps: respectEnvironment.fps !== false,
          idle: respectEnvironment.idle === true,
          pageLeave: respectEnvironment.pageLeave !== false,
        }

  // ===============================
  // ENVIRONMENT DETECTION (replaces useAnimationEnvironment)
  // ===============================
  if (!cachedEnvironment) {
    const prefersReducedMotion = usePreferredReducedMotion()
    const batteryInfo = useBattery()
    const fps = envOptions.fps ? useFps({ every: 10 }) : null
    const idle = envOptions.idle
      ? useIdle(5 * 60 * 1000) // 5 minutes
      : null
    const isPageLeft = envOptions.pageLeave ? usePageLeave() : null

    cachedEnvironment = {
      prefersReducedMotion,
      batteryInfo,
      fps,
      idle,
      isPageLeft,
    }
  }

  const env = cachedEnvironment

  // Computed environment flags
  const reducedMotion = computed(
    () => envOptions.reducedMotion && isMounted.value && env.prefersReducedMotion.value === 'reduce'
  )

  const batteryOptimization = computed(() => {
    if (!envOptions.battery) return false
    const level = env.batteryInfo.level.value
    return typeof level === 'number' ? level < 0.2 && !env.batteryInfo.charging.value : false
  })

  const lowFps = computed(() => envOptions.fps && env.fps ? env.fps.value < 30 : false)

  const userIdle = computed(() => envOptions.idle && env.idle ? env.idle.idle.value : false)

  const pageLeft = computed(() => envOptions.pageLeave && env.isPageLeft ? env.isPageLeft.value : false)

  const canAnimate = computed(
    () => !reducedMotion.value && !batteryOptimization.value && !lowFps.value
  )

  const shouldPauseAnimations = computed(
    () => userIdle.value || pageLeft.value || (batteryOptimization.value && env.batteryInfo.level.value < 0.1)
  )

  const animationQuality = computed<AnimationQuality>(() => {
    if (reducedMotion.value) return 'none'
    if (batteryOptimization.value) return 'low'
    if (lowFps.value) return 'medium'
    return 'high'
  })

  // ===============================
  // STATE MANAGEMENT (replaces useAnimationState)
  // ===============================
  let state: TState

  if (stateName) {
    // Get or create shared state
    if (!stateCache.has(stateName)) {
      const resolved = initialState || {}
      const cloned = structuredClone(resolved)
      stateCache.set(stateName, reactive({ isPlaying: false, ...cloned }))
      initialStateCache.set(stateName, structuredClone(cloned))
    }
    state = stateCache.get(stateName) as TState
  } else {
    // Local state
    state = reactive({ isPlaying: false, ...(initialState || {}) }) as TState
  }

  const isPlaying = computed({
    get: () => (state as any).isPlaying,
    set: (value: boolean) => {
      ;(state as any).isPlaying = value
    },
  })

  // ===============================
  // EVENT SYSTEM (replaces useAnimationBus)
  // ===============================
  const busName = events?.busName || 'default'

  if (!busRegistry.has(busName)) {
    busRegistry.set(busName, new Map())
  }
  const eventBuses = busRegistry.get(busName)!

  const getEventBus = (eventName: string) => {
    const namespacedKey = `${busName}:${eventName}` as unknown as EventBusKey<any>
    if (!eventBuses.has(eventName)) {
      eventBuses.set(eventName, useEventBus(namespacedKey))
    }
    return eventBuses.get(eventName)!
  }

  const emit = <K extends string>(event: K, payload?: any) => {
    const bus = getEventBus(event)
    bus.emit(payload)
  }

  const on = <K extends string>(event: K, handler: (payload: any) => void) => {
    const bus = getEventBus(event)
    return bus.on(handler as any)
  }

  // Listen for stop events
  const stopListeners: Array<() => void> = []
  if (events?.listenForStop) {
    const unsubscribe = on(events.listenForStop, () => {
      stop()
    })
    stopListeners.push(unsubscribe)
  }

  // ===============================
  // AUTO-PLAY LOGIC (replaces manual visibility watchers)
  // ===============================
  const targetElement = computed(() => unref(target))
  const elementVisible = autoplay
    ? useElementVisibility(targetElement, { threshold: autoplayOptions.threshold })
    : ref(false)
  const documentVisible = autoplay ? useDocumentVisibility() : ref('visible')

  // Navigation tracking (Astro-specific)
  const currentPath = ref(typeof window !== 'undefined' ? window.location.pathname : '')

  if (autoplay && autoplayOptions.includeNavigation && typeof window !== 'undefined') {
    const handleNavigationChange = () => {
      const newPath = window.location.pathname
      if (newPath !== currentPath.value) {
        currentPath.value = newPath
        stop()
      }
    }

    window.addEventListener('popstate', handleNavigationChange)
    if (typeof document !== 'undefined') {
      document.addEventListener('astro:before-preparation', handleNavigationChange)
      document.addEventListener('astro:page-load', handleNavigationChange)
    }

    onUnmounted(() => {
      window.removeEventListener('popstate', handleNavigationChange)
      if (typeof document !== 'undefined') {
        document.removeEventListener('astro:before-preparation', handleNavigationChange)
        document.removeEventListener('astro:page-load', handleNavigationChange)
      }
    })
  }

  const shouldAutoplay = computed(() => {
    if (!autoplay) return false
    if (!autoplayOptions.enabled?.value) return false
    if (documentVisible.value !== 'visible') return false
    if (!canAnimate.value) return false
    return elementVisible.value
  })

  // ===============================
  // WEB ANIMATIONS API (replaces useAnimateElement)
  // ===============================
  let webAnimation:
    | {
        animate: Ref<Animation | undefined>
        play: () => void
        pause: () => void
        cancel: () => void
        animateIn: (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>
        animateOut: (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>
      }
    | undefined

  if (animation) {
    const preset = animation.preset || 'fadeIn'
    const presetConfig = animationPresets[preset]
    const keyframes = animation.keyframes || presetConfig.keyframes
    const baseOptions =
      typeof presetConfig.options === 'object' ? presetConfig.options : { duration: presetConfig.options }

    const baseDuration = animation.duration || (typeof baseOptions.duration === 'number' ? baseOptions.duration : ANIMATION_DURATION.NORMAL)

    const effectiveDuration = computed(() => {
      switch (animationQuality.value) {
        case 'none':
          return 0
        case 'low':
          return baseDuration * 0.3
        case 'medium':
          return baseDuration * 0.6
        case 'high':
          return baseDuration
      }
    })

    const animationOptions = computed(
      () =>
        ({
          ...baseOptions,
          duration: effectiveDuration.value,
          ...(animation.easing && { easing: animation.easing }),
          immediate: false,
          onReady: onComplete,
        }) as UseAnimateOptions
    )

    const webAnimAPI = useAnimate(target, keyframes, animationOptions.value)

    const animateIn = async (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => {
      if (!isMounted.value || !webAnimAPI.isSupported.value) return

      if (animationQuality.value === 'none' || !canAnimate.value) {
        const element = unref(target)
        if (element && Array.isArray(keyframes) && keyframes.length > 0) {
          const finalFrame = keyframes[keyframes.length - 1] as Record<string, any>
          Object.assign(element.style, finalFrame)
        }
        onComplete?.()
        return
      }

      if (customKeyframes && webAnimAPI.animate.value) {
        webAnimAPI.cancel()
        const element = unref(target)
        if (element) {
          const newAnimation = element.animate(customKeyframes as Keyframe[], {
            ...animationOptions.value,
            ...customOptions,
          })
          if (onComplete) newAnimation.addEventListener('finish', onComplete)
          return newAnimation
        }
      } else {
        webAnimAPI.play()
      }
    }

    const animateOut = async (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => {
      if (!isMounted.value || !webAnimAPI.isSupported.value) return

      if (animationQuality.value === 'none' || !canAnimate.value) {
        const element = unref(target)
        if (element) {
          const defaultOutKeyframes = [
            { opacity: 1, transform: 'scale(1)' },
            { opacity: 0, transform: 'scale(0.8)' },
          ]
          const finalFrame = (outKeyframes?.[outKeyframes.length - 1] || defaultOutKeyframes[1]) as Record<string, any>
          Object.assign(element.style, finalFrame)
        }
        onComplete?.()
        return
      }

      const defaultOutKeyframes = [
        { opacity: 1, transform: 'scale(1)' },
        { opacity: 0, transform: 'scale(0.8)' },
      ]
      const defaultOutDuration =
        animationQuality.value === 'low'
          ? ANIMATION_DURATION.FAST * 0.3
          : animationQuality.value === 'medium'
            ? ANIMATION_DURATION.FAST * 0.6
            : ANIMATION_DURATION.FAST
      const defaultOutOptions = {
        duration: defaultOutDuration,
        easing: EASING.EASE_OUT,
        fill: FILL_MODE.FORWARDS as FillMode,
      }
      const element = unref(target)
      if (element) {
        const anim = element.animate((outKeyframes || defaultOutKeyframes) as Keyframe[], {
          ...defaultOutOptions,
          ...outOptions,
        })
        if (onComplete) anim.addEventListener('finish', onComplete)
        return anim
      }
    }

    webAnimation = {
      animate: webAnimAPI.animate,
      play: webAnimAPI.play,
      pause: webAnimAPI.pause,
      cancel: webAnimAPI.cancel,
      animateIn,
      animateOut,
    }
  }

  // ===============================
  // PLAYBACK CONTROLS
  // ===============================
  const play = () => {
    if (!canAnimate.value && animationQuality.value === 'none') {
      // Skip animation, just run callbacks
      onStart?.()
      if (events?.emitOnStart) emit(events.emitOnStart)
      return
    }

    isPlaying.value = true
    onStart?.()
    if (events?.emitOnStart) emit(events.emitOnStart)
    webAnimation?.play()
  }

  const pause = () => {
    webAnimation?.pause()
    // Note: Don't set isPlaying to false, just pause
  }

  const stop = () => {
    isPlaying.value = false
    onStop?.()
    if (events?.emitOnStop) emit(events.emitOnStop)
    webAnimation?.cancel()
  }

  const resume = () => {
    if (isPlaying.value) {
      webAnimation?.play()
    }
  }

  // ===============================
  // WATCHERS
  // ===============================

  // Auto-play watcher
  if (autoplay) {
    watch(shouldAutoplay, (should) => {
      if (should && !isPlaying.value) {
        play()
      } else if (!should && isPlaying.value) {
        stop()
      }
    }, { immediate: true })
  }

  // Auto-pause on environment changes
  watch(shouldPauseAnimations, (shouldPause) => {
    if (shouldPause && isPlaying.value) {
      pause()
    } else if (!shouldPause && isPlaying.value) {
      resume()
    }
  })

  // ===============================
  // TIMED SEQUENCE (NEW - replaces useTimedSequence)
  // ===============================
  let timedSequence:
    | {
        currentIndex: Ref<number>
        currentElement: ComputedRef<TElement | undefined>
        cycleIndex: Ref<number>
        next: () => void
        prev: () => void
        goTo: (index: number) => void
      }
    | undefined

  if (sequence) {
    const elements = computed(() => (Array.isArray(sequence.elements) ? sequence.elements : sequence.elements.value))
    const interval = computed(() => (typeof sequence.interval === 'number' ? sequence.interval : sequence.interval.value))
    const mode = sequence.mode || 'loop'

    // Use VueUse's useCycleList for element cycling
    const { state: currentElement, index: currentIndex, next, prev, go: goTo } = useCycleList(elements)

    const cycleIndex = ref(0)
    const startTime = ref<number | null>(null)
    const currentCycleStartTime = ref(0)

    // RAF loop for timed activation
    const { pause: pauseSequence, resume: resumeSequence } = useRafFn(
      (args) => {
        if (!isPlaying.value || startTime.value === null) {
          pauseSequence()
          return
        }

        const now = args.timestamp
        if (startTime.value === null) {
          startTime.value = now
          currentCycleStartTime.value = now
        }

        const totalElapsed = now - startTime.value
        const elapsedSinceCycle = now - currentCycleStartTime.value
        const elementsArray = elements.value

        if (!elementsArray.length) {
          pauseSequence()
          return
        }

        if (mode === 'loop' || mode === 'sequence') {
          // Sequential activation: one element at a time
          if (elapsedSinceCycle >= interval.value) {
            const element = elementsArray[currentIndex.value]
            const elapsedInCycle = elapsedSinceCycle

            // Activate current element
            sequence.onActivate(element, currentIndex.value, {
              cycleIndex: cycleIndex.value,
              elapsedInCycle,
              totalElapsed,
            })

            // Move to next element
            if (currentIndex.value === elementsArray.length - 1) {
              // Cycle complete
              cycleIndex.value++
              sequence.onCycleComplete?.()

              if (mode === 'sequence') {
                // Stop after one sequence
                stop()
                return
              }
            }

            next()
            currentCycleStartTime.value = now
          }
        } else if (mode === 'stagger') {
          // Stagger: activate elements with delays based on their index
          const timePerElement = interval.value / elementsArray.length

          elementsArray.forEach((element, index) => {
            const activationTime = index * timePerElement
            const nextActivationTime = (index + 1) * timePerElement

            if (elapsedSinceCycle >= activationTime && elapsedSinceCycle < nextActivationTime) {
              sequence.onActivate(element, index, {
                cycleIndex: cycleIndex.value,
                elapsedInCycle: elapsedSinceCycle - activationTime,
                totalElapsed,
              })
            }
          })

          // Check for cycle complete
          if (elapsedSinceCycle >= interval.value) {
            cycleIndex.value++
            sequence.onCycleComplete?.()
            currentCycleStartTime.value = now
          }
        } else if (mode === 'unison') {
          // Unison: activate all elements at once
          if (elapsedSinceCycle < 50) {
            // Activate within first frame
            elementsArray.forEach((element, index) => {
              sequence.onActivate(element, index, {
                cycleIndex: cycleIndex.value,
                elapsedInCycle: elapsedSinceCycle,
                totalElapsed,
              })
            })
          }

          // Check for cycle complete
          if (elapsedSinceCycle >= interval.value) {
            cycleIndex.value++
            sequence.onCycleComplete?.()
            currentCycleStartTime.value = now
          }
        }
      },
      { immediate: false }
    )

    // Auto-start/stop sequence with animation
    watch(isPlaying, (playing) => {
      if (playing) {
        startTime.value = performance.now()
        currentCycleStartTime.value = performance.now()
        resumeSequence()
      } else {
        pauseSequence()
        startTime.value = null
      }
    })

    timedSequence = {
      currentIndex,
      currentElement,
      cycleIndex,
      next,
      prev,
      goTo,
    }
  }

  // ===============================
  // CLEANUP
  // ===============================
  const cleanup = () => {
    stop()
    stopListeners.forEach((unsubscribe) => unsubscribe())
    webAnimation?.cancel()
  }

  onUnmounted(cleanup)

  // ===============================
  // RETURN API
  // ===============================
  return {
    // Playback controls
    play,
    pause,
    stop,
    resume,

    // State
    isPlaying,
    state,

    // Events
    emit,
    on,

    // Environment
    canAnimate,
    animationQuality,
    shouldPauseAnimations,

    // Web Animations
    ...(webAnimation && {
      animate: webAnimation.animate,
      animateIn: webAnimation.animateIn,
      animateOut: webAnimation.animateOut,
    }),

    // Timed Sequence
    ...(timedSequence && {
      sequence: timedSequence,
    }),

    // Cleanup
    cleanup,
  }
}

