      setupObservers();
      startAutoHideTimer();
    } else if (retries < maxRetries) {
      // Keep retrying with exponential backoff
      setTimeout(attemptShow, 500 + (retries * 300)); // 500ms, 800ms, 1100ms, 1400ms, etc.
