    // Debounce the position update
    updatePosition();
  });

  // Start observing the document body for changes
  mutationObserver.observe(document.body, {
    childList: true,
    subtree: true,
    attributes: false
  });

  // Start first retry
  setTimeout(attemptShow, 500);
}

