"""Gemini API integration for insight extraction"""

import json
import os
from pathlib import Path
from typing import List, Dict, Any, Optional
import subprocess


class GeminiExtractor:
    """Extract insights using Gemini API"""

    EXTRACTION_PROMPT = """
Analyze this file and extract all technical insights, decisions, patterns, and key learnings.

For each insight, provide:
1. **Title**: A clear, descriptive title
2. **Content**: The full insight with all relevant details
3. **Category**: One of: "technical_decision", "pattern", "gotcha", "best_practice", "lesson_learned"
4. **Tags**: Relevant hashtags (e.g., #animation, #performance, #vue)
5. **Topic**: The main topic this relates to (e.g., "animation", "workflows", "architecture")

Format your response as a JSON array:
[
  {
    "title": "Insight Title",
    "content": "Full insight description with technical details...",
    "category": "pattern",
    "tags": ["#animation", "#performance"],
    "topic": "animation"
  },
  ...
]

Only extract meaningful, reusable insights. Skip procedural notes or temporary planning details.
"""

    def __init__(self, api_key: Optional[str] = None):
        self.api_key = api_key or os.getenv('GEMINI_API_KEY')

    def extract_from_file(
        self,
        file_path: Path,
        project: Optional[str] = None,
        use_mcp: bool = True
    ) -> List[Dict[str, Any]]:
        """Extract insights from a file"""

        if not file_path.exists():
            raise FileNotFoundError(f"File not found: {file_path}")

        # Read file content
        with open(file_path, 'r', encoding='utf-8') as f:
            content = f.read()

        # Try MCP-based extraction first (if available in calling context)
        if use_mcp:
            try:
                insights = self._extract_via_mcp(file_path, content)
                if insights:
                    return insights
            except Exception:
                pass  # Fall back to direct API

        # Direct API extraction
        return self._extract_via_api(content)

    def _extract_via_mcp(self, file_path: Path, content: str) -> List[Dict[str, Any]]:
        """
        Extract using MCP tool (mcp__gemini-cli__ask-gemini).
        This is a placeholder - actual MCP call would be made by the calling context.
        """
        # This would be called by the MCP server context with proper tool access
        # For now, we return empty to fall back to API
        return []

    def _extract_via_api(self, content: str) -> List[Dict[str, Any]]:
        """Extract using direct Gemini API (requires google-generativeai)"""
        try:
            import google.generativeai as genai

            if not self.api_key:
                raise ValueError("GEMINI_API_KEY not set")

            genai.configure(api_key=self.api_key)
            model = genai.GenerativeModel('gemini-2.0-flash-exp')

            prompt = f"{self.EXTRACTION_PROMPT}\n\n# File Content:\n\n{content}"

            response = model.generate_content(
                prompt,
                generation_config={
                    'temperature': 0.3,
                    'max_output_tokens': 8192,
                }
            )

            # Parse JSON response
            response_text = response.text.strip()

            # Extract JSON from markdown code blocks if present
            if '```json' in response_text:
                response_text = response_text.split('```json')[1].split('```')[0].strip()
            elif '```' in response_text:
                response_text = response_text.split('```')[1].split('```')[0].strip()

            insights = json.loads(response_text)
            return insights

        except ImportError:
            raise ImportError(
                "google-generativeai not installed. "
                "Install with: pip install google-generativeai"
            )
        except Exception as e:
            raise RuntimeError(f"Gemini extraction failed: {e}")

    def format_for_markdown(self, insights: List[Dict[str, Any]]) -> str:
        """Format insights as markdown"""
        sections = []

        for insight in insights:
            section = f"## {insight['title']}\n\n"
            section += f"{insight['content']}\n\n"

            if insight.get('tags'):
                tags = ' '.join(insight['tags'])
                section += f"**Tags:** {tags}\n"

            if insight.get('category'):
                section += f"**Category:** {insight['category']}\n"

            section += "\n---\n"
            sections.append(section)

        return '\n'.join(sections)

    @staticmethod
    def validate_insight(insight: Dict[str, Any]) -> bool:
        """Validate insight structure"""
        required_fields = ['title', 'content']
        return all(field in insight for field in required_fields)

    @staticmethod
    def merge_insights(existing: List[Dict[str, Any]], new: List[Dict[str, Any]]) -> List[Dict[str, Any]]:
        """Merge new insights with existing, checking for duplicates"""
        merged = existing.copy()
        existing_titles = {i['title'].lower() for i in existing}

        for insight in new:
            if insight['title'].lower() not in existing_titles:
                merged.append(insight)

        return merged

