#!/usr/bin/env python3
"""
NATE_SECOND_BRAIN MCP Server

Exposes knowledge management capabilities as MCP tools for any AI agent.
"""

import os
import sys
import asyncio
from pathlib import Path
from typing import Any, Optional

from mcp.server import Server
from mcp.server.stdio import stdio_server
from mcp.types import Tool, TextContent

# Try relative imports first (when run as module), fall back to absolute
try:
    from .utils.database import Database
    from .utils.config import Config
    from .utils.git_ops import GitOps
    from .utils.gemini import GeminiExtractor
except ImportError:
    # Fallback for direct execution
    sys.path.insert(0, str(Path(__file__).parent))
    from utils.database import Database
    from utils.config import Config
    from utils.git_ops import GitOps
    from utils.gemini import GeminiExtractor


# Constants
BRAIN_DIR = Path(os.getenv('BRAIN_DIR', Path.home() / 'NATE_SECOND_BRAIN'))
DB_PATH = BRAIN_DIR / 'knowledge.db'
CONFIG_PATH = BRAIN_DIR / 'config.json'

# Initialize components
config = Config(CONFIG_PATH)
git = GitOps(BRAIN_DIR)
gemini = GeminiExtractor()

# Create MCP server
app = Server("nate-second-brain")


@app.list_tools()
async def list_tools() -> list[Tool]:
    """List available MCP tools"""
    return [
        Tool(
            name="extract_insights",
            description="Extract insights from planning documents using AI analysis",
            inputSchema={
                "type": "object",
                "properties": {
                    "file_path": {
                        "type": "string",
                        "description": "Path to file or folder to extract insights from"
                    },
                    "project": {
                        "type": "string",
                        "description": "Project name (auto-detected if not provided)"
                    },
                    "topic": {
                        "type": "string",
                        "description": "Topic to categorize insights under"
                    },
                    "auto_approve": {
                        "type": "boolean",
                        "default": False,
                        "description": "Auto-save all insights without review"
                    }
                },
                "required": ["file_path"]
            }
        ),
        Tool(
            name="load_insights",
            description="Load topic insights into agent's context",
            inputSchema={
                "type": "object",
                "properties": {
                    "topic": {
                        "type": "string",
                        "description": "Topic to load insights for (e.g., 'animation', 'workflows')"
                    },
                    "project": {
                        "type": "string",
                        "description": "Filter by project (optional)"
                    },
                    "max_results": {
                        "type": "number",
                        "default": 20,
                        "description": "Maximum number of insights to return"
                    }
                },
                "required": ["topic"]
            }
        ),
        Tool(
            name="search_insights",
            description="Full-text search across all insights",
            inputSchema={
                "type": "object",
                "properties": {
                    "query": {
                        "type": "string",
                        "description": "Search query"
                    },
                    "category": {
                        "type": "string",
                        "enum": ["project", "reference", "documentation"],
                        "description": "Filter by category (optional)"
                    },
                    "topic": {
                        "type": "string",
                        "description": "Filter by topic (optional)"
                    },
                    "max_results": {
                        "type": "number",
                        "default": 20,
                        "description": "Maximum number of results"
                    }
                },
                "required": ["query"]
            }
        ),
        Tool(
            name="archive_file",
            description="Archive planning documents to Second Brain",
            inputSchema={
                "type": "object",
                "properties": {
                    "file_path": {
                        "type": "string",
                        "description": "Path to file to archive"
                    },
                    "extract_first": {
                        "type": "boolean",
                        "default": True,
                        "description": "Extract insights before archiving"
                    },
                    "permanent_delete": {
                        "type": "boolean",
                        "default": False,
                        "description": "Permanently delete instead of archiving"
                    }
                },
                "required": ["file_path"]
            }
        ),
        Tool(
            name="get_stats",
            description="Get statistics about the knowledge base",
            inputSchema={
                "type": "object",
                "properties": {}
            }
        )
    ]


@app.call_tool()
async def call_tool(name: str, arguments: Any) -> list[TextContent]:
    """Handle tool calls"""

    if name == "extract_insights":
        return await handle_extract_insights(arguments)

    elif name == "load_insights":
        return await handle_load_insights(arguments)

    elif name == "search_insights":
        return await handle_search_insights(arguments)

    elif name == "archive_file":
        return await handle_archive_file(arguments)

    elif name == "get_stats":
        return await handle_get_stats(arguments)

    else:
        raise ValueError(f"Unknown tool: {name}")


async def handle_extract_insights(args: dict) -> list[TextContent]:
    """Extract insights from a file"""
    file_path = Path(args['file_path']).expanduser()
    project = args.get('project') or config.detect_project_from_path(str(file_path))
    topic = args.get('topic')
    auto_approve = args.get('auto_approve', False)

    if not file_path.exists():
        return [TextContent(
            type="text",
            text=f"Error: File not found: {file_path}"
        )]

    try:
        # Extract insights using Gemini
        insights = gemini.extract_from_file(file_path, project=project)

        if not insights:
            return [TextContent(
                type="text",
                text="No insights found in the file."
            )]

        # If auto-approve, save directly
        if auto_approve:
            with Database(DB_PATH) as db:
                saved_ids = []
                for insight in insights:
                    insight_id = db.insert_insight({
                        **insight,
                        'source_file': str(file_path),
                        'git_commit': git.get_current_commit()
                    })
                    saved_ids.append(insight_id)

            # Commit to git
            if config.auto_commit:
                git.commit_and_push(
                    f"Auto-extracted insights from {file_path.name}",
                    prefix=config.commit_message_prefix
                )

            return [TextContent(
                type="text",
                text=f"✅ Extracted and saved {len(insights)} insights automatically.\n\n" +
                     "\n".join([f"• {i['title']}" for i in insights])
            )]

        # Otherwise, return for review
        review_text = f"Found {len(insights)} insights for review:\n\n"
        for i, insight in enumerate(insights, 1):
            review_text += f"{i}. **{insight['title']}**\n"
            review_text += f"   Category: {insight.get('category', 'unknown')}\n"
            review_text += f"   Topic: {insight.get('topic', 'unknown')}\n"
            review_text += f"   Tags: {', '.join(insight.get('tags', []))}\n\n"

        review_text += "\nTo save these insights, use auto_approve=true or save manually via database."

        return [TextContent(type="text", text=review_text)]

    except Exception as e:
        return [TextContent(
            type="text",
            text=f"Error extracting insights: {str(e)}"
        )]


async def handle_load_insights(args: dict) -> list[TextContent]:
    """Load insights by topic"""
