  | "ai_translate"
  | "ai_summarize"
  | "ai_gen_title"
  | "ai_gen_tags"
  | "ai_gen_content"
  | "ai_add_title"
  | "ai_add_tags"
  | "ai_generate_content"
  | "set_title"
  | "set_content"
  | "set_tags"
  | "schedule_post"
  | "publish_content"
  | "add_image"
  | "fetch_content"
  | "reply_to_comment"
  | "reply_to_message";

// Platform types
export type PlatformType =
  | "instagram"
  | "facebook"
  | "tiktok"
  | "linkedIn"
  | "youtube"
  | "pinterest"
  | "threads"
  | "mastodon"
  | "bluesky";

// Single workflow recommendation
export interface WorkflowRecommendation {
  description: string;
  action: WorkflowActionType;
  impact?: 'high' | 'medium' | 'low';    // Impact level for prioritization (defaults to 'medium')
  priority?: number;                      // Priority within impact level (defaults to 1)
  targetPlatform?: string;                // Target platform (populated by getWorkflowRecommendations)
}

// Map of target platforms to their recommendation arrays
export type TargetPlatformRecommendations = {
  [key in PlatformType]?: WorkflowRecommendation[];
};

// Complete recommendations structure: trigger platform → target platforms → recommendations
export type WorkflowRecommendations = {
  [key in PlatformType]?: TargetPlatformRecommendations;
};

