/**
 * Get workflow recommendations for a specific trigger and target platform combination
 * Adds targetPlatform to each recommendation for tracking
 */
export function getWorkflowRecommendations(
  triggerPlatform: PlatformType,
  targetPlatform: PlatformType
): WorkflowRecommendation[] {
  const recommendations = WORKFLOW_RECOMMENDATIONS[triggerPlatform]?.[targetPlatform] || [];

  // Add target platform to each recommendation for tracking
  return recommendations.map(rec => ({
    ...rec,
    targetPlatform
  }));
}

/**
 * Get all target platform recommendations for a trigger platform
 */
export function getAllTargetRecommendations(
  triggerPlatform: PlatformType
): TargetPlatformRecommendations {
  return WORKFLOW_RECOMMENDATIONS[triggerPlatform] || {};
}

/**
 * Check if recommendations exist for a platform combination
 */
export function hasRecommendations(
  triggerPlatform: PlatformType,
  targetPlatform: PlatformType
): boolean {
  const recommendations = getWorkflowRecommendations(triggerPlatform, targetPlatform);
  return recommendations.length > 0;
}

/**
 * Get top N recommendations across ALL target platforms, sorted by impact and priority
 *
 * Impact-based smart selection:
 * - HIGH impact: Major tone transformation, missing features (e.g., titles), dramatic content shifts
 * - MEDIUM impact: Minor tone adjustments, helpful optimizations
 * - LOW impact: Optional enhancements
 *
 * @param triggerPlatform - The trigger platform
 * @param targetPlatforms - Array of target platforms
 * @param limit - Maximum number of recommendations to return (default: 3)
 * @returns Top N recommendations sorted by impact (high > medium > low) then priority (1 > 2 > 3)
 */
export function getTopRecommendations(
  triggerPlatform: PlatformType,
  targetPlatforms: PlatformType[],
  limit: number = 3
): WorkflowRecommendation[] {
  const allRecommendations: WorkflowRecommendation[] = [];

  // Collect recommendations for all target platforms
  for (const target of targetPlatforms) {
    const recs = getWorkflowRecommendations(triggerPlatform, target);
    allRecommendations.push(...recs);
  }

  // Sort by impact (high > medium > low), then by priority (1 > 2 > 3)
  const impactOrder = { high: 3, medium: 2, low: 1 };
  allRecommendations.sort((a, b) => {
    // Default to medium impact and priority 1 if missing
    const impactA = a.impact || 'medium';
    const impactB = b.impact || 'medium';
    const priorityA = a.priority || 1;
    const priorityB = b.priority || 1;

    // First, sort by impact
    if (impactOrder[impactA] !== impactOrder[impactB]) {
      return impactOrder[impactB] - impactOrder[impactA];
    }
    // Then, sort by priority (lower number = higher priority)
    return priorityA - priorityB;
  });

  // Return top N
  return allRecommendations.slice(0, limit);
}

