import { computed, watch, ref, type Ref } from 'vue';
import { useStore } from '@nanostores/vue';
import { useMounted } from '@vueuse/core';
import { onboardingStore, onboardingData } from '@/stores/onboardingStore';
import {
  type OnboardingData,
  type OnboardingStep,
  ONBOARDING_STEPS,
  getNextSubstep,
  getPreviousSubstep,
  isSubstepComplete,
  generateDynamicSubsteps,
  isDynamicTargetStep,
  getTargetIndexFromSubstep,
  normalizePlatformIdentifier,
  type Platform
} from '@/types/onboarding';
import {
  getTopRecommendations,
  type WorkflowRecommendation,
  type PlatformType,
  type WorkflowActionType
} from '@/constants/workflowRecommendations';
import { getWorkflowNodes } from '@/stores/workflowStore';
import { useAuth } from '@composables/useAuth';
import { selectedUserTeam } from '@/stores/teamStore';
import { useNotification } from '@/utils/useNotification';
import {
  createWorkflow,
  updateWorkflow,
  createWorkflowNode,
  updateWorkflowNode
} from '@/stores/workflowStore';
import { ulidFactory } from 'ulid-workers';
import { getClientSidePermissions } from '@/utils/appwriteUtils';

export interface UseOnboardingFlowReturn {
  // Core state
  data: Ref<OnboardingData | undefined>;
  currentStep: Ref<OnboardingStep>;
  currentSubstep: Ref<string>;
  error: Ref<string | null>;

  // Simple navigation
  next: () => Promise<void>;
  previous: () => Promise<void>;
  goTo: (step: OnboardingStep, substep: string) => Promise<void>;

  // Data updates
  update: (updates: Partial<OnboardingData>) => Promise<void>;

  // Validation
  canProceed: Ref<boolean>;

  // Utilities
  reset: () => Promise<void>;
  initialize: () => Promise<void>;
  generateOnboardingWorkflow: () => Promise<void>;

  // AI Enhancement utilities
  getOnboardingRecommendations: () => WorkflowRecommendation[];
  initializeAIRecommendations: () => Promise<void>;
  addAIEnhancement: (recommendation: WorkflowRecommendation) => Promise<void>;
  getAIActionSmartDefaults: (action: WorkflowActionType, targetPlatform: string) => Record<string, any>;
}

export function useOnboardingFlow(): UseOnboardingFlowReturn {
  const mounted = useMounted();
  const { user } = useAuth();
  const team = useStore(selectedUserTeam);
  const { success, error: showError } = useNotification();

  const data = useStore(onboardingData);
  const error = ref<string | null>(null);

  // Computed values following existing patterns with hydration safety
  const currentStep = computed(() => {
    if (!mounted.value) return 'welcome' as OnboardingStep;
    return (data.value?.currentStep || 'welcome') as OnboardingStep;
  });

  const currentSubstep = computed(() => {
    if (!mounted.value) return 'welcome_start';
    return data.value?.currentSubstep || 'welcome_start';
  });

  const canProceed = computed(() => {
    if (!mounted.value || !data.value) {
      return false;
    }
    return isSubstepComplete(currentSubstep.value, data.value as OnboardingData);
  });

  // Navigation helpers with dynamic substep support
  const next = async () => {
    if (!data.value) return;

    // Generate dynamic substeps for the current step
    const currentStepSubsteps = generateDynamicSubsteps(currentStep.value, data.value as OnboardingData);
    const currentSubstepIndex = currentStepSubsteps.indexOf(currentSubstep.value);

