/**
 * Add AI enhancement node to workflow at correct position
 *
 * Strategy: Insert BEFORE the target platform's publish node
 * CRITICAL: Normalizes platform identifiers to match database values (linkedIn, facebook)
 */
const addAIEnhancementNode = async (
  workflowId: string,
  targetPlatform: string,
  recommendation: WorkflowRecommendation,
  customConfig?: Record<string, any>
): Promise<string> => {
  const ulid = ulidFactory();

  try {
    // CRITICAL: Normalize platform identifier to match database values
    // Database stores "linkedIn" (mixed case), not "linkedin"
    const normalizedPlatform = normalizePlatformIdentifier(targetPlatform);

    // Step 1: Get all workflow nodes
    const nodesResponse = await getWorkflowNodes(workflowId);
    const allNodes = nodesResponse.documents;

    // Step 2: Find the publish node for this target platform
    // Use normalized platform for matching
    const publishNode = allNodes.find(
      node => node.action === 'publish_content' &&
      normalizePlatformIdentifier(node.platform) === normalizedPlatform
    );

    if (!publishNode) {
      throw new Error(`No publish node found for platform: ${targetPlatform}`);
    }

    // Step 3: Find what points TO this publish node
    const previousNode = allNodes.find(node => {
      const connections = JSON.parse(node.connections || '{}');
      return connections.next === publishNode.$id;
    });

    // Step 4: Create AI action node with smart defaults or custom config
    const aiNodeId = ulid();
    const config = customConfig || getAIActionSmartDefaults(recommendation.action, normalizedPlatform);

    const newAINode = await createWorkflowNode({
      workflowId,
      nodeType: 'ACTION',
      action: recommendation.action,
      platform: normalizedPlatform,  // Use normalized platform for database storage
      config: JSON.stringify(config),
      connections: JSON.stringify({
        previous: previousNode?.$id || null,
        next: publishNode.$id
      })
    }, aiNodeId);

    // Step 5: Update previous node to point to AI node
    if (previousNode) {
      const prevConnections = JSON.parse(previousNode.connections || '{}');
      await updateWorkflowNode(previousNode.$id, {
        connections: JSON.stringify({
          ...prevConnections,
          next: aiNodeId
        })
      });
    }

    // Step 6: Update publish node's previous connection
    const publishConnections = JSON.parse(publishNode.connections || '{}');
    await updateWorkflowNode(publishNode.$id, {
      connections: JSON.stringify({
        ...publishConnections,
        previous: aiNodeId
      })
    });

    success(`Added ${recommendation.action} for ${normalizedPlatform}`);
    return aiNodeId;

  } catch (err) {
    console.error('Error adding AI enhancement node:', err);
    error.value = err instanceof Error ? err.message : 'Failed to add AI action';
    showError(error.value);
    throw err;
  }
};

/**
 * Get AI recommendations for current onboarding state
 * CRITICAL: Normalizes platform identifiers before fetching recommendations
 */
const getOnboardingRecommendations = (): WorkflowRecommendation[] => {
  if (!data.value?.platformTrigger || !data.value?.platformTargets) {
    return [];
  }

  // Normalize platform identifiers
  const normalizedTrigger = normalizePlatformIdentifier(data.value.platformTrigger) as PlatformType;
  const normalizedTargets = data.value.platformTargets.map(p => normalizePlatformIdentifier(p) as PlatformType);

  return getTopRecommendations(
    normalizedTrigger,
    normalizedTargets,
