    if (platforms.length > 0) {
      // Find where to insert dynamic steps (after choose_target_platforms)
      const targetPlatformsIndex = substeps.indexOf('choose_target_platforms');

      if (targetPlatformsIndex !== -1) {
        // Insert dynamic target connection steps
        for (let i = 0; i < platforms.length; i++) {
