/**
 * Find the target element and calculate arrow position
 */
const updatePosition = async () => {
  if (!props.targetSelector || !mounted.value) return;

  await nextTick();

  // Find the target element
  const element = document.querySelector(props.targetSelector) as HTMLElement;

  if (!element) {
    console.warn(`[TutorialArrow] Target element not found: ${props.targetSelector}`);
    targetElement.value = null;
    return;
  }

  targetElement.value = element;
  const rect = element.getBoundingClientRect();

  // Position the arrow above the target element, centered horizontally
  arrowPosition.value = {
    position: 'fixed',
    top: `${rect.top - props.verticalOffset}px`,
    left: `${rect.left + (rect.width / 2)}px`,
    transform: 'translateX(-50%)',
    width: 'auto',
    height: 'auto',
    inset: 'auto'
  };
};

